UPDATE locales_npc_text SET Text0_0_loc6='¿Me estás retando, $ghermano:hermana;?',Text0_1_loc6='¿Me estás retando, $ghermano:hermana;?' WHERE entry=13433; /* Text0_0: Are you challenging me, $g brother:sister;? */ /* Text0_1: Are you challenging me, $g brother:sister;? */
