UPDATE locales_npc_text SET Text0_0_loc6='Nuestros almacenes están vacíos. Hasta que no lleguen más suministros, no puedo vender nada.' WHERE entry=13502; /* Text0_0: Our storehouses have been depleted.  I have nothing to sell until we can get more supplies. */
