UPDATE locales_npc_text SET Text0_0_loc6='¡Nadie construye más deprisa, más barato y mejor que los hermanos Cargarrayo!' WHERE entry=14426; /* Text0_0: No one builds it faster, cheaper, and better than the Blastbolt brothers! */
