UPDATE `locales_page_text` SET `text_loc6`='Consigue un jade. Se esconden en muchos lugares diferentes. Son raros, pero si hablas con un herrero o un ingeniero, pueden tener uno o dos de sobra.' WHERE `entry`=911;
