UPDATE `locales_page_text` SET `text_loc6`='<HTML><BODY><H1 align = "center"> Capítulo 1: EL NORTE DEL PILÓN </H1><BR/><BR/><IMG src = "Interface  Pictures  11482_crystals_mini_north" align = "left" /><P align = "right"> El Pilón <BR/> del Norte acepta los cristales <BR/> amarillo, rojo, <BR/> y <BR/> verde. </P><BR/><BR/><BR /><BR/><P align = "left"> El Pilón se enfoca en la energía que puede crearse utilizando cristales amarillos como la fuente principal de poder. </P></BODY></HTML>' WHERE `entry`=1511;
UPDATE `locales_page_text` SET `text_loc6`='<HTML><BODY><H1 align = "center"> Combinaciones de cristales del norte </H1><BR/><P align = "left"> Hay dos efectos posibles: uno que producirá daño y otro que negará dañar. </P><BR/><P align = "left"> CARGA DE CRISTAL: Requiere cristales rojos y amarillos. <BR/> Esto producirá una gran explosión. </P><BR/><P align = "left"> RESTAURACIÓN DE CRISTAL: Requiere cristales amarillos y verdes.<BR/> Esto te proporcionará un aura de curación. </P></BODY></HTML>' WHERE `entry`=1512;
