UPDATE `locales_page_text` SET `text_loc6`='<HTML><BODY><H1 align = "center"> Capítulo 3: WESTERN PYLON </H1><BR/><BR/><IMG src = "Interface  Pictures  11482_crystals_mini_west" align = "left" /><P align = "right"> El Pilón <BR/> Occidental acepta <BR/> cristales rojos, verdes, <BR/> y azules <BR/>. </P><BR/><BR/><BR /><BR/><P align = "left"> El Pilón se enfoca en la energía que puede crearse utilizando cristales rojos como la fuente principal de poder. </P></BODY></HTML>' WHERE `entry`=1531;
UPDATE `locales_page_text` SET `text_loc6`='<HTML><BODY><H1 align = "center"> Combinaciones de cristales occidentales </H1><BR/><P align = "left"> Hay dos efectos posibles: uno que aumenta la protección contra daños y otro que debilita las defensas de un enemigo. </P><BR/><P align = "left"> CRYSTAL WARD: Requiere cristales rojos y verdes. <BR/> Esto te proporcionará un aumento de armadura. </P><BR /><P align = "left"> RENDIMIENTO DE CRISTAL: Requiere cristales azules y rojos. <BR/> Esto puede disminuir la armadura de un objetivo específico. </P></BODY></HTML>' WHERE `entry`=1532;
