UPDATE `locales_page_text` SET `text_loc6`='Has descubierto algo de gran importancia. Este libro no se parece a nada que hayas visto antes.$B$BParece que alguien o algo ha logrado romper el sello de El Athenaeum. El texto es confuso, apenas se puede leer.' WHERE `entry`=2637;
