UPDATE `locales_page_text` SET `text_loc6`='<HTML><BODY><H1 align = "center"> UN TREATISE EN RANKS MILITARES </H1><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRankAlliance " align = "left" /><BR/><P align = "right"> Lo que sigue son </P><P align = "right"> los rangos militares </P><P align = "right"> de la Alianza, </P><P align = "right"> procedió según la información </P><P align = "right"> tal como está </P><P align = "right"> conocida por las carreras </P><P align = "derecha"> de la Horda. Cada </P><P align = "right"> se presenta en dos secciones </P><P align = "right">, los oficiales y luego los enlistados, con los rangos de cada una en orden descendente, desde el más alto al más bajo¡Viva la Alianza! </P></BODY></HTML>' WHERE `entry`=2654;
