UPDATE `locales_page_text` SET `text_loc6`='<HTML><BODY><H1 align = "center"> RANKS OF OFFRERS OF HORDE </H1><BR/><P align = "center"> Part 1 </P><IMG src = "InterfacePvPRankBadgesPvPRank14" align = "left" /><BR/><P align = "right"> High Warlord </P><BR/><BR/><IMG src = "InterfacePvPRankBadgesPvPRank13" align = "left" /><BR/>< P align = "right"> Warlord </P><BR/><BR/><IMG src = "InterfacePvPRankBadgesPvPRank12" align = "left" /><BR/><P align = "right"> General </ P ><BR/><BR/><IMG src = "InterfacePvPRankBadgesPvPRank11" align = "left" /><BR/><P align = "right"> Teniente General </P><BR/><BR/>< IMG src = "InterfacePvPRankBadgesPvPRank10 "align =" left "/><BR/><P align =" right "> Champion </P><BR/><BR/></BODY></HTML>' WHERE `entry`=2658;
UPDATE `locales_page_text` SET `text_loc6`='Cuando miras al Ojo, las imágenes de una gran batalla llenan tu mente ..$B$BEste conflicto aterrador parece estar ocurriendo en algún lugar de Lordaeron ..$B$BApenas puedes distinguir una figura femenina solitaria, en medio de mil cadáveres, defendiéndose de un mar de Azote.$B$BElla es irremediablemente superada en número.$B$BZarcillos de luz escapan de sus manos, cortando a través de cientos de muertos vivientes.' WHERE `entry`=2659;
UPDATE `locales_page_text` SET `text_loc6`='<HTML><BODY><H1 align = "center"> RANKS ENLISTADO DEL HORDE </H1><BR/><IMG src = "Interface  PvPRankBadges  PvPRank01" /><BR/><P align = "right "> First Sergeant </P><BR/><BR/><IMG src =" Interface  PvPRankBadges  PvPRank04 "/><BR/><P align =" right "> Sergeant Senior </P><BR / ><BR/><IMG src = "Interface  PvPRankBadges  PvPRank03" /><BR/><P align = "right"> Sergeant </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank02 "/><BR/><P align =" right "> Grunt </P><BR/><BR/><IMG src =" Interface  PvPRankBadges  PvPRank01 "/><BR/><P align = "right"> Scout </P><BR/><BR/></BODY></HTML>' WHERE `entry`=2660;
UPDATE `locales_page_text` SET `text_loc6`='<HTML><BODY><H1 align = "center"> RANGO MILITAR DEL HORDE & amp; ALIANZA </H1><BR/><BR/><IMG src = "InterfacePvPRankBadgesPvPRankHorde" align = "left" /><BR/><P align = "right"> Throm\'ka! En este </P><P align = "right"> libro encontrará </P><P align = "right"> muchos listados de </P><P align = "right"> los rangos militares </ P><P align = "right"> de la Horda y </P><P align = "right"> la pequeña Alianza. </P><P align = "right"> Primero vendrá la </P><P align = "right"> Horda, luego la Alianza, </P><P align = "right"> cada uno con listas de oficiales y rangos de alistados. Como es apropiado,los más fuertes se enumeran en la parte superior, y los más débiles se enumeran debajo de ellos. </P></BODY></HTML>' WHERE `entry`=2661;
UPDATE `locales_page_text` SET `text_loc6`='<HTML><BODY><H1 align = "center"> RANKS OF OFFRERS OF HORDE </H1><BR/><P align = "center"> Part 1 </P><IMG src = "InterfacePvPRankBadgesPvPRank14" align = "left" /><BR/><P align = "right"> High Warlord </P><BR/><BR/><IMG src = "InterfacePvPRankBadgesPvPRank13" align = "left" /><BR/>< P align = "right"> Warlord </P><BR/><BR/><IMG src = "InterfacePvPRankBadgesPvPRank12" align = "left" /><BR/><P align = "right"> General </ P ><BR/><BR/><IMG src = "InterfacePvPRankBadgesPvPRank11" align = "left" /><BR/><P align = "right"> Teniente General </P><BR/><BR/>< IMG src = "InterfacePvPRankBadgesPvPRank10 "align =" left "/><BR/><P align =" right "> Champion </P><BR/><BR/></BODY></HTML>' WHERE `entry`=2662;
