UPDATE creature_ai_texts SET content_loc6='¡Grek probar!' WHERE entry=-245; /* content: Grek try! */
UPDATE creature_ai_texts SET content_loc6='¡Este aceite no vale para Grek! ¿Qué crees que es Grek? ¿Un debilucho con túnica?' WHERE entry=-246; /* content: This oil no good for Grek! What Grek look like to you, some weakling in robes? */
