UPDATE db_script_string SET content_loc6='Estudiad mucho y quizás algún día podréis invocar a vuestro propio esbirro, pero ahora pasaremos a estudiar al manáfago.' WHERE entry=2000005374;
UPDATE db_script_string SET content_loc6='Lo que tenéis delante es un manáfago. Entre los talentos naturales de esta criatura está la capacidad de neutralizar encantamientos y otras habilidades, convirtiéndola en un ser inigualable a la hora de enfrentarse a un oponente con poderes mágicos.' WHERE entry=2000005375;
UPDATE db_script_string SET content_loc6='A la hora de enfrentarse a cualquier taumaturgo, esta bestia salvaje será vuestro mejor amigo. Veamos ahora algo ligeramente distinto. La siguiente criatura os ayudará en vuestros viajes y hará más agradables vuestros viajes futuros. Vamos a echar un vistazo a un corcel vil.' WHERE entry=2000005376;
UPDATE db_script_string SET content_loc6='Aquí lo tenéis. Nuestra lección sobre las invocaciones ha llegado a su fin. En breve iniciaremos otra clase, así que si queréis poneros al día, os podéis quedar por aquí.' WHERE entry=2000005378;
UPDATE db_script_string SET content_loc6='¡Uf! Aquí está mi sombrero de la suerte. Lo había buscado por todas partes.' WHERE entry=2000005379;
UPDATE db_script_string SET content_loc6='No sé en qué estaba pensando. Mira que salir sin mi espada... Me la habría puesto si la hubiera visto aquí...' WHERE entry=2000005380;
UPDATE db_script_string SET content_loc6='No puedo seguir sin mi capa. Espero que esté aquí...' WHERE entry=2000005381;
UPDATE db_script_string SET content_loc6='¡Aquí está! Juraría que no estaba aquí la última vez que miré.' WHERE entry=2000005382;
UPDATE db_script_string SET content_loc6='No puedo dormir si no me cuentan un buen cuento. Ahora, seguro que duermo bien.' WHERE entry=2000005383;
UPDATE db_script_string SET content_loc6='¿Qué ha sido ese ruido? Parecía un rugido.' WHERE entry=2000005384;
UPDATE db_script_string SET content_loc6='¡¿Zarpadura?! Ahí mismo $Gamigo mío:amiga mía;... ¿dónde está tu maestro Volcor?' WHERE entry=2000005386;
UPDATE db_script_string SET content_loc6='¡Guau! Espera. Un momento...' WHERE entry=2000005388;
UPDATE db_script_string SET content_loc6='Ahí está... esto deberá servir. Ahora dime qué ha pasado, Zarpadura.' WHERE entry=2000005390;
UPDATE db_script_string SET content_loc6='Ya entiendo, $Gamigo mío:amiga mía;. Encontraré a alguien que ayude a tu maestro. Vuelve junto a él ahora, o al menos permanece cerca de él.' WHERE entry=2000005392;
UPDATE db_script_string SET content_loc6='Si dispones de tiempo, podrías ayudar a Zarpadura y a su maestro Volcor. Si te interesa, habla conmigo más adelante...' WHERE entry=2000005394;
UPDATE db_script_string SET content_loc6='Ya he tenido bastante de tanto lloriqueo incesante, capitán. ¿Y de qué eres capitán tú? ¡Lo que hay que ver!' WHERE entry=2000005395;
UPDATE db_script_string SET content_loc6='%s se encoge de hombros.' WHERE entry=2000005397;
UPDATE db_script_string SET content_loc6='Vaya. Otra vez a revisar el diseño.' WHERE entry=2000005398;
UPDATE db_script_string SET content_loc6='Por favor, discúlpame mientras comienzo mis pruebas...' WHERE entry=2000005400;
UPDATE db_script_string SET content_loc6='Interesante...' WHERE entry=2000005401;
UPDATE db_script_string SET content_loc6='Pero, ¿qué es esto?' WHERE entry=2000005402;
UPDATE db_script_string SET content_loc6='¡Oh, vaya!' WHERE entry=2000005403;
UPDATE db_script_string SET content_loc6='Si la sangre no se me hubiera coagulado hace tiempo, estaría hirviendo de rabia ahora mismo...' WHERE entry=2000005404;
UPDATE db_script_string SET content_loc6='Canteros... err... Que avisen a Defias: El ancla oxidada se hundirá esta noche.' WHERE entry=2000005407;
UPDATE db_script_string SET content_loc6='El sacerdote se arrodilla y reza en silencio.' WHERE entry=2000005409;
UPDATE db_script_string SET content_loc6='Luz, dame fuerza.' WHERE entry=2000005410;
UPDATE db_script_string SET content_loc6='Luz, guía a los caídos de la falsedad a la verdad, de la oscuridad hacia la luz y de la muerte a la vida infinita.' WHERE entry=2000005411;
UPDATE db_script_string SET content_loc6='¡¡Nooooo!!' WHERE entry=2000005415;
UPDATE db_script_string SET content_loc6='Amigos... Os he fallado...' WHERE entry=2000005416;
UPDATE db_script_string SET content_loc6='No, amigo mío... Nosotros te hemos fallado. Te hemos fallado.' WHERE entry=2000005417;
UPDATE db_script_string SET content_loc6='Todo está dispuesto, belisario.' WHERE entry=2000005418;
UPDATE db_script_string SET content_loc6='El señor de fatalidad Kazzak estará satisfecho. Vas a aumentar el ritmo de tus ataques. Destruid a los orcos y los bastiones de los enanos se precipitarán.' WHERE entry=2000005419;
UPDATE db_script_string SET content_loc6='No te preocupes por eso. He aumentado la producción en la Forja Muerta. Tendrás a todos los infernales que quieras para que sigan tus órdenes. No fracases, Jovaan.' WHERE entry=2000005421;
UPDATE db_script_string SET content_loc6='Se hará como dices, belisario. Una última pregunta, si se me permite...' WHERE entry=2000005422;
UPDATE db_script_string SET content_loc6='¿Pero?' WHERE entry=2000005423;
UPDATE db_script_string SET content_loc6='¿Qué hay en la caja?' WHERE entry=2000005424;
UPDATE db_script_string SET content_loc6='¿Caja? No te envié una caja, Jovaan. ¿No tienes cosas más importantes de las que preocuparte? ¡Ve a ocuparte de ellas!' WHERE entry=2000005425;
UPDATE db_script_string SET content_loc6='Estoy harto de pedir las cosas con cortesía, hombre pez.' WHERE entry=2000005426;
UPDATE db_script_string SET content_loc6='¡¿Dónde está la Clave de Condenación?! Sé que un tercio está en este pueblo.' WHERE entry=2000005427;
UPDATE db_script_string SET content_loc6='¿De qué me hablasss, piel ssuave?' WHERE entry=2000005428;
UPDATE db_script_string SET content_loc6='Los nagasss no tienen ninguna cccclave...' WHERE entry=2000005429;
UPDATE db_script_string SET content_loc6='No... Claro que no. ¿En qué estaría pensando? ¿Para qué querríais vosotros un tercio de la Clave de Condenación?' WHERE entry=2000005430;
UPDATE db_script_string SET content_loc6='Qué tonto. Quizás debería soltarte y dejarte marchar...' WHERE entry=2000005431;
UPDATE db_script_string SET content_loc6='Sssí..., sssí... Déjame marcharrr...' WHERE entry=2000005432;
UPDATE db_script_string SET content_loc6='¡¿DE VERDAD CREES QUE SOY TAN ESTÚPIDO, NAGA?! ¡SUFRE!' WHERE entry=2000005433;
UPDATE db_script_string SET content_loc6='%s escupe sobre el naga Cicatriz Espiral.' WHERE entry=2000005434;
UPDATE db_script_string SET content_loc6='¡LEVÁNTATE, BASURA INMUNDA! ¡LEVÁNTATE ANTES DE QUE TE MATE!' WHERE entry=2000005435;
UPDATE db_script_string SET content_loc6='%s recupera la conciencia.' WHERE entry=2000005436;
UPDATE db_script_string SET content_loc6='¡No vuelvas a pegarle al comandante Cicatriz Essspiral, orco! ¡Te lo diré! La clave... essstá essscondido en el Alto Cicatriz Espiral. Los nagasss de allí lo guardan bajo llave...' WHERE entry=2000005438;
UPDATE db_script_string SET content_loc6='¡CICATRIZ ESSSPIRAL!' WHERE entry=2000005439;
UPDATE db_script_string SET content_loc6='Una voz tenebrosa y profunda surge de %s...' WHERE entry=2000005440;
UPDATE db_script_string SET content_loc6='¿Qué es lo que quieres de mí, chamán?' WHERE entry=2000005443;
UPDATE db_script_string SET content_loc6='¿Acaso no tienes ojos? ¿No has visto a los demonios en nuestro mismo umbral? ¡No puedo prescindir de ningún soldado!' WHERE entry=2000005444;
UPDATE db_script_string SET content_loc6='Reza a tus dioses, chamán. Quizás vengan en tu ayuda. Sin embargo, la Horda no puede...' WHERE entry=2000005445;
UPDATE db_script_string SET content_loc6='¡Silencio! ¡Ahora llega la destrucción!' WHERE entry=2000005446;
UPDATE db_script_string SET content_loc6='¡Contempla el innegable poder de nuestro oscuro amo!' WHERE entry=2000005447;
UPDATE db_script_string SET content_loc6='¡Con este don, arrasaré la tierra para recrearla!' WHERE entry=2000005448;
UPDATE db_script_string SET content_loc6='¡Mira! ¡Mira cómo se abre el suelo ante nosotros! ¡Mira cómo fluye la energía! Alimentará a nuestro ejército...' WHERE entry=2000005449;
UPDATE db_script_string SET content_loc6='¡Nunca nos faltará poder! He asegurado nuestro futuro. ¡Someteos a mi gloria!' WHERE entry=2000005450;
UPDATE db_script_string SET content_loc6='Ya... está... hecho...' WHERE entry=2000005451;
UPDATE db_script_string SET content_loc6='¡Parte la tierra, Gul\'dan! ¡Corta el vínculo!' WHERE entry=2000005452;
UPDATE db_script_string SET content_loc6='¡No lo hagas, Gul\'dan! ¡Los elementos no nos perdonarán nunca!' WHERE entry=2000005453;
UPDATE db_script_string SET content_loc6='¡Nos han abandonado! ¡No olvides!' WHERE entry=2000005455;
UPDATE db_script_string SET content_loc6='¡Tomaremos por la fuerza lo que la tierra nos niegue!' WHERE entry=2000005456;
UPDATE db_script_string SET content_loc6='¡Para! ¡Detente de una vez!' WHERE entry=2000005457;
UPDATE db_script_string SET content_loc6='Nos has condenado a todos... Estamos perdidos.' WHERE entry=2000005458;
UPDATE db_script_string SET content_loc6='¡Si quieres la llave tendrás que cogerme!' WHERE entry=2000005460;
UPDATE db_script_string SET content_loc6='¡Cógeme si puedes! ¡Corro tan rápido como el viento!' WHERE entry=2000005461;
UPDATE db_script_string SET content_loc6='¿Por qué ibas a querer hacerme daño? ¡Vamos! ¡Podemos ser amigos!' WHERE entry=2000005462;
UPDATE db_script_string SET content_loc6='¿MORIR? ¡Estás haciendo llorar a Pusillín!' WHERE entry=2000005463;
UPDATE db_script_string SET content_loc6='¡Saluda a mis amiguitos!' WHERE entry=2000005464;
UPDATE db_script_string SET content_loc6='¡A sus órdenes, señor!' WHERE entry=2000005465;
UPDATE db_script_string SET content_loc6='Danos la fuerza de los gigantes de Sombraluna, Corok. El maestro es un gobernante justo. Se asegurará de cuidar de aquellos que ayudaron a dominar a los disidentes de este mundo.' WHERE entry=2000005466;
UPDATE db_script_string SET content_loc6='Entonces os convertiréis en enemigos de mi señor, Illidan. Los enemigos de Illidan son destruidos. ¿Son capaces tus gigantes de resistir un ataque de las tropas de la Punta Eclipse?' WHERE entry=2000005468;
UPDATE db_script_string SET content_loc6='No... no lo pensaba...' WHERE entry=2000005469;
UPDATE db_script_string SET content_loc6='Volveremos a encontrarnos... Algún día.' WHERE entry=2000005471;
UPDATE db_script_string SET content_loc6='¡La encarnación de la muerte ha sido liberada una vez más sobre el mundo! Teron Sanguino ha vuelto...' WHERE entry=2000005472;
UPDATE db_script_string SET content_loc6='¡Considéralo un calentamiento! ¡Tu entrenamiento real será mucho más duro!' WHERE entry=2000005476;
UPDATE db_script_string SET content_loc6='Con una recompensa como esa, ¿cómo podrían decir que no?' WHERE entry=2000005477;
UPDATE db_script_string SET content_loc6='¡Escóndete! ¡Escóndete antes de que te vean! ¡Arriba!' WHERE entry=2000005478;
UPDATE db_script_string SET content_loc6='¡Alto!' WHERE entry=2000005479;
UPDATE db_script_string SET content_loc6='¿Qué sucede, mi señor?' WHERE entry=2000005480;
UPDATE db_script_string SET content_loc6='¡Zarath, debes volver al Templo Oscuro de inmediato! Pa... parece que he extraviado las órdenes de Lord Illidan. ¡Rápido!' WHERE entry=2000005481;
UPDATE db_script_string SET content_loc6='Mi señor, sin duda no esperas que te deje desatendido. Lord Illidan me cortaría la cabeza si te ocurriera algo.' WHERE entry=2000005482;
UPDATE db_script_string SET content_loc6='Zarath, soy perfectamente capaz de subir este camino hasta Punta Eclipse. ¡Si no entregamos la misiva, Lord Illidan nos cortará la cabeza a los dos! ¡Puedes marcharte!' WHERE entry=2000005483;
UPDATE db_script_string SET content_loc6='Como desees, mi señor.' WHERE entry=2000005484;
UPDATE db_script_string SET content_loc6='Ah, dulce cardo de sangre... Probablemente olvidado por uno de esos mugrientos adictos de Punta Eclipse.' WHERE entry=2000005485;
UPDATE db_script_string SET content_loc6='Su pérdida es, sin duda, mi ganancia...' WHERE entry=2000005486;
UPDATE db_script_string SET content_loc6='El momento de atacar se acerca. Acabad con Icarius.' WHERE entry=2000005487;
UPDATE db_script_string SET content_loc6='%s se mete el fardo de cardos de sangre en la toga.' WHERE entry=2000005488;
UPDATE db_script_string SET content_loc6='Atrás, $N. Los espíritus pueden atacar.' WHERE entry=2000005489;
UPDATE db_script_string SET content_loc6='¿Por qué nos has capturado?' WHERE entry=2000005490;
UPDATE db_script_string SET content_loc6='¿Qué esperas obtener de esto?' WHERE entry=2000005491;
UPDATE db_script_string SET content_loc6='%s se inclina.' WHERE entry=2000005492;
UPDATE db_script_string SET content_loc6='Queremos ayudarte, espíritu. Buscamos tu sabiduría.' WHERE entry=2000005493;
UPDATE db_script_string SET content_loc6='¿Sabiduría? Tu pueblo fue el que provocó la terrible ira de Cyrukh.' WHERE entry=2000005494;
UPDATE db_script_string SET content_loc6='¿Por qué deberíamos ayudarte? El mundo está arrasado. Destruido.' WHERE entry=2000005495;
UPDATE db_script_string SET content_loc6='%s asiente.' WHERE entry=2000005496;
UPDATE db_script_string SET content_loc6='Tienes razón, espíritu. Mi pueblo os hizo esto. Déjanos enmendar nuestros errores.' WHERE entry=2000005497;
UPDATE db_script_string SET content_loc6='Solo dinos esto: ¿qué o quién es Cyrukh?' WHERE entry=2000005498;
UPDATE db_script_string SET content_loc6='¡Insensato! ¡Cyrukh, el señor de Fuego! Cuando se rompió el vínculo, Cyrukh despertó. Son las lágrimas de Cyrukh las que llenan este mundo de roca fundida.' WHERE entry=2000005499;
UPDATE db_script_string SET content_loc6='El... ¿El Señor del Fuego? ¿Despertó a un señor elemental? Claro... ¡Maldito Gul\'dan!' WHERE entry=2000005500;
UPDATE db_script_string SET content_loc6='Ahora ha perdido la razón. Se volvió loco durante la rotura. No hay descanso para los elementos.' WHERE entry=2000005501;
UPDATE db_script_string SET content_loc6='Tiene que haber una forma.' WHERE entry=2000005502;
UPDATE db_script_string SET content_loc6='Sí, una forma. Destruir a Cyrukh. Destruir a un señor elemental.' WHERE entry=2000005503;
UPDATE db_script_string SET content_loc6='No dudes de mis camaradas ni de mí, espíritu. Dinos cómo invocar al señor elemental. Mis aliados y yo lo destruiremos.' WHERE entry=2000005504;
UPDATE db_script_string SET content_loc6='Debéis pronunciar las palabras...' WHERE entry=2000005505;
UPDATE db_script_string SET content_loc6='Sí... Sí..., las palabras. Las palabras de la maldición. Las palabras pronunciadas por el oscuro, tu maestro.' WHERE entry=2000005506;
UPDATE db_script_string SET content_loc6='Sí... Gul\'dan...' WHERE entry=2000005507;
UPDATE db_script_string SET content_loc6='Encontraremos esas palabras. Invocaremos a tu maestro y lo destruiremos.' WHERE entry=2000005508;
UPDATE db_script_string SET content_loc6='Hablas. Solo hablas... Ahora libéranos, mortal.' WHERE entry=2000005509;
UPDATE db_script_string SET content_loc6='Ahora, a recuperar las palabras perdidas.' WHERE entry=2000005510;
UPDATE db_script_string SET content_loc6='¿... crear un fondo para la compra de gomina? Me gusta mi pelo tal y como está, gracias.' WHERE entry=2000005511;
UPDATE db_script_string SET content_loc6='¿... como desertor, disfrutarás de actividades como nadar atado y cuidar perros? ¡Qué ridículo!' WHERE entry=2000005512;
UPDATE db_script_string SET content_loc6='Nunca me uniría a nada así. Mejor guardar esto. Puede ser de utilidad en el baño...' WHERE entry=2000005513;
UPDATE db_script_string SET content_loc6='¿... los desertores buscan un Theramore donde los ciudadanos puedan pasar el día haciendo malabarismos? ¿Qué es esta tontería?' WHERE entry=2000005514;
UPDATE db_script_string SET content_loc6='Esto es lo más divertido que he leído en mucho tiempo. ¡Me has alegrado el día!' WHERE entry=2000005515;
UPDATE db_script_string SET content_loc6='Esto... esto es una broma, ¿verdad?' WHERE entry=2000005516;
UPDATE db_script_string SET content_loc6='Hey, gracias.' WHERE entry=2000005517;
UPDATE db_script_string SET content_loc6='%s lee el panfleto.' WHERE entry=2000005518;
UPDATE db_script_string SET content_loc6='¿... recibe el 50 por ciento de descuento en ropa interior de desertores? ¿Qué es esta basura?' WHERE entry=2000005519;
UPDATE db_script_string SET content_loc6='¡Menuda panda de lunáticos! ¿De verdad os creéis esto?' WHERE entry=2000005520;
UPDATE db_script_string SET content_loc6='¡Gracias, $R! Tus servicios a los Mag\'har no se olvidarán.' WHERE entry=2000005521;
UPDATE db_script_string SET content_loc6='¡Muchas gracias, héroe!' WHERE entry=2000005522;
UPDATE db_script_string SET content_loc6='¡Muerte a los ogros!' WHERE entry=2000005524;
UPDATE db_script_string SET content_loc6='Intrusos, vuestra victoria será efímera. Soy el comandante Varos Zancanubes. Mis dracos controlan el cielo y protegen este conducto. ¡Yo me encargaré personalmente de que El Oculus no caiga en vuestro poder!' WHERE entry=2000005526;
UPDATE db_script_string SET content_loc6='El tramposo Señor de la magia Urom protege el tercer anillo. Aparecerá solo e indefenso, ¡pero que no te engañen las apariencias! Urom es un poderoso conjurador que dirige un grupo de criaturas fantasmales. Búscale arriba.' WHERE entry=2000005527;
UPDATE db_script_string SET content_loc6='El mayor de tus desafíos aún está por llegar. El Guardián-Ley Eregos es un dragón Azul de inmenso poder. Lo encontrarás sobrevolando el anillo superior.' WHERE entry=2000005528;
UPDATE db_script_string SET content_loc6='Solo aquellos que se alimentan de almas perdidas conocen el hambre.' WHERE entry=2000005529;
UPDATE db_script_string SET content_loc6='Contempla el vacío. Esa es la perpetuidad en la que moran.' WHERE entry=2000005530;
UPDATE db_script_string SET content_loc6='Se beben tu temor: es la sangre de la vida.' WHERE entry=2000005531;
UPDATE db_script_string SET content_loc6='Son el susurro del escalofriante viento.' WHERE entry=2000005532;
UPDATE db_script_string SET content_loc6='No mueren, no viven. Están fuera del ciclo.' WHERE entry=2000005533;
UPDATE db_script_string SET content_loc6='Donde uno cae, muchos podrán ocupar su puesto.' WHERE entry=2000005534;
UPDATE db_script_string SET content_loc6='¡Gracias! No podía aguantar mucho más... Aquí ha sucedido algo terrible.' WHERE entry=2000005535;
UPDATE db_script_string SET content_loc6='Creíamos que el Sagrario estaba bien protegido, pero no estábamos preparados para un ataque como este.' WHERE entry=2000005536;
UPDATE db_script_string SET content_loc6='Los dragonantes negros aparecieron de la nada y atacaron antes de que pudiésemos reaccionar.' WHERE entry=2000005537;
UPDATE db_script_string SET content_loc6='La defensa fue inútil. Mientras mis hermanos morían, conseguí retirarme aquí y bloquear la entrada.' WHERE entry=2000005538;
UPDATE db_script_string SET content_loc6='Fue una masacre calculada y eficaz, pero su verdadero objetivo eran los huevos que guardamos en el Sagrario.' WHERE entry=2000005539;
UPDATE db_script_string SET content_loc6='En el ataque inicial, pude ver a su verdadero líder, un temible dragón Crepuscular adulto.' WHERE entry=2000005541;
UPDATE db_script_string SET content_loc6='No conozco todos sus planes, héroes, ¡pero sé que no debemos permitir que tengan éxito!' WHERE entry=2000005542;
UPDATE db_script_string SET content_loc6='¡Te lo agradezco!' WHERE entry=2000005544;
UPDATE db_script_string SET content_loc6='¡Vale, allá voy!' WHERE entry=2000005548;
UPDATE db_script_string SET content_loc6='¡Venga, en marcha!' WHERE entry=2000005553;
UPDATE db_script_string SET content_loc6='Cuchicheos... pétalos de fuego... Cuchicheos...' WHERE entry=2000005554;
UPDATE db_script_string SET content_loc6='Mmm... Mmm...' WHERE entry=2000005555;
UPDATE db_script_string SET content_loc6='... quizás... con la mezcla correcta...' WHERE entry=2000005556;
UPDATE db_script_string SET content_loc6='... vides con espinas... murmura... ¡Auuu!' WHERE entry=2000005557;
UPDATE db_script_string SET content_loc6='¡Vámonos, son demasiados! ¡Escapad mientras podéis!' WHERE entry=2000005558;
UPDATE db_script_string SET content_loc6='¡Ayuda! ¡Que alguien nos ayude!' WHERE entry=2000005559;
UPDATE db_script_string SET content_loc6='Nuestros amigos lucharon bien, Brann, pero nosotros no hemos terminado todavía.' WHERE entry=2000005560;
UPDATE db_script_string SET content_loc6='Puede, pero es solo cuestión de tiempo que volvamos a irrumpir en Ulduar. ¿Ha habido suerte al buscar un teletransportador?' WHERE entry=2000005561;
UPDATE db_script_string SET content_loc6='En absoluto. Sospecho que tiene algo que ver con el gigantesco ensamblaje mecánico que nuestros exploradores divisaron frente a la puerta.' WHERE entry=2000005562;
UPDATE db_script_string SET content_loc6='Vale. Así que, al fin y al cabo, ¿tendremos que enfrentarnos a esa cosa?' WHERE entry=2000005563;
UPDATE db_script_string SET content_loc6='¿Qué pasa con ese protodraco blindado y con el gigante de fuego que avistamos cerca? ¿Crees que tus magos pueden encargarse de ellos?' WHERE entry=2000005564;
UPDATE db_script_string SET content_loc6='Al Kirin Tor no le resulta posible dedicar ningún recurso adicional a enfrentarse a nada de semejante tamaño. Aunque puede que no sea necesario.' WHERE entry=2000005565;
UPDATE db_script_string SET content_loc6='Podemos pasar de largo sin que nos vean. Mientras podamos abatir el ensamblaje frente a la puerta, debiéramos ser capaces de entrar.' WHERE entry=2000005566;
UPDATE db_script_string SET content_loc6='¿¡Escabullirnos!? ¿Qué crees que somos, marmotas?' WHERE entry=2000005567;
UPDATE db_script_string SET content_loc6='Bien. Si nuestros aliados son los que van a ensuciarse las manos, dejaremos que sean ellos quienes decidan cómo proceder.' WHERE entry=2000005569;
UPDATE db_script_string SET content_loc6='¡¿Qué... Qué has hecho, $N?! ¡Brann! ¡Braaaaaannnn!' WHERE entry=2000005570;
UPDATE db_script_string SET content_loc6='¡Brann! ¡$N acaba de activar el sistema de defensa orbital! ¡Si no nos largamos pronto de aquí, estamos fritos!' WHERE entry=2000005571;
UPDATE db_script_string SET content_loc6='¡Magos del Kirin Tor, cuando Brann dé la orden, abrid el escudo! ¡Defended esta plataforma y nuestros aliados con vuestras vidas! ¡Por Dalaran! ¡Por Dalaran!' WHERE entry=2000005575;
UPDATE db_script_string SET content_loc6='Nuestros aliados están preparados. ¡Desactivad el escudo y abrid paso!' WHERE entry=2000005576;
UPDATE db_script_string SET content_loc6='¡Lo hemos conseguido, chicos! Llegamos antes de la llegada de Algalon. Igual podemos entrar en el sistema e interferir sus análisis…' WHERE entry=2000005578;
UPDATE db_script_string SET content_loc6='Volveré al Archivum y veré si puedo bloquear su señal. Igual consigo algo de tiempo mientras os ocupáis de él.' WHERE entry=2000005579;
UPDATE db_script_string SET content_loc6='¡Oye! ¡Deja de merodear por mi sótano! ¡Lárgate!' WHERE entry=2000005580;
UPDATE db_script_string SET content_loc6='¿Qué estabas haciendo en mi sótano? ¡Hay problemas con la comida y lo último que necesito son extraños enredando en mis bienes! ¡Fuera!' WHERE entry=2000005581;
UPDATE db_script_string SET content_loc6='Toda esta situación resulta un poco paranoica, ¿no te parece?' WHERE entry=2000005582;
UPDATE db_script_string SET content_loc6='Las órdenes son las órdenes. Si el Príncipe dice salta...' WHERE entry=2000005583;
UPDATE db_script_string SET content_loc6='No puedes negar que es una orden rara. ¿Comida sospechosa? ¡Basándote en esa definición, deberías arrestar a Belfast!' WHERE entry=2000005584;
UPDATE db_script_string SET content_loc6='¡TE HE OÍDO! ¡Se acabó la cerveza para ti! ¡Ni una gota más!' WHERE entry=2000005585;
UPDATE db_script_string SET content_loc6='Ya basta, Michael. El negocio ya está sufriendo bastante con este susto. Podemos usar hasta la última moneda de cobre.' WHERE entry=2000005586;
UPDATE db_script_string SET content_loc6='Los soldados están realizando un gran trabajo. Mal, si lo que te interesa es que tus clientes vivan otro día más, la seguridad del pueblo es lo más importante.' WHERE entry=2000005587;
UPDATE db_script_string SET content_loc6='%s asiente a regañadientes.' WHERE entry=2000005588;
UPDATE db_script_string SET content_loc6='Eso no lo puedo discutir.' WHERE entry=2000005589;
UPDATE db_script_string SET content_loc6='No te preocupes demasiado. Para cuando terminé la guardia no habíamos encontrado ni un poquito de ese grano.' WHERE entry=2000005590;
UPDATE db_script_string SET content_loc6='Gracias a la Luz.' WHERE entry=2000005591;
UPDATE db_script_string SET content_loc6='%s asiente.' WHERE entry=2000005592;
UPDATE db_script_string SET content_loc6='¡Hombre! ¡Hola de nuevo!' WHERE entry=2000005593;
UPDATE db_script_string SET content_loc6='Vale, basta de trabajo por el momento. Es hora de tomar unos refrescos y charlar un rato en la posada.' WHERE entry=2000005594;
UPDATE db_script_string SET content_loc6='Espera, ¿qué es ese olor?' WHERE entry=2000005595;
UPDATE db_script_string SET content_loc6='No puedo ser yo. ¡Me bañé hace tres días!' WHERE entry=2000005596;
UPDATE db_script_string SET content_loc6='Oh, por los pelos. Solo es el grano.' WHERE entry=2000005597;
UPDATE db_script_string SET content_loc6='Espera un segundo. ¡Se supone que el grano no debería oler ASÍ! Será mejor que vaya a buscar a un guardia.' WHERE entry=2000005598;
UPDATE db_script_string SET content_loc6='¿No te importa que eche un ojo a tu mercancía por si ha sido manipulada?' WHERE entry=2000005599;
UPDATE db_script_string SET content_loc6='No, Sargento.' WHERE entry=2000005600;
UPDATE db_script_string SET content_loc6='Un momento, ¿qué es esto? ¡Me has mentido, Perelli!' WHERE entry=2000005601;
UPDATE db_script_string SET content_loc6='¡De qué habla, Sargento!' WHERE entry=2000005602;
UPDATE db_script_string SET content_loc6='Voy a confiscar este grano sospechoso, Perelli. Buscábamos comida manipulada y será mejor que no hagas tonterías mientras el príncipe Arthas comprueba esto.' WHERE entry=2000005603;
UPDATE db_script_string SET content_loc6='Tienes que creerme, ¡soy inocente!' WHERE entry=2000005604;
UPDATE db_script_string SET content_loc6='Ya veremos, Perelli. Ya veremos.' WHERE entry=2000005605;
UPDATE db_script_string SET content_loc6='Martha, no me queda harina para hacer pan. ¿No tendrás algo de grano de ese envío que ha llegado hace poco?' WHERE entry=2000005606;
UPDATE db_script_string SET content_loc6='Oh, hola, Jena. Claro que puedes tomar prestado algo de grano. Cógelo.' WHERE entry=2000005607;
UPDATE db_script_string SET content_loc6='¡Gracias, Martha! Te debo una.' WHERE entry=2000005608;
UPDATE db_script_string SET content_loc6='¡Martha, a este grano le pasa algo raro! Algunos de los soldados del Príncipe lo estaban buscando. Voy a buscar a uno.' WHERE entry=2000005609;
UPDATE db_script_string SET content_loc6='Oh, cielos.' WHERE entry=2000005610;
UPDATE db_script_string SET content_loc6='Oh, no.' WHERE entry=2000005611;
UPDATE db_script_string SET content_loc6='Parece que va a haber tormenta, Desaliñado...' WHERE entry=2000005612;
UPDATE db_script_string SET content_loc6='%s comienza a gruñir...' WHERE entry=2000005613;
UPDATE db_script_string SET content_loc6='¿Qué pasa, colega?' WHERE entry=2000005614;
UPDATE db_script_string SET content_loc6='¿Qué has encontrado, chico?' WHERE entry=2000005615;
UPDATE db_script_string SET content_loc6='Esto no tiene buena pinta, Desaliñado. Quédate aquí y cuida de la casa, tengo que encontrar a un soldado.' WHERE entry=2000005616;
UPDATE db_script_string SET content_loc6='¡Este envío de grano no nos ha dado más que problemas!' WHERE entry=2000005617;
UPDATE db_script_string SET content_loc6='¡Voy a perder mi bonificación de puntualidad por esto!' WHERE entry=2000005618;
UPDATE db_script_string SET content_loc6='¡Ni siquiera debería estar aquí hoy!' WHERE entry=2000005619;
UPDATE db_script_string SET content_loc6='Bueno, supongo que debería volver a cargar todo en la vagoneta.' WHERE entry=2000005620;
UPDATE db_script_string SET content_loc6='¡Oh, vamos! Se me ha estropeado la vagoneta, mi caballo ha perdido una herradura ¡y ahora la mercancía se me estropea!' WHERE entry=2000005621;
UPDATE db_script_string SET content_loc6='Tranquilo, pequeño draco.' WHERE entry=2000005623;
UPDATE db_script_string SET content_loc6='Un espécimen fabuloso.' WHERE entry=2000005624;
UPDATE db_script_string SET content_loc6='Duerme ahora, joven...' WHERE entry=2000005625;
UPDATE db_script_string SET content_loc6='Sí, este debería ayudar a que mis estudios avancen...' WHERE entry=2000005626;
UPDATE db_script_string SET content_loc6='Espera... ¿has oído eso? ¡Algo se aproxima desde el Oeste!' WHERE entry=2000005627;
UPDATE db_script_string SET content_loc6='¡Me he quedado sin flechas! Me temo que si viene alguno más tendrás que ocuparte tú de ellos.' WHERE entry=2000005628;
UPDATE db_script_string SET content_loc6='¡Alabada sea Elune! No sé si habría acabado el día con vida si no fuera por ti, $Gamigo:amiga;.' WHERE entry=2000005629;
UPDATE db_script_string SET content_loc6='Por favor, ve a hablar con la centinela Onaeya en la Atalaya de Maestra, en Vallefresno, procurará dar una recompensa adecuada a la valentía que has mostrado este día.' WHERE entry=2000005631;
UPDATE db_script_string SET content_loc6='¡Zafarrancho de combate! ¡Se acercan nagas!' WHERE entry=2000005632;
UPDATE db_script_string SET content_loc6='Si conseguimos pararlos ahora, seguro que estaremos fuera de peligro.' WHERE entry=2000005633;
UPDATE db_script_string SET content_loc6='¡Preparaos para disparar a mi orden! Listos, apunten...' WHERE entry=2000005634;
UPDATE db_script_string SET content_loc6='¡Fuego a discreción!' WHERE entry=2000005635;
UPDATE db_script_string SET content_loc6='¡Tiradores, en formación y a vuestros puestos!' WHERE entry=2000005636;
UPDATE db_script_string SET content_loc6='¡Lo hemos logrado! ¡Hemos vencido a Tethyr!' WHERE entry=2000005637;
UPDATE db_script_string SET content_loc6='¡Hemos vencido al monstruo marino!' WHERE entry=2000005638;
UPDATE db_script_string SET content_loc6='Jinete álzate...' WHERE entry=2000005639;
UPDATE db_script_string SET content_loc6='Ya conociste la muerte...' WHERE entry=2000005641;
UPDATE db_script_string SET content_loc6='¡Ahora mírala de cerca!' WHERE entry=2000005642;
UPDATE db_script_string SET content_loc6='%s ríe.' WHERE entry=2000005644;
UPDATE db_script_string SET content_loc6='He... He muerto... Tanto dolor... en la oscuridad... ¿Puedes verme, $R?' WHERE entry=2000005645;
UPDATE db_script_string SET content_loc6='¡A tus órdenes!' WHERE entry=2000005646;
UPDATE db_script_string SET content_loc6='Espera aquí, $N. El robot espía hará salir a Lescovar lo antes posible. ¡Prepárate! Ataca solo después de escuchar su conversación.' WHERE entry=2000005647;
UPDATE db_script_string SET content_loc6='Que tengáis los dos un buen día. Me gustaría hablar con el señor Lescovar.' WHERE entry=2000005648;
UPDATE db_script_string SET content_loc6='Claro. Él te espera en la biblioteca.' WHERE entry=2000005649;
UPDATE db_script_string SET content_loc6='Gracias. Que la Luz os acompañe a los dos.' WHERE entry=2000005650;
UPDATE db_script_string SET content_loc6='Señor, su invitado ha llegado. Le está esperando.' WHERE entry=2000005651;
UPDATE db_script_string SET content_loc6='Ah, muchas gracias. Te acompaño hasta la biblioteca mientras me ocupo de este asuntillo.' WHERE entry=2000005652;
UPDATE db_script_string SET content_loc6='He de meditar, dejadme a solas.' WHERE entry=2000005654;
UPDATE db_script_string SET content_loc6='¡Enseguida!' WHERE entry=2000005655;
UPDATE db_script_string SET content_loc6='%s espera hasta perder de vista a los guardias.' WHERE entry=2000005656;
UPDATE db_script_string SET content_loc6='Aquí estás. ¿Traes noticias de los Páramos de Poniente?' WHERE entry=2000005657;
UPDATE db_script_string SET content_loc6='VanCleef manda decir que los planes siguen en pie. Sin embargo, ha oído rumores de que alguien anda husmeando por ahí.' WHERE entry=2000005658;
UPDATE db_script_string SET content_loc6='Mmm, podría ser ese entrometido de Shaw. Veré lo que puedo descubrir. Y ahora, vete. Me pondré en contacto contigo pronto.' WHERE entry=2000005659;
UPDATE db_script_string SET content_loc6='Muy bien. Entonces regresaré.' WHERE entry=2000005660;
UPDATE db_script_string SET content_loc6='¡Eso es! ¡Eso es lo que estabas esperando! ¡MÁTALOS!' WHERE entry=2000005661;
UPDATE db_script_string SET content_loc6='Lamentable mortal, ¡Onyxia no responde ante nadie!' WHERE entry=2000005662;
UPDATE db_script_string SET content_loc6='Tu patético reto no ha pasado desapercibido. Disfrutaré jugando contigo antes de que mueras.' WHERE entry=2000005663;
UPDATE db_script_string SET content_loc6='Quedan muy pocos en el mundo que puedan preparar el Resguardo de despertar, $N. Tenemos la fortuna de yo sea uno de ellos.' WHERE entry=2000005665;
UPDATE db_script_string SET content_loc6='%s se arrodilla, llena el vial con agua de la Poza de la Luna' WHERE entry=2000005666;
UPDATE db_script_string SET content_loc6='La poción está lista. Ahora solo queda esperar que Clintar nos perdone por interrumpir su estancia en el Sueño Esmeralda.' WHERE entry=2000005668;
UPDATE db_script_string SET content_loc6='%s se pone de rodillas, pero parece estar en otro lado.' WHERE entry=2000005669;
UPDATE db_script_string SET content_loc6='%s se levanta y empieza a reconocer su entorno.' WHERE entry=2000005670;
UPDATE db_script_string SET content_loc6='Acércate más, joven. No hay nada que temer en mí ni en $N.' WHERE entry=2000005671;
UPDATE db_script_string SET content_loc6='Cuando hagas falta, oirás la llamada. Vuela hacia el este, a Skettis, y espera la señal de $N.' WHERE entry=2000005674;
UPDATE db_script_string SET content_loc6='%s saluda al gavilán que ayudó a encontrar las Piedras del Cuervo.' WHERE entry=2000005675;
UPDATE db_script_string SET content_loc6='Tus heridas son graves, pero vivirás.' WHERE entry=2000005677;
UPDATE db_script_string SET content_loc6='Ahora descansa, has luchado bien hoy.' WHERE entry=2000005678;
UPDATE db_script_string SET content_loc6='¡Hombres!' WHERE entry=2000005681;
UPDATE db_script_string SET content_loc6='¡Mujeres!' WHERE entry=2000005682;
UPDATE db_script_string SET content_loc6='Eh, ¿has leído lo del botín en este cartel de Se Busca?' WHERE entry=2000005683;
UPDATE db_script_string SET content_loc6='¿Te vas a pasar todo el día hablando de ese cartel de Se Busca?' WHERE entry=2000005686;
UPDATE db_script_string SET content_loc6='Me vendría bien el dinero extra.' WHERE entry=2000005687;
UPDATE db_script_string SET content_loc6='¡Dinos dónde se esconde tu líder!' WHERE entry=2000005689;
UPDATE db_script_string SET content_loc6='¡No te daremos nada hasta que hables, escoria!' WHERE entry=2000005690;
UPDATE db_script_string SET content_loc6='¡Madre!' WHERE entry=2000005691;
UPDATE db_script_string SET content_loc6='Mordenaku... Tus aliados nos han salvado y han destruido al desdichado, Zuluhed. Siempre serán amigos del Ala Abisal.' WHERE entry=2000005692;
UPDATE db_script_string SET content_loc6='Con el tiempo, necesitaremos de nuevo la ayuda de los mortales. En este mundo, nuestro derecho de nacimiento está destruido. Serán las razas mortales las que nos ayuden a recuperar nuestro legítimo lugar en Draenor. Protégelos, Mordenaku...' WHERE entry=2000005693;
UPDATE db_script_string SET content_loc6='Estúpidos mortales. ¿Pensasteis que no os quitaría la vida por vuestras transgresiones?' WHERE entry=2000005695;
UPDATE db_script_string SET content_loc6='Efectivamente, ha llegado la hora de acabar con esta farsa.' WHERE entry=2000005696;
UPDATE db_script_string SET content_loc6='¡Destruidlos! ¡Destruidlos a todos!' WHERE entry=2000005697;
UPDATE db_script_string SET content_loc6='Aparta si no quieres que te convierta en otro adorno para mi armadura. Tu líder y yo tenemos asuntos que atender...' WHERE entry=2000005698;
UPDATE db_script_string SET content_loc6='%s sonríe.' WHERE entry=2000005699;
UPDATE db_script_string SET content_loc6='Creí que lo verías como yo...' WHERE entry=2000005700;
UPDATE db_script_string SET content_loc6='Soy Lady Sinestra' WHERE entry=2000005702;
UPDATE db_script_string SET content_loc6='No alargaré esto más de lo necesario Lady Siniestra. Hemos hablado largo y tendido, ahora dime qué quieres de los Faucedraco.' WHERE entry=2000005703;
UPDATE db_script_string SET content_loc6='Sin duda habrás oído hablar de los fracasos de Nefarian en Azeroth... A pesar de que haya caído, los experimentos continúan. Mi maestro... continúa el trabajo que su progenie comenzó.' WHERE entry=2000005704;
UPDATE db_script_string SET content_loc6='¿El... el maestro? ¿Vive?' WHERE entry=2000005705;
UPDATE db_script_string SET content_loc6='%s asiente.' WHERE entry=2000005706;
UPDATE db_script_string SET content_loc6='Fuiste un teniente jefe, Mor\'ghor. Tu trabajo en Grim Batol no se olvidará fácilmente.' WHERE entry=2000005707;
UPDATE db_script_string SET content_loc6='Ahora... Necesitamos los huevos que has recuperado. Los huevos de Ala Abisal. Al fin y al cabo, son un producto del maestro. Pagaremos el precio que pidas.' WHERE entry=2000005708;
UPDATE db_script_string SET content_loc6='%s tartamudea.' WHERE entry=2000005709;
UPDATE db_script_string SET content_loc6='Ss... Sí, sí... por supuesto. Solo necesitamos los cristales y minerales de este lugar. Hay... necesitaremos monturas.' WHERE entry=2000005710;
UPDATE db_script_string SET content_loc6='El maestro estará muy satisfecho con estas noticias. El Vuelo Negro os proporcionará todo lo que pidáis. Podréis cabalgar en nuestros dracos si lo necesitáis.' WHERE entry=2000005711;
UPDATE db_script_string SET content_loc6='Agradezco tu atención, Mor\'ghor. Ahora debo irme.' WHERE entry=2000005712;
UPDATE db_script_string SET content_loc6='Así que mi sangre no fue pago suficiente, ¿eh? Bien, deja que recuperemos tu esencia arcana. Al fin y al cabo no le debo nada a Balthas.' WHERE entry=2000005713;
UPDATE db_script_string SET content_loc6='%s coloca un fetiche en la pila ritual.' WHERE entry=2000005714;
UPDATE db_script_string SET content_loc6='El proceso es arduo. Primero debemos invocar a los acólitos de los elementos. Entonces debes destruir a estos acólitos para que mis secuaces puedan preparase.' WHERE entry=2000005715;
UPDATE db_script_string SET content_loc6='¡Bien hecho! Continuemos...' WHERE entry=2000005716;
UPDATE db_script_string SET content_loc6='¡Preparaos! Pronto llegará el acólito de agua.' WHERE entry=2000005717;
UPDATE db_script_string SET content_loc6='¡Ven, acólito de tierra!' WHERE entry=2000005718;
UPDATE db_script_string SET content_loc6='¡Muéstrate, fuego!' WHERE entry=2000005719;
UPDATE db_script_string SET content_loc6='¡Es tuyo mi Señor!' WHERE entry=2000005721;
UPDATE db_script_string SET content_loc6='Tengo el ojo puesto en ti, Faranell.' WHERE entry=2000005722;
UPDATE db_script_string SET content_loc6='Sin abominaciones que te protejan. Sin secretismos. Sin conspirar en la oscuridad.' WHERE entry=2000005723;
UPDATE db_script_string SET content_loc6='Si escupes sin mi permiso este lugar quedará cerrado. Por la fuerza.' WHERE entry=2000005724;
UPDATE db_script_string SET content_loc6='Te aseguro, supervisor, que la Sociedad Real de Boticarios desea fervientemente compensar la trágica equivocación que acabó con tantas vidas.' WHERE entry=2000005725;
UPDATE db_script_string SET content_loc6='No te causaremos problemas. Solo queremos continuar con nuestra investigación en paz.' WHERE entry=2000005726;
UPDATE db_script_string SET content_loc6='Ya veremos, Perelli. Ya veremos.' WHERE entry=2000005727;
UPDATE db_script_string SET content_loc6='¡Haz que ese loco podrido cierre la bocaza!' WHERE entry=2000005728;
UPDATE db_script_string SET content_loc6='%s localiza al orco...' WHERE entry=2000005730;
UPDATE db_script_string SET content_loc6='%s falla...' WHERE entry=2000005731;
UPDATE db_script_string SET content_loc6='%s afina la puntería...' WHERE entry=2000005732;
UPDATE db_script_string SET content_loc6='%s falla de nuevo...' WHERE entry=2000005733;
UPDATE db_script_string SET content_loc6='%s apunta al orco por casualidad...' WHERE entry=2000005734;
UPDATE db_script_string SET content_loc6='%s acierta.' WHERE entry=2000005735;
UPDATE db_script_string SET content_loc6='%s apunta a las botellas...' WHERE entry=2000005736;
UPDATE db_script_string SET content_loc6='%s falla...' WHERE entry=2000005737;
UPDATE db_script_string SET content_loc6='%s afina la puntería con las botellas...' WHERE entry=2000005738;
UPDATE db_script_string SET content_loc6='%s falla de nuevo...' WHERE entry=2000005739;
UPDATE db_script_string SET content_loc6='%s dispara rápidamente al punto muerto del blanco central...' WHERE entry=2000005740;
UPDATE db_script_string SET content_loc6='%s entonces apunta a las botellas...' WHERE entry=2000005741;
UPDATE db_script_string SET content_loc6='%s solo le da a un lado de la caja.' WHERE entry=2000005742;
UPDATE db_script_string SET content_loc6='%s dispara rápidamente al orco de trapo...' WHERE entry=2000005743;
UPDATE db_script_string SET content_loc6='%s se vuelve hacia el blanco de papel...' WHERE entry=2000005744;
UPDATE db_script_string SET content_loc6='%s apunta sin desenfundar y dispara rápidamente a las botellas...' WHERE entry=2000005745;
UPDATE db_script_string SET content_loc6='¿James? James... No, tú no eres James, pero sé quién eres...' WHERE entry=2000005746;
UPDATE db_script_string SET content_loc6='Eres $Gel que:la que; siguió la pista de los soldados que nos hicieron esto.' WHERE entry=2000005747;
UPDATE db_script_string SET content_loc6='Intenté por todos medios decírselo a Jim... decírselo a alguien... quién estaba detrás de esto, pero no encontré la forma...' WHERE entry=2000005748;
UPDATE db_script_string SET content_loc6='Gracias por ayudarnos y por ayudar a Jim. Si le ves, dile que el pequeño Jimmy y yo le queremos y que le estamos esperando.' WHERE entry=2000005749;
UPDATE db_script_string SET content_loc6='Mami, ¿cuándo volveremos a ver a papi?' WHERE entry=2000005750;
UPDATE db_script_string SET content_loc6='No sé si volveremos a ver a papi, Jimmy, pero sé que te quiere y te echa mucho de menos.' WHERE entry=2000005751;
UPDATE db_script_string SET content_loc6='Ahora probémoslo...' WHERE entry=2000005752;
UPDATE db_script_string SET content_loc6='Ahora, a empezar con las reparaciones.' WHERE entry=2000005754;
UPDATE db_script_string SET content_loc6='Con un gruñido, %s se aleja del cartel de Se busca y vuelve a su sitio cerca del fuego.' WHERE entry=2000005755;
UPDATE db_script_string SET content_loc6='%s lee el cartel de Se busca.' WHERE entry=2000005756;
UPDATE db_script_string SET content_loc6='Adelante. Corta la cuerda.' WHERE entry=2000005757;
UPDATE db_script_string SET content_loc6='Tú has cortado la cuerda. Así que tendrás el cuchillo.' WHERE entry=2000005758;
UPDATE db_script_string SET content_loc6='¡Argg! Cobarde.' WHERE entry=2000005759;
UPDATE db_script_string SET content_loc6='¿Sabes? Creo que podemos llegar al Bastión del Honor desde aquí.' WHERE entry=2000005760;
UPDATE db_script_string SET content_loc6='Si el general Krakork se entera, nos comerá vivos. No.' WHERE entry=2000005761;
UPDATE db_script_string SET content_loc6='¡Ah! Podemos decirle que Hagash tropezó y lo disparó sin querer.' WHERE entry=2000005762;
UPDATE db_script_string SET content_loc6='Estoy ciego, idiota, no sordo. Ni se te ocurra.' WHERE entry=2000005763;
UPDATE db_script_string SET content_loc6='Mientras recoge las botellas, %s suspira.' WHERE entry=2000005764;
UPDATE db_script_string SET content_loc6='¡Aquí están!' WHERE entry=2000005765;
UPDATE db_script_string SET content_loc6='Gracias, amable $Gamigo:amiga;. Me has liberado de esta prisión de agua de Cicatriz Espiral, pero todavía quedan muchos otros prisioneros en los alrededores. ¿Me ayudarás a liberarlos también?' WHERE entry=2000005766;
UPDATE db_script_string SET content_loc6='¡Ahora, voy a buscar a una arpía desprevenida!' WHERE entry=2000005768;
UPDATE db_script_string SET content_loc6='¡Ahí hay una! ¡Por la Horda!' WHERE entry=2000005769;
UPDATE db_script_string SET content_loc6='¡Toma eso, asquerosa arpía! ¡Síiiii!' WHERE entry=2000005770;
UPDATE db_script_string SET content_loc6='¡No puedes escaparte de un guerrero de la Horda! ¡Toma esto! ¡Y esto!' WHERE entry=2000005771;
UPDATE db_script_string SET content_loc6='Mogg, entra. No es seguro que estés ahí fuera mientras tu padre no está.' WHERE entry=2000005774;
UPDATE db_script_string SET content_loc6='¡Ya habéis oído al ser de carne! ¡VAMOS!' WHERE entry=2000005780;
UPDATE db_script_string SET content_loc6='Avísame cuando estés $Glisto:lista;, $N. Atacaremos a tu orden.' WHERE entry=2000005781;
UPDATE db_script_string SET content_loc6='¡Ahora o nunca, soldados! ¡Adelante! ¡Por K\'aresh! ¡Por el Protectorado!' WHERE entry=2000005782;
UPDATE db_script_string SET content_loc6='¡Ha llegado la hora de tu destrucción, Dimensius!' WHERE entry=2000005783;
UPDATE db_script_string SET content_loc6='El Iluminado...' WHERE entry=2000005785;
UPDATE db_script_string SET content_loc6='Me alegra que lo consiguieras, Uther.' WHERE entry=2000005787;
UPDATE db_script_string SET content_loc6='Controla tu tono cuando te dirijas a mí, chico. Serás el Príncipe, pero yo todavía soy tu superior como paladín.' WHERE entry=2000005788;
UPDATE db_script_string SET content_loc6='Ojalá pudiera olvidar. Mira, Uther... hay algo sobre la Plaga que deberías saber.' WHERE entry=2000005789;
UPDATE db_script_string SET content_loc6='Oh, no... llegamos demasiado tarde. ¡Toda esta gente está infectada! Quizá ahora den la impresión contraria, ¡pero solo es cuestión de tiempo para que se conviertan en no muertos!' WHERE entry=2000005790;
UPDATE db_script_string SET content_loc6='¿Qué?' WHERE entry=2000005791;
UPDATE db_script_string SET content_loc6='Hay que purgar la ciudad entera.' WHERE entry=2000005792;
UPDATE db_script_string SET content_loc6='¡¿Cómo puedes siquiera pensarlo?! ¡Tiene que haber otra opción!' WHERE entry=2000005793;
UPDATE db_script_string SET content_loc6='¡Maldita sea, Uther! Como tu futuro rey, ¡te ordeno que purgues esta ciudad!' WHERE entry=2000005794;
UPDATE db_script_string SET content_loc6='Todavía no eres mi rey, chico. ¡Y tampoco obedecería esa orden si lo fueras!' WHERE entry=2000005795;
UPDATE db_script_string SET content_loc6='Entonces debo considerar esto un acto de traición.' WHERE entry=2000005796;
UPDATE db_script_string SET content_loc6='¿¡Traición!? ¿Has perdido el juicio, Arthas?' WHERE entry=2000005797;
UPDATE db_script_string SET content_loc6='¿Eso crees? Lord Uther, por mi derecho de sucesión y soberanía de mi corona, y a partir de este momento, te relevo de tu cargo y suspendo a tus paladines del servicio.' WHERE entry=2000005798;
UPDATE db_script_string SET content_loc6='¡Arthas! No puedes...' WHERE entry=2000005799;
UPDATE db_script_string SET content_loc6='¡Ya está! Aquellos que tengáis la voluntad de salvar esta tierra... seguidme. Los demás... salid de mi vista.' WHERE entry=2000005800;
UPDATE db_script_string SET content_loc6='Acabas de sobrepasar un límite terrible, Arthas.' WHERE entry=2000005801;
UPDATE db_script_string SET content_loc6='¿Jaina?' WHERE entry=2000005802;
UPDATE db_script_string SET content_loc6='Lo siento, Arthas. No puedo verte hacer esto.' WHERE entry=2000005803;
UPDATE db_script_string SET content_loc6='Colocaos aquí y yo guiaré un pequeño ejército a Stratholme para comenzar la matanza. ¡Debemos contener y purgar a los infectados por el bien de todo Lordaeron!' WHERE entry=2000005804;
UPDATE db_script_string SET content_loc6='Todos los oficiales deben avisarme cuando sus escuadrones estén listos. Entraremos en Stratholme a mi orden.' WHERE entry=2000005805;
UPDATE db_script_string SET content_loc6='Parece que todo el mundo está listo. Recordad, esta gente está infectada por la peste y pronto morirá. Debemos purgar Stratholme para proteger de la Plaga lo que queda de Lordaeron. Vamos.' WHERE entry=2000005806;
UPDATE db_script_string SET content_loc6='A todo el mundo que conozco parece estar pasándole algo realmente malo.' WHERE entry=2000005807;
UPDATE db_script_string SET content_loc6='¿Has hablado con alguien que no haya empezado a sentirse enfermo?' WHERE entry=2000005808;
UPDATE db_script_string SET content_loc6='Tiene que ser algo del agua, te lo digo.' WHERE entry=2000005809;
UPDATE db_script_string SET content_loc6='Mi cama tiene muy buena pinta ahora.' WHERE entry=2000005810;
UPDATE db_script_string SET content_loc6='¡Príncipe Arthas, bendita sea la Luz! Muchos ciudadanos han comenzado a enfermar gravemente, ¿nos puedes ayudar?' WHERE entry=2000005811;
UPDATE db_script_string SET content_loc6='Solo puedo ayudarte con una muerte limpia.' WHERE entry=2000005812;
UPDATE db_script_string SET content_loc6='¿Qué? ¡No puede ser!' WHERE entry=2000005813;
UPDATE db_script_string SET content_loc6='¿Has visto eso?' WHERE entry=2000005815;
UPDATE db_script_string SET content_loc6='Solo ha sido el comienzo.' WHERE entry=2000005816;
UPDATE db_script_string SET content_loc6='Sí, este es el principio. Te he estado esperando, joven Príncipe. Soy Mal\'Ganis.' WHERE entry=2000005817;
UPDATE db_script_string SET content_loc6='Como puedes ver, tu gente ahora me pertenece. Pondré esta ciudad patas arriba, casa por casa, hasta que la llama de la vida quede extinguida... para siempre.' WHERE entry=2000005818;
UPDATE db_script_string SET content_loc6='¡No lo permitiré, Mal\'Ganis! ¡Será mejor que esta gente muera por mi mano a que sean tus esclavos en la muerte!' WHERE entry=2000005819;
UPDATE db_script_string SET content_loc6='Mal\'Ganis enviará a sus esbirros de la Plaga para que interfieran con nosotros. Los que tengáis el acero y la magia más fuertes iréis a acabar con ellos. Yo lideraré al resto de mis tropas para purgar a los infectados de Stratholme.' WHERE entry=2000005820;
UPDATE db_script_string SET content_loc6='Te agradezco que me hayas salvado en esta línea del tiempo. Ahora, tengo que informar a mis superiores. Tienen que saber de inmediato lo que me ha pasado.' WHERE entry=2000005821;
UPDATE db_script_string SET content_loc6='Tu poder nos fortalece a todos.' WHERE entry=2000005826;
UPDATE db_script_string SET content_loc6='Nuestras vidas son tuyas, Caballero de sangre.' WHERE entry=2000005828;
UPDATE db_script_string SET content_loc6='Incluso la Luz no es rival para la voluntad indomable de nuestro pueblo.' WHERE entry=2000005829;
UPDATE db_script_string SET content_loc6='No pensaste en serio que volvería a hacer negocios contigo, ¿verdad? A veces, la mejor forma de volver a tener rentabilidad es saber cuándo recortar tus pérdidas.' WHERE entry=2000005833;
UPDATE db_script_string SET content_loc6='Mi espada Archeus me sirvió bien en vida, pero al abandonar mi espíritu esta infeliz existencia, ya no la necesito más.' WHERE entry=2000005834;
UPDATE db_script_string SET content_loc6='Me aferraré al amor de mi hija y espero encontrar el perdón bajo la Luz por mis pecados.' WHERE entry=2000005835;

