UPDATE db_script_string SET content_loc7='%s emite un gruñido silencioso mientras mira por encima del hombro.' WHERE entry=2000005266; /* content: %s emits a low, quiet growl, looking back over its shoulder. */
UPDATE db_script_string SET content_loc7='¿Qué tipo de cosa serviría a esos despreciables y siniestros Cultos Vermis?' WHERE entry=2000005267; /* content: What manner of thing would ever serve those sinister Wyrmcult wretches? */
UPDATE db_script_string SET content_loc7='¿Cómo ha conseguido tantos seguidores el Culto Vermis? ¡¿De dónde vienen?!' WHERE entry=2000005268; /* content: How has the Wyrmcult gained so many followers? Where are they coming from?! */
UPDATE db_script_string SET content_loc7='El Culto Vermis debe de estar usando otro camino. Los seguimos encontrando en la Foresta Ruuan.' WHERE entry=2000005269; /* content: The Wyrmcult must be using another path. We are still finding them in Ruuan Weald. */
UPDATE db_script_string SET content_loc7='Debemos esperar aquí. Enviaron a otros primero para comprobar el interior del túnel.' WHERE entry=2000005270; /* content: We are to wait here. They sent others ahead to check the tunnel interior. */
UPDATE db_script_string SET content_loc7='Con todo el respeto, A\'dal nos envió aquí para investigar las actividades de Kael, no para organizar un ataque a gran escala.' WHERE entry=2000005271; /* content: With all due respect, A\'dal sent us here to investigate Kael\'s activities, not mount a full-scaled attack. */
UPDATE db_script_string SET content_loc7='¡Y mira lo que hemos conseguido con eso!' WHERE entry=2000005278; /* content: ...and just look at where that got us! */
UPDATE db_script_string SET content_loc7='¡Por fin el chico grande habla de acción!' WHERE entry=2000005280; /* content: Finally some talk of action from the big guy! */
UPDATE db_script_string SET content_loc7='¿Ya es seguro pasar por ese maldito chisme?' WHERE entry=2000005283; /* content: Is that damned thing safe to go through yet? */
UPDATE db_script_string SET content_loc7='En realidad, no.' WHERE entry=2000005284; /* content: Not really. */
UPDATE db_script_string SET content_loc7='¿Puedes parar de preguntar eso?' WHERE entry=2000005285; /* content: Will you stop asking that? */
UPDATE db_script_string SET content_loc7='Está bien, parece seguro. ¡Entra ahí, ingeniero!' WHERE entry=2000005286; /* content: Alright, it looks safe. Get in there, engineer! */
UPDATE db_script_string SET content_loc7='¡A ver qué pasa!' WHERE entry=2000005287; /* content: Here goes nothing! */
UPDATE db_script_string SET content_loc7='Esto dejará marca...' WHERE entry=2000005288; /* content: This is gonna leave a mark... */
UPDATE db_script_string SET content_loc7='%s se toca con sus manos para comprobar que todo está en su sitio.' WHERE entry=2000005289; /* content: %s runs his hands across his body to make sure everything is intact. */
UPDATE db_script_string SET content_loc7='¡Lo conseguí! ¡Lo conseguí!' WHERE entry=2000005290; /* content: I made it! I made it! */
UPDATE db_script_string SET content_loc7='%s desaprueba con la cabeza.' WHERE entry=2000005291; /* content: %s shakes his head in disapproval. */
UPDATE db_script_string SET content_loc7='No me gustaba mucho, de todas formas. Hablaba mucho y hacía poco.' WHERE entry=2000005292; /* content: I didn\'t like him much anyway. Too much talk, not enough "do." */
UPDATE db_script_string SET content_loc7='¡Que os vaya bien, estúpidos incompetentes! ¡Otro ingeniero vaporizado!' WHERE entry=2000005293; /* content: Nice going you incompetent morons! Another one of our engineers vaporized! */
UPDATE db_script_string SET content_loc7='¡Disfruta de las actuaciones de Perry Gatner en unos instantes!' WHERE entry=2000005302; /* content: Check out the comedy stylings of Perry Gatner in just a few minutes! */
UPDATE db_script_string SET content_loc7='Si estás $glisto:lista; para divertirte, vete a la taberna del Fin del Mundo.' WHERE entry=2000005303; /* content: If you are ready to laugh, head over to the World\'s End Tavern! */
UPDATE db_script_string SET content_loc7='Ahora en la Ciudad de Shattrath: ¡los Tauren Chieftains! ¡Actuarán en la Taberna del Fin del Mundo en solo unos minutos!' WHERE entry=2000005304; /* content: Now, in Shattrath City: The Tauren Chieftains! Playing in the World\'s End Tavern at the top of the hour! */
UPDATE db_script_string SET content_loc7='¡Dijiste que Brogg podía quedarse! ¡Prometiste a Brogg que ayudarías a recuperar el montículo del clan Quebrantarrocas!' WHERE entry=2000005306; /* content: You said Brogg could stay here! You promised Brogg you\'d help regain Stonemaul clan mound! */
UPDATE db_script_string SET content_loc7='Mmmm... ¿Dónde está mi caña de pescar? Oh, ahí está.' WHERE entry=2000005307; /* content: Hmmm... Where is my fishing hook? Oh, there it is. */
UPDATE db_script_string SET content_loc7='Veamos si los peces pican...' WHERE entry=2000005308; /* content: Let\'s see if the fish are biting. */
UPDATE db_script_string SET content_loc7='Mmmm... Creo que necesito preparar cebos. Iré a mirar en el vagón de mi padre.' WHERE entry=2000005309; /* content: Hmmm... I think I need better bait. I\'ll check my father\'s wagon. */
UPDATE db_script_string SET content_loc7='¡Desechos de jabaespín! Eso debería de servir. A los peces les encantan los jabaespines.' WHERE entry=2000005310; /* content: Quillboar scraps! These should do the trick. Fish love Quillboar. */
UPDATE db_script_string SET content_loc7='¡He cogido uno! Voy a ver si alguno de los guardias tiene hambre. ¡Vamos, Colmillo!' WHERE entry=2000005311; /* content: Caught one! I\'ll see if any of the guards are hungry. Come on Fang! */
UPDATE db_script_string SET content_loc7='He pescado un pez... Si tienes hambre' WHERE entry=2000005312; /* content: I caught a fish if you are hungry... */
UPDATE db_script_string SET content_loc7='¡Muy bien hecho! ¡Traes honor a la Horda, chico, te lo agradecemos!' WHERE entry=2000005313; /* content: A nice catch it is at that! You bring honor to the Horde boy, We thank you! */
UPDATE db_script_string SET content_loc7='¿Quiere algo más de comer, señor?' WHERE entry=2000005314; /* content: Would you like something more to eat sir? */
UPDATE db_script_string SET content_loc7='¡Más pan, chico!' WHERE entry=2000005315; /* content: More bread boy! */
UPDATE db_script_string SET content_loc7='Ahora mismo, señor.' WHERE entry=2000005316; /* content: Right away sir. */
UPDATE db_script_string SET content_loc7='Aquí está su pan, señor.' WHERE entry=2000005317; /* content: Here is your bread sir. */
UPDATE db_script_string SET content_loc7='Aaaah... Muy bien. ¡Ahora, fuera!' WHERE entry=2000005318; /* content: Aaahh... Very good. Now scat! */
UPDATE db_script_string SET content_loc7='Como desees, señor.' WHERE entry=2000005319; /* content: As you wish sir. */
UPDATE db_script_string SET content_loc7='¿Puedo ir a buscar más piedras para mi collar, papá?' WHERE entry=2000005320; /* content: Can I go collect more rocks for my necklace, Papa? */
UPDATE db_script_string SET content_loc7='¡Dabu! ¡Tendré cuidado, papá!' WHERE entry=2000005322; /* content: Dabu! I will be careful Papa! */
UPDATE db_script_string SET content_loc7='Ooohh... Esta piedra es bonita y me gusta mucho el color. A ver si puedo encontrar más iguales...' WHERE entry=2000005323; /* content: Ooohh... This rock is nice and colorful. Let\'s see if I can find more like it... */
UPDATE db_script_string SET content_loc7='Mmm... Esa piedra es bonita, pero demasiado pequeña.' WHERE entry=2000005324; /* content: Hmm... That rock is nice, but too small. */
UPDATE db_script_string SET content_loc7='Seguro que a mi amigo Gruk le gustaría esta piedra para la lanza que está haciendo. Es muy afilada... Espero que le guste.' WHERE entry=2000005326; /* content: I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it. */
UPDATE db_script_string SET content_loc7='Ya está... Con esto debería tener suficientes piedras para hacer un collar. De todas formas, ya debería volver a casa...' WHERE entry=2000005327; /* content: There... That should be enough rocks to make a necklace. I should probably get home now anyway. */
UPDATE db_script_string SET content_loc7='Te he traído un regalo de bienvenida, Jarl.' WHERE entry=2000005328; /* content: I brought you a housewarming gift, Jarl. */
UPDATE db_script_string SET content_loc7='Vamos, ábrelo. Creo que te gustará.' WHERE entry=2000005329; /* content: Go on, open it. I think you\'ll like it. */
UPDATE db_script_string SET content_loc7='¿Jarl?' WHERE entry=2000005330; /* content: Jarl? */
UPDATE db_script_string SET content_loc7='A menos que sea algo que se pueda comer, no lo quiero.' WHERE entry=2000005331; /* content: Unless it\'s something I can eat, I don\'t want it. */
UPDATE db_script_string SET content_loc7='Jarl, no has sido tú mismo desde que llegué. ¿Qué está pasando?' WHERE entry=2000005332; /* content: Jarl, you haven\'t been yourself ever since I arrived. What\'s going on? */
UPDATE db_script_string SET content_loc7='Nada. ¿Quieres ayudarme a preparar un estofado de anca de rana?' WHERE entry=2000005333; /* content: Nothing. Wanna help me whip up some frog leg stew? */
UPDATE db_script_string SET content_loc7='¡He venido a visitarte, Jarl! ¡Y te encuentro viviendo en una cabaña vacía en medio de la nada balbuceando sobre ranas! ¡Eres un caso perdido!' WHERE entry=2000005334; /* content: I came out here to visit you, Jarl! And I find you living in an empty cabin in the middle of nowhere, babbling about frogs? You\'re hopeless! */
UPDATE db_script_string SET content_loc7='Y no descansaré hasta llegar al final de este asunto... Así que si necesitas mi ayuda de nuevo, ya sabes dónde encontrarme.' WHERE entry=2000005335; /* content: And I am determined to find out more... So if you need my help again, you know where I\'ll be. */
UPDATE db_script_string SET content_loc7='¿Anaia?... ¿Me engañan mis ojos? ¿Eres tú de verdad?' WHERE entry=2000005336; /* content: Anaya...? Do my eyes deceive me? Is it really you? */
UPDATE db_script_string SET content_loc7='Él ha sido cruel con nosotros, amor mío, pero ten por seguro que ahora sí permaneceremos unidos.' WHERE entry=2000005337; /* content: The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited. */
UPDATE db_script_string SET content_loc7='El destino ha sido muy cruel al permitirnos volver a estar juntos solo tras miles de años de separación...' WHERE entry=2000005338; /* content: That the fates should be so cruel as to permit us only this after a thousand years apart... */
UPDATE db_script_string SET content_loc7='¿Me odias, amor mío? Por haber tenido que destruir tu forma viviente y por liberar tu espíritu de tan triste cautiverio...' WHERE entry=2000005339; /* content: Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage. */
UPDATE db_script_string SET content_loc7='No permitas que esto atormente tu corazón, amor mío. Me has liberado de la esclavitud y por ello te amo más aún.' WHERE entry=2000005340; /* content: Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more. */
UPDATE db_script_string SET content_loc7='Desafortunadamente, esto también debe interrumpirse... Los lazos que me atan a este mundo se debilitan y me arrastran...' WHERE entry=2000005341; /* content: Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away... */
UPDATE db_script_string SET content_loc7='¡No! Anaia... ¡Anaia! ¡No me abandones! Por favor...' WHERE entry=2000005342; /* content: No! Anaya... Anaya! Don\'t leave me! Please... */
UPDATE db_script_string SET content_loc7='Adiós, Cerellean, hasta que volvamos a reunirnos...' WHERE entry=2000005343; /* content: Farewell, Cerellean, until we are joined once again... */
UPDATE db_script_string SET content_loc7='¿Cómo, amor mío? ¿Cómo encontraré fuerzas para enfrentarme a las edades del mundo sin ti a mi lado...?' WHERE entry=2000005345; /* content: How, my love? How will I find the strength to face the ages of the world without you by my side... */
UPDATE db_script_string SET content_loc7='Salto de Fonroca se encuentra en las montañas al Este y Norte.' WHERE entry=2000005347; /* content: Cliffspring Falls lies along the mountains to the east and north. */
UPDATE db_script_string SET content_loc7='¿Qué significa esto, Mor\'ghor?' WHERE entry=2000005348; /* content: What is the meaning of this, Mor\'ghor? */
UPDATE db_script_string SET content_loc7='¡Silencio!' WHERE entry=2000005349; /* content: SILENCE! */
UPDATE db_script_string SET content_loc7='¿ESTO es tu héroe?' WHERE entry=2000005351; /* content: THIS is your hero? */
UPDATE db_script_string SET content_loc7='Te han engañado, imbécil.' WHERE entry=2000005352; /* content: You have been deceived, imbecile. */
UPDATE db_script_string SET content_loc7='¡La... operación... entera... HA SIDO COMPROMETIDA!' WHERE entry=2000005353; /* content: This... whole... operation... HAS BEEN COMPROMISED! */
UPDATE db_script_string SET content_loc7='Espero ver el caparazón de ese insecto hecho pedazos en mi guarida dentro de una hora. Fracasa y tu destino será mucho peor que la muerte.' WHERE entry=2000005354; /* content: I expect to see this insect\'s carcass in pieces in my lair within the hour. Fail and you will suffer a fate so much worse than death. */
UPDATE db_script_string SET content_loc7='%s comienza a manipular la Piedra de Relu sobre los dos fósiles.' WHERE entry=2000005356; /* content: %s begins to manipulate the Stone of Relu over the two fossils. */
UPDATE db_script_string SET content_loc7='¿Quién ha invocado a Aman?' WHERE entry=2000005357; /* content: Who hath summoned forth Aman? */
UPDATE db_script_string SET content_loc7='Ah, veo que trabajas arduamente con las reliquias del pasado.' WHERE entry=2000005358; /* content: Ah, I see you toil with relics of the past. */
UPDATE db_script_string SET content_loc7='Te advierto que incluso tus creadores pueden equivocarse.' WHERE entry=2000005359; /* content: Be warned that even your creators are fallible. */
UPDATE db_script_string SET content_loc7='Si escarbas demasiado en tu pasado, tu futuro puede tener un fin repentino.' WHERE entry=2000005360; /* content: Digging too deep into your past might bring an abrupt end to your future. */
UPDATE db_script_string SET content_loc7='¡Saludos, Gezhe! Se han enviado diecisiete pedidos más al sector Hara\'samid. Los mercados de la veladrita pesada se han venido abajo, tal y como predijiste.' WHERE entry=2000005362; /* content: Hail, Gezhe!  Seventeen more orders have been placed in the Hara\'samid Sector.  The markets of heavy veldarite have all but collapsed, as you predicted. */
UPDATE db_script_string SET content_loc7='Las exportaciones de Heraazi están subiendo. Ya hemos untado a los oficiales de aduanas que hacía falta y el contrabando empezará a llegar en cuestión de días.' WHERE entry=2000005363; /* content: Heraazi exports are on the rise, sir.  We\'ve already bribed the appropriate taxation officers and contraband will begin to arrive within days. */
UPDATE db_script_string SET content_loc7='No me sorprende nada. Vuelve con alguna noticia de verdad, ¿de acuerdo?' WHERE entry=2000005364; /* content: Nothing surprising there.  Come back with some real news, will you? */
UPDATE db_script_string SET content_loc7='Lo que pensaba. Está bien. Puedes irte.' WHERE entry=2000005365; /* content: As expected.  Very well, dismissed. */
UPDATE db_script_string SET content_loc7='Con tu venia, señor.' WHERE entry=2000005366; /* content: By your leave, sir. */
UPDATE db_script_string SET content_loc7='Si estás aquí, significa que ya puedes empezar a estudiar la invocación de cohortes demoníacas para hacer lo que te plazca. Empezaremos por las criaturas más humildes que podrás invocar y continuaremos a partir de ahí. Comencemos.' WHERE entry=2000005367; /* content: If you\'re here, then it means you are prepared to begin the study of summoning demonic cohorts to do your bidding. We will start with the lowliest creatures you will be able to call and continue from there. Let us begin. */
UPDATE db_script_string SET content_loc7='La criatura más fácil de invocar para ti es el diablillo. A estas alturas, ya deberías ser capaz de crear al esbirro, pero por razones de complejidad, empezaré por él.' WHERE entry=2000005368; /* content: The easiest creature for you to summon is the imp. You should already be able to bring forth this minion but for completeness\' sake I will start with him. */
UPDATE db_script_string SET content_loc7='Esta pequeña bestia hedionda es el diablillo. Es pequeño y débil, lo que lo convierte en un ser prácticamente inútil como escudo de carne y hueso y su capacidad de destrucción es realmente mediocre. Esta criatura resulta más útil como apoyo de un grupo más grande.' WHERE entry=2000005369; /* content: This foul little beast is the imp. It is small and weak, making it almost useless as a meatshield, and its damage output is mediocre at best. This creature is best used for support of a larger group. */
UPDATE db_script_string SET content_loc7='Ahora que has podido estudiar el diablillo, pasemos al siguiente esbirro que podrás invocar, el abisario.' WHERE entry=2000005370; /* content: Now that you have had a chance to study the imp, let us move on to the next minion you will be able to summon, the voidwalker. */
UPDATE db_script_string SET content_loc7='Este ente demoníaco se conoce como abisario. Si fuerza y resistencia son importantes, resultando ideal para la defensa. Envíalo al frente para atacar a tu enemigo y luego úsalo como escudo mientras utilizas tus encantamientos y habilidades para extinguir la vida de tu oponente.' WHERE entry=2000005371; /* content: This demonic entity is known as the Voidwalker. Its strength and endurance are significant, making it ideal for defense. Send it to attack your enemy, then use it as a shield while you use your spells and abilities to drain away your opponent\'s life. */
UPDATE db_script_string SET content_loc7='Merece la pena contemplarlo, por si no lo has visto antes. Se trata de una criatura realmente impresionante dentro como fuera del campo de batalla. Ahora, echemos un vistazo a lo que estoy seguro que todos ustedes, alumnos varones, han estado esperando. El súcubo.' WHERE entry=2000005372; /* content: If you\'ve never seen one, it is a sight to behold. A very impressive creature both on and off the field of battle. Next, let us take a look at what I am sure all you male students have been waiting for. The succubus. */
UPDATE db_script_string SET content_loc7='Muy bien. Además de la distracción evidente que un esbirro como este supondrá para sus enemigos masculinos, ella también tiene una capacidad de destrucción impresionante. No obstante, su frágil resistencia la hace casi inservible como escudo.' WHERE entry=2000005373; /* content: All right now. Aside from the obvious distractions a minion like this will provide against your more masculine foes, she is also capable of dealing out impressive amounts of damage. However, her fragile endurance makes her almost useless as a shield. */
UPDATE db_script_string SET content_loc7='Estudia mucho y quizás algún día podrás invocar a tu propio esbirro, pero ahora pasaremos a estudiar al manáfago.' WHERE entry=2000005374; /* content: Study hard and you might one day be able to summon one on your own, but for now it\'s time to move on to the felhunter. */
UPDATE db_script_string SET content_loc7='Lo que tenéis delante es un manáfago. Entre los talentos naturales de esta criatura está la capacidad de neutralizar encantamientos y otras habilidades, convirtiéndola en un ser inigualable a la hora de enfrentarse a un oponente con poderes mágicos.' WHERE entry=2000005375; /* content: What you see before you is a felhunter. This creature\'s natural talents include spell lock and other abilities which make it unequalled when facing a magically attuned opponent. */
UPDATE db_script_string SET content_loc7='A la hora de enfrentarse a cualquier taumaturgo, esta bestia salvaje será tu mejor amigo. Veamos ahora algo ligeramente distinto. La siguiente criatura te ayudará en tus viajes y hará más agradables tus viajes futuros. Vamos a echar un vistazo a un corcel vil.' WHERE entry=2000005376; /* content: When facing a spellcaster of any kind, this feral beast will be your best friend. Now, let us take a look at something a bit different. This next creature will aid your travels and make your future journeys much easier. Let\'s take a look at a felsteed. */
UPDATE db_script_string SET content_loc7='Aquí lo tenéis. Nuestra lección sobre las invocaciones ha llegado a su fin. En breve iniciaremos otra clase, así que si queréis poneros al día, os podéis quedar por aquí.' WHERE entry=2000005378; /* content: There you have it. Our lesson on summoning has come to an end. A new class will begin shortly, so if you wish to brush up, feel free to stay around. */
UPDATE db_script_string SET content_loc7='¡Uf! Aquí está mi sombrero de la suerte. Lo había buscado por todas partes.' WHERE entry=2000005379; /* content: Phew! There\'s my lucky hat. I\'ve been looking for it everywhere. */
UPDATE db_script_string SET content_loc7='No sé en qué estaba pensando. Mira que salir sin mi espada... Me la habría puesto si la hubiera visto aquí...' WHERE entry=2000005380; /* content: I don\'t know what I was thinking, going out without my sword. I would\'ve put it on if I\'d seen it here... */
UPDATE db_script_string SET content_loc7='No puedo seguir sin mi capa. Espero que esté aquí...' WHERE entry=2000005381; /* content: I can\'t possibly go out without my cloak. I hope it\'s in here... */
UPDATE db_script_string SET content_loc7='¡Aquí está! Juraría que no estaba aquí la última vez que miré.' WHERE entry=2000005382; /* content: There it is! I could\'ve sworn it wasn\'t here last time I checked... */
UPDATE db_script_string SET content_loc7='No puedo dormir si no me cuentan un buen cuento. Ahora, seguro que duermo bien.' WHERE entry=2000005383; /* content: I can\'t sleep without a good bedtime story. Now I\'m certain to rest well. */
UPDATE db_script_string SET content_loc7='¿Qué ha sido ese ruido? Parecía un rugido.' WHERE entry=2000005384; /* content: What was that noise? It sounded like a roar. */
UPDATE db_script_string SET content_loc7='¡¿Zarpadura?! Ahí mismo $gamigo mío:amiga mía;... ¿dónde está tu maestro Volcor?' WHERE entry=2000005386; /* content: Grimclaw?! Easy there, my friend... where is your master Volcor? */
UPDATE db_script_string SET content_loc7='¡Guau! Espera. Un momento...' WHERE entry=2000005388; /* content: Whoa, whoa there, my friend. One moment... */
UPDATE db_script_string SET content_loc7='Ahí está... esto deberá servir. Ahora dime qué ha pasado, Zarpadura.' WHERE entry=2000005390; /* content: There... that should help. Now, tell me what\'s happened, Grimclaw. */
UPDATE db_script_string SET content_loc7='Ya entiendo, $gamigo mío:amiga mía;. Encontraré a alguien que ayude a tu maestro. Vuelve junto a él ahora, o al menos permanece cerca de él.' WHERE entry=2000005392; /* content: I understand, my friend. I shall find someone to help your master. Go back to him now, or at least stay close. */
UPDATE db_script_string SET content_loc7='Si dispones de tiempo, podrías ayudar a Zarpadura y a su maestro Volcor. Si te interesa, habla conmigo más adelante...' WHERE entry=2000005394; /* content: If you have the time, Grimclaw and his master Volcor could use your help. If you\'re interested, speak with me further... */
UPDATE db_script_string SET content_loc7='Ya he tenido bastante de tanto lloriqueo incesante, capitán. ¿Y de qué eres capitán tú? ¡Lo que hay que ver!' WHERE entry=2000005395; /* content: I\'ve had just about enough of your incessant whining, captain. What are you even a captain of? I mean, The nerve! */
UPDATE db_script_string SET content_loc7='%s se encoge de hombros.' WHERE entry=2000005397; /* content: %s shrugs. */
UPDATE db_script_string SET content_loc7='Vaya. Otra vez a revisar el diseño.' WHERE entry=2000005398; /* content: Oh well, back to the drawing board. */
UPDATE db_script_string SET content_loc7='Por favor, discúlpame mientras comienzo mis pruebas...' WHERE entry=2000005400; /* content: Please excuse me while I begin my tests... */
UPDATE db_script_string SET content_loc7='Interesante...' WHERE entry=2000005401; /* content: Interesting... */
UPDATE db_script_string SET content_loc7='Pero, ¿qué es esto?' WHERE entry=2000005402; /* content: But what is this? */
UPDATE db_script_string SET content_loc7='¡Oh, vaya!' WHERE entry=2000005403; /* content: Oh, my! */
UPDATE db_script_string SET content_loc7='Si la sangre no se me hubiera coagulado hace tiempo, estaría hirviendo de rabia ahora mismo...' WHERE entry=2000005404; /* content: If my blood hadn\'t clotted long ago, it would be boiling with rage right now... */
UPDATE db_script_string SET content_loc7='El sacerdote se arrodilla y reza en silencio.' WHERE entry=2000005409; /* content: The priest kneels in silent prayer. */
UPDATE db_script_string SET content_loc7='Luz, dame fuerza.' WHERE entry=2000005410; /* content: Light, give me strength. */
UPDATE db_script_string SET content_loc7='Luz, guía a los caídos de la falsedad a la verdad, de la oscuridad hacia la luz y de la muerte a la vida infinita.' WHERE entry=2000005411; /* content: Light, lead the fallen from untruth to truth, from darkness to Light and from death to immortality. */
UPDATE db_script_string SET content_loc7='¡¡Nooooo!!' WHERE entry=2000005415; /* content: NOOOOO!!! */
UPDATE db_script_string SET content_loc7='Amigos... Os he fallado...' WHERE entry=2000005416; /* content: Friends... I\'ve failed you... */
UPDATE db_script_string SET content_loc7='No, amigo mío... Nosotros te hemos fallado. Te hemos fallado.' WHERE entry=2000005417; /* content: No my friend... we failed you... we failed you. */
UPDATE db_script_string SET content_loc7='Todo está dispuesto, belisario.' WHERE entry=2000005418; /* content: Everything is in readiness, warbringer. */
UPDATE db_script_string SET content_loc7='El señor de fatalidad Kazzak estará satisfecho. Vas a aumentar el ritmo de tus ataques. Destruid a los orcos y los bastiones de los enanos se precipitarán.' WHERE entry=2000005419; /* content: Doom Lord Kazzak will be pleased. You are to increase the pace of your attacks. Destroy the orcish and dwarven strongholds with all haste. */
UPDATE db_script_string SET content_loc7='No te preocupes por eso. He aumentado la producción en la Forja Muerta. Tendrás a todos los infernales que quieras para que sigan tus órdenes. No fracases, Jovaan.' WHERE entry=2000005421; /* content: Don\'t worry about that. I\'ve increased production at the Deathforge. You\'ll have all the infernals you need to carry out your orders. Don\'t fail, Jovaan. */
UPDATE db_script_string SET content_loc7='Se hará como dices, belisario. Una última pregunta, si se me permite...' WHERE entry=2000005422; /* content: It shall be as you say, warbringer. One last question, if I may... */
UPDATE db_script_string SET content_loc7='¿Pero?' WHERE entry=2000005423; /* content: Yes? */
UPDATE db_script_string SET content_loc7='¿Qué hay en la caja?' WHERE entry=2000005424; /* content: What\'s in the crate? */
UPDATE db_script_string SET content_loc7='¿Caja? No te envié una caja, Jovaan. ¿No tienes cosas más importantes de las que preocuparte? ¡Ve a ocuparte de ellas!' WHERE entry=2000005425; /* content: Crate? I didn\'t send you a crate, Jovaan. Don\'t you have more important things to worry about? Go see to them! */
UPDATE db_script_string SET content_loc7='Estoy harto de pedir las cosas con cortesía, hombre pez.' WHERE entry=2000005426; /* content: I\'m through asking nicely, fish-man. */
UPDATE db_script_string SET content_loc7='¡¿Dónde está la Clave de Condenación?! Sé que un tercio está en este pueblo.' WHERE entry=2000005427; /* content: Where is the Cipher of Damnation? I know a third of it is in this village. */
UPDATE db_script_string SET content_loc7='¿De qué me hablasss, piel ssuave?' WHERE entry=2000005428; /* content: Whhaaat... are you sssspeaking of, ssssoft-sskin? */
UPDATE db_script_string SET content_loc7='Los nagasss no tienen ninguna cccclave...' WHERE entry=2000005429; /* content: The naga do nottt... have thissss... cipherrr... */
UPDATE db_script_string SET content_loc7='No... Claro que no. ¿En qué estaría pensando? ¿Para qué quieren ustedes un tercio de la Clave de Condenación?' WHERE entry=2000005430; /* content: No... Of course not. What was I thinking? Why would you have a third of the Cipher of Damnation? */
UPDATE db_script_string SET content_loc7='Qué tonto. Quizás debería soltarte y dejarte marchar...' WHERE entry=2000005431; /* content: I feel so stupid. Maybe I should unchain you and set you free? */
UPDATE db_script_string SET content_loc7='Sssí..., sssí... Déjame marcharrr...' WHERE entry=2000005432; /* content: Yesss... Yesss... Ssset me freee... */
UPDATE db_script_string SET content_loc7='¡¿DE VERDAD CREES QUE SOY TAN ESTÚPIDO, NAGA?! ¡SUFRE!' WHERE entry=2000005433; /* content: DO I HONESTLY LOOK THAT STUPID TO YOU, NAGA? NOW YOU FEEL PAIN! */
UPDATE db_script_string SET content_loc7='%s escupe sobre el naga Cicatriz Espiral.' WHERE entry=2000005434; /* content: %s spits on the Coilskar naga. */
UPDATE db_script_string SET content_loc7='¡LEVÁNTATE, BASURA INMUNDA! ¡LEVÁNTATE ANTES DE QUE TE MATE!' WHERE entry=2000005435; /* content: GET UP YOU ROTTEN PIECE OF TRASH! GET UP BEFORE I END YOU! */
UPDATE db_script_string SET content_loc7='%s recupera la conciencia.' WHERE entry=2000005436; /* content: %s regains consciousness. */
UPDATE db_script_string SET content_loc7='¡No vuelvas a pegarle al comandante Cicatriz Essspiral, orco! ¡Te lo diré! La clave... essstá essscondido en el Alto Cicatriz Espiral. Los nagasss de allí lo guardan bajo llave...' WHERE entry=2000005438; /* content: Do not beat the Coilsssskaarrr commander anymore, orc! I will tell you! The cipherrr... It is hidden in one of the chests at Coilssskarrr Point... The naga there hold the keysss... */
UPDATE db_script_string SET content_loc7='¡CICATRIZ ESSSPIRAL!' WHERE entry=2000005439; /* content: COILSSSKARRR! */
UPDATE db_script_string SET content_loc7='Una voz tenebrosa y profunda surge de %s...' WHERE entry=2000005440; /* content: A deep, bone chilling voice echoes from the %s... */
UPDATE db_script_string SET content_loc7='¿Qué es lo que quieres de mí, chamán?' WHERE entry=2000005443; /* content: What is it that you want from me, shaman? */
UPDATE db_script_string SET content_loc7='¿Acaso no tienes ojos? ¿No has visto a los demonios en nuestro mismo umbral? ¡No puedo prescindir de ningún soldado!' WHERE entry=2000005444; /* content: Do you not have eyes? Did you not see the demons on our very doorstep? I do not have the soldiers to spare! */
UPDATE db_script_string SET content_loc7='Reza a tus dioses, chamán. Quizás vengan en tu ayuda. Sin embargo, la Horda no puede...' WHERE entry=2000005445; /* content: Pray to your gods, shaman. Perhaps they will come to your aid. The Horde, however, cannot... */
UPDATE db_script_string SET content_loc7='¡Silencio! ¡Ahora llega la destrucción!' WHERE entry=2000005446; /* content: Be silent! The shattering is soon to come! */
UPDATE db_script_string SET content_loc7='¡Contempla el innegable poder de nuestro oscuro amo!' WHERE entry=2000005447; /* content: Bear witness to the undeniable power of our dark master! */
UPDATE db_script_string SET content_loc7='¡Con este don, arrasaré la tierra para recrearla!' WHERE entry=2000005448; /* content: With his gift, I shall raze this land and reform it! */
UPDATE db_script_string SET content_loc7='¡Mira! ¡Mira cómo se abre el suelo ante nosotros! ¡Mira cómo fluye la energía! Alimentará a nuestro ejército...' WHERE entry=2000005449; /* content: Watch! See the ground shatter before us! Watch as the energy flows! It will feed our armies... */
UPDATE db_script_string SET content_loc7='¡Nunca nos faltará poder! He asegurado nuestro futuro. ¡Someteos a mi gloria!' WHERE entry=2000005450; /* content: We will never be without power! I have secured our future! Bask in my glory! */
UPDATE db_script_string SET content_loc7='Ya... está... hecho...' WHERE entry=2000005451; /* content: It... is... done... */
UPDATE db_script_string SET content_loc7='¡Parte la tierra, Gul\'dan! ¡Corta el vínculo!' WHERE entry=2000005452; /* content: Tear the land asunder, Gul\'dan! Sever the tie! */
UPDATE db_script_string SET content_loc7='¡No lo hagas, Gul\'dan! ¡Los elementos no nos perdonarán nunca!' WHERE entry=2000005453; /* content: Do not do this, Gul\'dan! The elements will never forgive us! */
UPDATE db_script_string SET content_loc7='¡Nos han abandonado! ¡No olvides!' WHERE entry=2000005455; /* content: They have abandoned us! Do not forget! */
UPDATE db_script_string SET content_loc7='¡Tomaremos por la fuerza lo que la tierra nos niegue!' WHERE entry=2000005456; /* content: We will take from the land if it refuses to give! */
UPDATE db_script_string SET content_loc7='¡Para! ¡Detente de una vez!' WHERE entry=2000005457; /* content: Stop! You must stop! */
UPDATE db_script_string SET content_loc7='Nos has condenado a todos... Estamos perdidos.' WHERE entry=2000005458; /* content: You have damned us all... We are lost. */
UPDATE db_script_string SET content_loc7='¡Si quieres la llave tendrás que cogerme!' WHERE entry=2000005460; /* content: If you want the key, you\'ll have to catch me! */
UPDATE db_script_string SET content_loc7='¡Cógeme si puedes! ¡Corro tan rápido como el viento!' WHERE entry=2000005461; /* content: Chase me if you dare! I run without a care! */
UPDATE db_script_string SET content_loc7='¿Por qué ibas a querer hacerme daño? ¡Vamos! ¡Podemos ser amigos!' WHERE entry=2000005462; /* content: Why would you ever want to harm me!? Come. Friends we can be! */
UPDATE db_script_string SET content_loc7='¿MORIR? ¡Estás haciendo llorar a Pusillín!' WHERE entry=2000005463; /* content: DIE?! You make Pusillin cry! */
UPDATE db_script_string SET content_loc7='¡Saluda a mis amiguitos!' WHERE entry=2000005464; /* content: Say hello to my little friends! */
UPDATE db_script_string SET content_loc7='¡A sus órdenes, señor!' WHERE entry=2000005465; /* content: As you command, Sir! */
UPDATE db_script_string SET content_loc7='Danos la fuerza de los gigantes de Sombraluna, Corok. El maestro es un gobernante justo. Se asegurará de cuidar de aquellos que ayudaron a dominar a los disidentes de este mundo.' WHERE entry=2000005466; /* content: Lend us the strength of the giants of Shadowmoon, Corok. The master is a just ruler. He will be sure to take care of those that assisted in subduing the dissidents of this world. */
UPDATE db_script_string SET content_loc7='Entonces os convertiréis en enemigos de mi señor, Illidan. Los enemigos de Illidan son destruidos. ¿Son capaces tus gigantes de resistir un ataque de las tropas de la Punta Eclipse?' WHERE entry=2000005468; /* content: Then you become enemies of my lord, Illidan. Enemies of Illidan are destroyed. Are your giants able to withstand an attack from the forces of Eclipse Point? */
UPDATE db_script_string SET content_loc7='No... no lo pensaba...' WHERE entry=2000005469; /* content: No... I did not think so... */
UPDATE db_script_string SET content_loc7='Volveremos a encontrarnos... Algún día.' WHERE entry=2000005471; /* content: We will meet again... Someday. */
UPDATE db_script_string SET content_loc7='¡La encarnación de la muerte ha sido liberada una vez más sobre el mundo! Teron Sanguino ha vuelto...' WHERE entry=2000005472; /* content: Death incarnate has been released upon the world once more! Teron Gorefiend has returned... */
UPDATE db_script_string SET content_loc7='¡Considéralo un calentamiento! ¡Tu entrenamiento real será mucho más duro!' WHERE entry=2000005476; /* content: Consider this a warm up!  Your real training will be far harsher! */
UPDATE db_script_string SET content_loc7='Con una recompensa como esa, ¿cómo podrían decir que no?' WHERE entry=2000005477; /* content: Wit a reward like dis, how could they refuse, mon? */
UPDATE db_script_string SET content_loc7='¡Escóndete! ¡Escóndete antes de que te vean! ¡Arriba!' WHERE entry=2000005478; /* content: Hide! Hide before they see you! Back up! */
UPDATE db_script_string SET content_loc7='¡Alto!' WHERE entry=2000005479; /* content: Halt! */
UPDATE db_script_string SET content_loc7='¿Qué sucede, mi señor?' WHERE entry=2000005480; /* content: What is it, my lord? */
UPDATE db_script_string SET content_loc7='¡Zarath, debes volver al Templo Oscuro de inmediato! Pa... parece que he extraviado las órdenes de Lord Illidan. ¡Rápido!' WHERE entry=2000005481; /* content: Zarath you must return to the Black Temple at once! I... I seem to have misplaced Lord Illidan\'s orders. Quickly! */
UPDATE db_script_string SET content_loc7='Mi señor, sin duda no esperas que te deje desatendido. Lord Illidan me cortaría la cabeza si te ocurriera algo.' WHERE entry=2000005482; /* content: My lord, surely you do not expect me to leave you unattended. Lord Illidan would have my head if anything were to happen to you. */
UPDATE db_script_string SET content_loc7='Zarath, soy perfectamente capaz de subir este camino hasta Punta Eclipse. ¡Si no entregamos la misiva, Lord Illidan nos cortará la cabeza a los dos! ¡Puedes marcharte!' WHERE entry=2000005483; /* content: Zarath, I am perfectly capable of making it up this road to Eclipse Point. If we do not deliver the missive, Lord Illidan will have both of our heads! You are dismissed! */
UPDATE db_script_string SET content_loc7='Como desee, mi señor.' WHERE entry=2000005484; /* content: As you wish, my lord. */
UPDATE db_script_string SET content_loc7='Ah, dulce cardo de sangre... Probablemente olvidado por uno de esos mugrientos adictos de Punta Eclipse.' WHERE entry=2000005485; /* content: Ah, sweet, sweet bloodthistle... Probably left behind by one of those filthy addicts at Eclipse Point. */
UPDATE db_script_string SET content_loc7='Su pérdida es, sin duda, mi ganancia...' WHERE entry=2000005486; /* content: Their loss is most definitely my gain... */
UPDATE db_script_string SET content_loc7='El momento de atacar se acerca. Acabad con Icarius.' WHERE entry=2000005487; /* content: The time to strike is at hand. Terminate Icarius. */
UPDATE db_script_string SET content_loc7='%s se mete el fardo de cardos de sangre en la toga.' WHERE entry=2000005488; /* content: %s puts the bundle of bloodthistle in his robe. */
UPDATE db_script_string SET content_loc7='Atrás, $n. Los espíritus pueden atacar.' WHERE entry=2000005489; /* content: Stand back, $N. The spirits may lash out. */
UPDATE db_script_string SET content_loc7='¿Por qué nos has capturado?' WHERE entry=2000005490; /* content: Why have you captured us? */
UPDATE db_script_string SET content_loc7='¿Qué esperas obtener de esto?' WHERE entry=2000005491; /* content: What is it that you hope to gain from this? */
UPDATE db_script_string SET content_loc7='%s se inclina.' WHERE entry=2000005492; /* content: %s bows. */
UPDATE db_script_string SET content_loc7='Queremos ayudarte, espíritu. Buscamos tu sabiduría.' WHERE entry=2000005493; /* content: We wish to help you, spirit. We seek your wisdom. */
UPDATE db_script_string SET content_loc7='¿Sabiduría? Tu pueblo fue el que provocó la terrible ira de Cyrukh.' WHERE entry=2000005494; /* content: Wisdom? It was your people that caused the insufferable wrath of Cyrukh. */
UPDATE db_script_string SET content_loc7='¿Por qué deberíamos ayudarte? El mundo está arrasado. Destruido.' WHERE entry=2000005495; /* content: Why should we help you? The world is ruined. Destroyed. */
UPDATE db_script_string SET content_loc7='%s asiente.' WHERE entry=2000005496; /* content: %s nods. */
UPDATE db_script_string SET content_loc7='Tienes razón, espíritu. Mi pueblo os hizo esto. Déjanos enmendar nuestros errores.' WHERE entry=2000005497; /* content: You are right, spirit. It was my people that did this to you. Allow us to make it right. */
UPDATE db_script_string SET content_loc7='Solo dinos esto: ¿qué o quién es Cyrukh?' WHERE entry=2000005498; /* content: Please tell us this: Who or what is Cyrukh? */
UPDATE db_script_string SET content_loc7='¡Insensato! ¡Cyrukh, el señor de Fuego! Cuando se rompió el vínculo, Cyrukh despertó. Son las lágrimas de Cyrukh las que llenan este mundo de roca fundida.' WHERE entry=2000005499; /* content: Fool! Cyrukh the fire lord! When the tie was severed, Cyrukh awoke. It is the tears of Cyrukh that fill this land with molten rock. */
UPDATE db_script_string SET content_loc7='El... ¿El Señor del Fuego? ¿Despertó a un señor elemental? Claro... ¡Maldito Gul\'dan!' WHERE entry=2000005500; /* content: The... The fire lord? An elemental lord was unearthed? Of course... Gul\'dan you were a damned fool! */
UPDATE db_script_string SET content_loc7='Ahora ha perdido la razón. Se volvió loco durante la rotura. No hay descanso para los elementos.' WHERE entry=2000005501; /* content: He is insane now. Driven mad during the shattering. There is no rest for the elements. */
UPDATE db_script_string SET content_loc7='Tiene que haber una forma.' WHERE entry=2000005502; /* content: There must be a way. */
UPDATE db_script_string SET content_loc7='Sí, una forma. Destruir a Cyrukh. Destruir a un señor elemental.' WHERE entry=2000005503; /* content: Yes, a way. Destroy Cyrukh. Destroy an elemental lord. */
UPDATE db_script_string SET content_loc7='No dudes de mis camaradas ni de mí, espíritu. Dinos cómo invocar al señor elemental. Mis aliados y yo lo destruiremos.' WHERE entry=2000005504; /* content: Do not doubt my comrades and I, spirit. Tell us how to conjure the fire lord. My allies will destroy him. */
UPDATE db_script_string SET content_loc7='Debéis pronunciar las palabras...' WHERE entry=2000005505; /* content: You must utter the words... */
UPDATE db_script_string SET content_loc7='Sí... Sí..., las palabras. Las palabras de la maldición. Las palabras pronunciadas por el oscuro, tu maestro.' WHERE entry=2000005506; /* content: Yes... Yes... the words. The words of damnation. The words spoken by the dark one. Your master. */
UPDATE db_script_string SET content_loc7='Sí... Gul\'dan...' WHERE entry=2000005507; /* content: Yes... Gul\'dan... */
UPDATE db_script_string SET content_loc7='Encontraremos esas palabras. Invocaremos a tu maestro y lo destruiremos.' WHERE entry=2000005508; /* content: We will find these words. We will summon your master and we will destroy him. */
UPDATE db_script_string SET content_loc7='Hablas. Solo hablas... Ahora libéranos, mortal.' WHERE entry=2000005509; /* content: Talk. More talk... Now release us, mortal. */
UPDATE db_script_string SET content_loc7='Ahora, a recuperar las palabras perdidas.' WHERE entry=2000005510; /* content: Now to recover the lost words! */
UPDATE db_script_string SET content_loc7='¿... crear un fondo para la compra de gomina? Me gusta mi pelo tal y como está, gracias.' WHERE entry=2000005511; /* content: ...to create a fund for the purchase of hair gel? I like my hair the way it is, thanks! */
UPDATE db_script_string SET content_loc7='¿... como desertor, disfrutarás de actividades como nadar atado y cuidar perros? ¡Qué ridículo!' WHERE entry=2000005512; /* content: ...as a deserter, you\'ll enjoy activities like tethered swimming and dog grooming? How ridiculous! */
UPDATE db_script_string SET content_loc7='Nunca me uniría a nada así. Mejor guardar esto. Puede ser de utilidad en el baño...' WHERE entry=2000005513; /* content: I\'d never join anything like this. Better keep this, though. It\'ll come in handy in the privy... */
UPDATE db_script_string SET content_loc7='¿... los desertores buscan un Theramore donde los ciudadanos puedan pasar el día haciendo malabarismos? ¿Qué es esta tontería?' WHERE entry=2000005514; /* content: ...the deserters seek a Theramore where citizens will be free to juggle at all hours of the day? What is this nonsense? */
UPDATE db_script_string SET content_loc7='Esto es lo más divertido que he leído en mucho tiempo. ¡Me has alegrado el día!' WHERE entry=2000005515; /* content: That\'s the funniest thing I\'ve read in a long time. You\'ve just made my day! */
UPDATE db_script_string SET content_loc7='Esto... esto es una broma, ¿verdad?' WHERE entry=2000005516; /* content: This... this is a joke, right? */
UPDATE db_script_string SET content_loc7='Hey, gracias.' WHERE entry=2000005517; /* content: Hey, thanks. */
UPDATE db_script_string SET content_loc7='%s lee el panfleto.' WHERE entry=2000005518; /* content: %s reads the leaflet. */
UPDATE db_script_string SET content_loc7='¿... recibe el 50 por ciento de descuento en ropa interior de desertores? ¿Qué es esta basura?' WHERE entry=2000005519; /* content: ...receive 50 percent off deserter undergarments? What is this garbage? */
UPDATE db_script_string SET content_loc7='¡Menuda panda de lunáticos! ¿De verdad os creéis esto?' WHERE entry=2000005520; /* content: What a bunch of lunatics! You actually believe this stuff? */
UPDATE db_script_string SET content_loc7='¡Gracias, $r! Tus servicios a los Mag\'har no se olvidarán.' WHERE entry=2000005521; /* content: Thank you, $r! Your service to the Mag\'har will not be forgotten. */
UPDATE db_script_string SET content_loc7='¡Muchas gracias, héroe!' WHERE entry=2000005522; /* content: Many Thanks, hero! */
UPDATE db_script_string SET content_loc7='¡Muerte a los ogros!' WHERE entry=2000005524; /* content: Death to the ogres! */
UPDATE db_script_string SET content_loc7='Intrusos: su victoria será efímera. Soy el Comandante Varos Cloudstrider. Mis dracos controlan los cielos y protegen este conducto. ¡Me encargaré personalmente de que el Óculo no caiga en sus manos!' WHERE entry=2000005526; /* content: Intruders, your victory will be short-lived. I am Commander Varos Cloudstrider. My drakes control the skies and protect this conduit. I will see to it personally that the Oculus does not fall into your hands! */
UPDATE db_script_string SET content_loc7='Solo aquellos que se alimentan de almas perdidas conocen el hambre.' WHERE entry=2000005529; /* content: They who dine on lost souls know only hunger. */
UPDATE db_script_string SET content_loc7='Contempla el vacío. Esa es la perpetuidad en la que moran.' WHERE entry=2000005530; /* content: Gaze into the void. It is the perpetuity in which they dwell. */
UPDATE db_script_string SET content_loc7='Se beben tu temor: es la sangre de la vida.' WHERE entry=2000005531; /* content: They drink your fear: it is the blood of life. */
UPDATE db_script_string SET content_loc7='Son el susurro del escalofriante viento.' WHERE entry=2000005532; /* content: They are the whisper on the shivering wind. */
UPDATE db_script_string SET content_loc7='No mueren, no viven. Están fuera del ciclo.' WHERE entry=2000005533; /* content: They do not die; they do not live. They are outside the cycle. */
UPDATE db_script_string SET content_loc7='Donde uno cae, muchos podrán ocupar su puesto.' WHERE entry=2000005534; /* content: Where one falls, many shall take its place. */
UPDATE db_script_string SET content_loc7='¡Gracias! No podía aguantar mucho más... Ha ocurrido algo terrible en este lugar.' WHERE entry=2000005535; /* content: Thank you! I could not have held out for much longer.... A terrible thing has happened here. */
UPDATE db_script_string SET content_loc7='Creíamos que el Sagrario estaba bien fortificado, pero no estábamos preparados para este tipo de asalto.' WHERE entry=2000005536; /* content: We believed the Sanctum was well-fortified, but we were not prepared for the nature of this assault. */
UPDATE db_script_string SET content_loc7='El Dragonante Negro se materializó de la nada y nos embistió antes de que pudiéramos reaccionar.' WHERE entry=2000005537; /* content: The Black Dragonkin materialized from thin air, and set upon us before we could react. */
UPDATE db_script_string SET content_loc7='Era nuestro fin. Con mis hermanos pereciendo a mi alrededor, logré la retirada hasta aquí, donde barriqué la entrada.' WHERE entry=2000005538; /* content: We did not stand a chance. As my brethren perished around me, I managed to retreat here and bar the entrance. */
UPDATE db_script_string SET content_loc7='Nos masacraron con una eficacia fría, pero parecían más interesados en los huevos guardados aquí, en el Sagrario.' WHERE entry=2000005539; /* content: They slaughtered us with cold efficiency, but the true focus of their interest seemed to be the eggs kept here in the sanctum. */
UPDATE db_script_string SET content_loc7='El comandante de las fuerzas terrestres es un salvaje cruel llamado Zarithrian, pero me temo que hay fuerzas superiores de por medio.' WHERE entry=2000005540; /* content: The commander of the forces on the ground here is a cruel brute named Zarithrian, but I fear there are greater powers at work. */
UPDATE db_script_string SET content_loc7='En su asalto inicial, pude ver fugazmente a su verdadero líder, un temible dragón crepuscular.' WHERE entry=2000005541; /* content: In their initial assault, I caught a glimpse of their true leader, a fearsome full-grown twilight dragon. */
UPDATE db_script_string SET content_loc7='Desconozco sus verdaderos planes, héroes, pero sí sé esto: ¡no hay que permitirles que los lleven a cabo!' WHERE entry=2000005542; /* content: I know not the extent of their plans, heroes, but I know this:  They cannot be allowed to succeed! */
UPDATE db_script_string SET content_loc7='¡Te lo agradezco!' WHERE entry=2000005544; /* content: You have my thanks! */
UPDATE db_script_string SET content_loc7='¡Vale, allá voy!' WHERE entry=2000005548; /* content: Ok, here I go! */
UPDATE db_script_string SET content_loc7='¡Venga, en marcha!' WHERE entry=2000005553; /* content: Let\'s move forward! */
UPDATE db_script_string SET content_loc7='Cuchicheos... pétalos de fuego... Cuchicheos...' WHERE entry=2000005554; /* content: ...mumble...Petals of Fire...mumble... */
UPDATE db_script_string SET content_loc7='Mmm... Mmm...' WHERE entry=2000005555; /* content: ...mumble mumble... */
UPDATE db_script_string SET content_loc7='... quizás... con la mezcla correcta...' WHERE entry=2000005556; /* content: ...with the right mixture, perhaps... */
UPDATE db_script_string SET content_loc7='... vides con espinas... murmura... ¡Auuu!' WHERE entry=2000005557; /* content: ...thorny vines...mumble...ouch! */
UPDATE db_script_string SET content_loc7='¡Vámonos, son demasiados! ¡Escapad mientras podéis!' WHERE entry=2000005558; /* content: Get out of here, there are too many of them! Escape while you can! */
UPDATE db_script_string SET content_loc7='¡Ayuda! ¡Que alguien nos ayude!' WHERE entry=2000005559; /* content: Help! Someone help us! */
UPDATE db_script_string SET content_loc7='Nuestros amigos lucharon bien, Brann, pero nosotros no hemos terminado todavía.' WHERE entry=2000005560; /* content: Our friends fought well, Brann, but we\'re not done yet. */
UPDATE db_script_string SET content_loc7='Puede, pero es solo cuestión de tiempo que volvamos a irrumpir en Ulduar. ¿Ha habido suerte al buscar un teletransportador?' WHERE entry=2000005561; /* content: Perhaps so, but it\'s only a matter of time until we break back into Ulduar. Any luck finding a way to teleport inside? */
UPDATE db_script_string SET content_loc7='En absoluto. Sospecho que tiene algo que ver con el gigantesco ensamblaje mecánico que nuestros exploradores divisaron frente a la puerta.' WHERE entry=2000005562; /* content: None at all. I suspect it has something to do with that giant mechanical construct that our scouts spotted in front of the gate. */
UPDATE db_script_string SET content_loc7='Bueno. Así que, al fin y al cabo, ¿tendremos que enfrentarnos a esa cosa?' WHERE entry=2000005563; /* content: Oi. So we\'ll have to contend with that thing after all then? */
UPDATE db_script_string SET content_loc7='¿Qué pasa con ese protodraco blindado y con el gigante de fuego que avistamos cerca? ¿Crees que tus magos pueden encargarse de ellos?' WHERE entry=2000005564; /* content: What about the plated proto-drake and the fire giant that were spotted nearby? Think your mages can handle those? */
UPDATE db_script_string SET content_loc7='Al Kirin Tor no le resulta posible dedicar ningún recurso adicional a enfrentarse a nada de semejante tamaño. Aunque puede que no sea necesario.' WHERE entry=2000005565; /* content: The Kirin Tor can\'t possibly spare any additional resources to take on anything that size. We may not have to though. */
UPDATE db_script_string SET content_loc7='Podemos pasar de largo sin que nos vean. Mientras podamos abatir el ensamblaje frente a la puerta, debiéramos ser capaces de entrar.' WHERE entry=2000005566; /* content: We can sneak past them. As long as we can take down that construct in front of the gate, we should be able to get inside. */
UPDATE db_script_string SET content_loc7='¿¡Escabullirnos!? ¿Qué crees que somos, marmotas?' WHERE entry=2000005567; /* content: Sneak?! What do you think we are, marmots? */
UPDATE db_script_string SET content_loc7='Bien. Si nuestros aliados son los que van a ensuciarse las manos, dejaremos que sean ellos quienes decidan cómo proceder.' WHERE entry=2000005569; /* content: Fine. If our allies are going to be the ones getting their hands dirty, we\'ll leave it to them to decide how to proceed. */
UPDATE db_script_string SET content_loc7='¡¿Qué... Qué has hecho, $n?! ¡Brann! ¡Braaaaaannnn!' WHERE entry=2000005570; /* content: What... What did you just do, $n?! Brann! Braaaaannn! */
UPDATE db_script_string SET content_loc7='¡Brann! ¡$n acaba de activar el sistema de defensa orbital! ¡Si no nos largamos pronto de aquí, estamos fritos!' WHERE entry=2000005571; /* content: Brann! $n just activated the orbital defense system! If we don\'t get out of here soon, we\'re going to be toast! */
UPDATE db_script_string SET content_loc7='¡Magos del Kirin Tor, cuando Brann dé la orden, abran el escudo! ¡Defiendan esta plataforma y nuestros aliados con sus vidas! ¡Por Dalaran! ¡Por Dalaran!' WHERE entry=2000005575; /* content: Mages of the Kirin Tor, on Brann\'s Command, release the shield! Defend this platform and our allies with your lives! For Dalaran! */
UPDATE db_script_string SET content_loc7='Nuestros aliados están preparados. ¡Desactiven el escudo y abran paso!' WHERE entry=2000005576; /* content: Our allies are ready. Bring down the shield and make way! */
UPDATE db_script_string SET content_loc7='¡Lo logramos! Llegamos antes que Algalon. Tal vez podamos alterar el sistema para interferir en su análisis…' WHERE entry=2000005578; /* content: We did it, lads! We got here before Algalon\'s arrival. Maybe we can rig the systems to interfere with his analysis-- */
UPDATE db_script_string SET content_loc7='Volveré al Archivum y trataré de interferir su señal. Tal vez nos de algo de tiempo mientras ustedes se ocupan de él.' WHERE entry=2000005579; /* content: I\'ll head back to the Archivum and see if I can jam his signal. I might be able to buy us some time while you take care of him. */
UPDATE db_script_string SET content_loc7='¡Oye! ¡Deja de merodear por mi sótano! ¡Lárgate!' WHERE entry=2000005580; /* content: Hey! Stop rooting around in my cellar! Clear out! */
UPDATE db_script_string SET content_loc7='¿Qué estabas haciendo en mi sótano? ¡Hay problemas con la comida y lo último que necesito son extraños enredando en mis bienes! ¡Fuera!' WHERE entry=2000005581; /* content: What were you doing in my cellar? There\'s a food scare going on, and the last thing I need is strangers rummaging around in my goods! Shoo! */
UPDATE db_script_string SET content_loc7='Toda esta situación resulta un poco paranoica, ¿no te parece?' WHERE entry=2000005582; /* content: This whole situation seems a bit paranoid, don\'t you think? */
UPDATE db_script_string SET content_loc7='Las órdenes son las órdenes. Si el Príncipe dice salta...' WHERE entry=2000005583; /* content: Orders are orders. If the Prince says jump... */
UPDATE db_script_string SET content_loc7='No puedes negar que es una orden rara. ¿Comida sospechosa? ¡Basándote en esa definición, deberías arrestar a Belfast!' WHERE entry=2000005584; /* content: It\'s a strange order, you can\'t deny. Suspicious food? Under that definition, you should arrest Belfast! */
UPDATE db_script_string SET content_loc7='¡TE HE OÍDO! ¡Se acabó la cerveza para ti! ¡Ni una gota más!' WHERE entry=2000005585; /* content: I HEARD THAT! No more ale for you! Not a drop! */
UPDATE db_script_string SET content_loc7='Ya basta, Michael. El negocio ya está sufriendo bastante con este susto. Podemos usar hasta la última moneda de cobre.' WHERE entry=2000005586; /* content: Enough, Michael. Business is hurting enough with this scare as it is. We can use every copper. */
UPDATE db_script_string SET content_loc7='Los soldados están realizando un gran trabajo. Mal, si lo que te interesa es que tus clientes vivan otro día más, la seguridad del pueblo es lo más importante.' WHERE entry=2000005587; /* content: The soldiers are doing important work. The safety of the people is more important, Mal, if you\'re interested in your customers living to spend another day. */
UPDATE db_script_string SET content_loc7='%s asiente a regañadientes.' WHERE entry=2000005588; /* content: %s grudgingly nods. */
UPDATE db_script_string SET content_loc7='Eso no lo puedo discutir.' WHERE entry=2000005589; /* content: I can\'t argue with that. */
UPDATE db_script_string SET content_loc7='No te preocupes demasiado. Para cuando terminé la guardia no habíamos encontrado ni un poquito de ese grano.' WHERE entry=2000005590; /* content: Don\'t worry too much. By the time I went off duty, we hadn\'t found a scrap of befouled grain here. */
UPDATE db_script_string SET content_loc7='Gracias a la Luz.' WHERE entry=2000005591; /* content: Thank the Light for that. */
UPDATE db_script_string SET content_loc7='%s asiente.' WHERE entry=2000005592; /* content: %s nods. */
UPDATE db_script_string SET content_loc7='¡Hombre! ¡Hola de nuevo!' WHERE entry=2000005593; /* content: Why, hello again! */
UPDATE db_script_string SET content_loc7='Vale, basta de trabajo por el momento. Es hora de tomar unos refrescos y charlar un rato en la posada.' WHERE entry=2000005594; /* content: Ok, enough work for now. Time for refreshments and a little conversation in the inn. */
UPDATE db_script_string SET content_loc7='Espera, ¿qué es ese olor?' WHERE entry=2000005595; /* content: Wait, what\'s that smell? */
UPDATE db_script_string SET content_loc7='No puedo ser yo. ¡Me bañé hace tres días!' WHERE entry=2000005596; /* content: Can\'t be me, I took a bath 3 days ago! */
UPDATE db_script_string SET content_loc7='Oh, por los pelos. Solo es el grano.' WHERE entry=2000005597; /* content: Oh, close call. It\'s just the grain here. */
UPDATE db_script_string SET content_loc7='Espera un segundo. ¡Se supone que el grano no debería oler ASÍ! Será mejor que vaya a buscar a un guardia.' WHERE entry=2000005598; /* content: Wait a second. Grain isn\'t supposed to smell like THAT! I better go find a guard. */
UPDATE db_script_string SET content_loc7='¿No te importa que eche un ojo a tu mercancía por si ha sido manipulada?' WHERE entry=2000005599; /* content: You don\'t mind me checking out your merchandise for signs of tampering, do you? */
UPDATE db_script_string SET content_loc7='No, Sargento.' WHERE entry=2000005600; /* content: No, Sergeant. */
UPDATE db_script_string SET content_loc7='Un momento, ¿qué es esto? ¡Me has mentido, Perelli!' WHERE entry=2000005601; /* content: Wait, what is this? You\'ve been holding out on me, Perelli! */
UPDATE db_script_string SET content_loc7='¡De qué habla, Sargento!' WHERE entry=2000005602; /* content: What are you talking about, Sergeant! */
UPDATE db_script_string SET content_loc7='Voy a confiscar este grano sospechoso, Perelli. Buscábamos comida manipulada y será mejor que no hagas tonterías mientras el príncipe Arthas comprueba esto.' WHERE entry=2000005603; /* content: I\'m confiscating this suspicious grain, Perelli. We were looking for signs of tampered food, and it would be in your best interest to stay put while Prince Arthas checks this out. */
UPDATE db_script_string SET content_loc7='Tienes que creerme, ¡soy inocente!' WHERE entry=2000005604; /* content: You have to believe me, I\'m innocent! */
UPDATE db_script_string SET content_loc7='Ya veremos, Perelli. Ya veremos.' WHERE entry=2000005605; /* content: We\'ll see about that, Perelli. We\'ll see about that. */
UPDATE db_script_string SET content_loc7='Martha, no me queda harina para hacer pan. ¿No tendrás algo de grano de ese envío que ha llegado hace poco?' WHERE entry=2000005606; /* content: Martha, I\'m out of flour for bread. You wouldn\'t happen to have any grain from that recent shipment, would you? */
UPDATE db_script_string SET content_loc7='Oh, hola, Jena. Claro que puedes tomar prestado algo de grano. Cógelo.' WHERE entry=2000005607; /* content: Oh hello, Jena. Of course you can borrow some grain. Help yourself. */
UPDATE db_script_string SET content_loc7='¡Gracias, Martha! Te debo una.' WHERE entry=2000005608; /* content: Thanks, Martha! I owe you one. */
UPDATE db_script_string SET content_loc7='¡Martha, a este grano le pasa algo raro! Algunos de los soldados del Príncipe lo estaban buscando. Voy a buscar a uno.' WHERE entry=2000005609; /* content: Martha, something\'s wrong with this grain! Some of the Prince\'s soldiers were looking for this. I\'m going to go look for one. */
UPDATE db_script_string SET content_loc7='Oh, cielos.' WHERE entry=2000005610; /* content: Oh, dear. */
UPDATE db_script_string SET content_loc7='Oh, no.' WHERE entry=2000005611; /* content: Oh, my. */
UPDATE db_script_string SET content_loc7='Parece que va a haber tormenta, Desaliñado...' WHERE entry=2000005612; /* content: Looks like a storm\'s coming in, Scruffy... */
UPDATE db_script_string SET content_loc7='%s comienza a gruñir...' WHERE entry=2000005613; /* content: %s begins to growl... */
UPDATE db_script_string SET content_loc7='¿Qué pasa, colega?' WHERE entry=2000005614; /* content: What\'s wrong, pal? */
UPDATE db_script_string SET content_loc7='¿Qué has encontrado, chico?' WHERE entry=2000005615; /* content: What did you find, boy? */
UPDATE db_script_string SET content_loc7='Esto no tiene buena pinta, Desaliñado. Quédate aquí y cuida de la casa, tengo que encontrar a un soldado.' WHERE entry=2000005616; /* content: This is no good, Scruffy. Stay here and guard the house, I need to go find a soldier. */
UPDATE db_script_string SET content_loc7='¡Este envío de grano no nos ha dado más que problemas!' WHERE entry=2000005617; /* content: This grain shipment has been nothing but trouble! */
UPDATE db_script_string SET content_loc7='¡Voy a perder mi bonificación de puntualidad por esto!' WHERE entry=2000005618; /* content: I\'m going to lose my on time bonus because of this! */
UPDATE db_script_string SET content_loc7='¡Ni siquiera debería estar aquí hoy!' WHERE entry=2000005619; /* content: I wasn\'t even supposed to be here today! */
UPDATE db_script_string SET content_loc7='Bueno, supongo que debería volver a cargar todo en la vagoneta.' WHERE entry=2000005620; /* content: Well, guess I should load everything back into the cart. */
UPDATE db_script_string SET content_loc7='¡Oh, vamos! Se me ha estropeado la vagoneta, mi caballo ha perdido una herradura ¡y ahora la mercancía se me estropea!' WHERE entry=2000005621; /* content: Oh, come on! My cart broke, my horse lost a shoe, and now the cargo goes bad! */
UPDATE db_script_string SET content_loc7='Tranquilo, pequeño draco.' WHERE entry=2000005623; /* content: Easy now, drakeling. */
UPDATE db_script_string SET content_loc7='Un espécimen fabuloso.' WHERE entry=2000005624; /* content: A wonderful specimen. */
UPDATE db_script_string SET content_loc7='Duerme ahora, joven...' WHERE entry=2000005625; /* content: Sleep now, young one.... */
UPDATE db_script_string SET content_loc7='Sí, este debería ayudar a que mis estudios avancen...' WHERE entry=2000005626; /* content: Yes, this one should advance my studies nicely.... */
UPDATE db_script_string SET content_loc7='Espera... ¿has oído eso? ¡Algo se aproxima desde el Oeste!' WHERE entry=2000005627; /* content: Wait... did you hear that? Something approaches from the west! */
UPDATE db_script_string SET content_loc7='¡Me he quedado sin flechas! Me temo que si viene alguno más tendrás que ocuparte tú de ellos.' WHERE entry=2000005628; /* content: I\'ve run out of arrows! I\'m afraid if any more come you will need to take them on by yourself my friend. */
UPDATE db_script_string SET content_loc7='¡Alabada sea Elune! No sé si habría acabado el día con vida si no fuera por ti, $gamigo:amiga;.' WHERE entry=2000005629; /* content: Praise Elune! I don\'t know if I could have survived the day without you, friend. */
UPDATE db_script_string SET content_loc7='Por favor, ve a hablar con la centinela Onaeya en la Atalaya de Maestra, en Vallefresno, procurará dar una recompensa adecuada a la valentía que has mostrado este día.' WHERE entry=2000005631; /* content: Please speak with Sentinel Onaeya at Maestra\'s Post in Ashenvale, she will see to it that you are properly rewarded for your bravery this day. */
UPDATE db_script_string SET content_loc7='¡Zafarrancho de combate! ¡Se acercan nagas!' WHERE entry=2000005632; /* content: All hands to battle stations! Naga incoming! */
UPDATE db_script_string SET content_loc7='Si conseguimos pararlos ahora, seguro que estaremos fuera de peligro.' WHERE entry=2000005633; /* content: If we can just hold them now, I am sure we will be in the clear. */
UPDATE db_script_string SET content_loc7='¡Preparaos para disparar a mi orden! Listos, apunten...' WHERE entry=2000005634; /* content: Prepare to fire upon my command! Ready, aim... */
UPDATE db_script_string SET content_loc7='¡Fuego a discreción!' WHERE entry=2000005635; /* content: FIRE AT WILL! */
UPDATE db_script_string SET content_loc7='¡Tiradores, en formación y a sus puestos!' WHERE entry=2000005636; /* content: Marksmen, form up and take your positions! */
UPDATE db_script_string SET content_loc7='¡Lo hemos logrado! ¡Hemos vencido a Tethyr!' WHERE entry=2000005637; /* content: We did it! We\'ve defeated Tethyr! */
UPDATE db_script_string SET content_loc7='¡Hemos vencido al monstruo marino!' WHERE entry=2000005638; /* content: We defeated the sea monster! */
UPDATE db_script_string SET content_loc7='Jinete álzate...' WHERE entry=2000005639; /* content: Horseman rise... */
UPDATE db_script_string SET content_loc7='Ya conociste la muerte...' WHERE entry=2000005641; /* content: You felt death once... */
UPDATE db_script_string SET content_loc7='¡Ahora mírala de cerca!' WHERE entry=2000005642; /* content: Now, know demise! */
UPDATE db_script_string SET content_loc7='%s ríe.' WHERE entry=2000005644; /* content: %s laughs. */
UPDATE db_script_string SET content_loc7='He... He muerto... Tanto dolor... en la oscuridad... ¿Puedes verme, $r?' WHERE entry=2000005645; /* content: I... I have died... I was in so much pain... engulfed in darkness... Can you see me, $r? */
UPDATE db_script_string SET content_loc7='¡A tus órdenes!' WHERE entry=2000005646; /* content: By your command! */
UPDATE db_script_string SET content_loc7='Espera aquí, $n. El robot espía hará salir a Lescovar lo antes posible. ¡Prepárate! Ataca solo después de escuchar su conversación.' WHERE entry=2000005647; /* content: Wait here, $N. Spybot will make Lescovar come out as soon as possible. Be ready! Attack only after you\'ve overheard their conversation. */
UPDATE db_script_string SET content_loc7='Que tengáis los dos un buen día. Me gustaría hablar con el señor Lescovar.' WHERE entry=2000005648; /* content: Good day to you both. I would speak to Lord Lescovar. */
UPDATE db_script_string SET content_loc7='Claro. Él te espera en la biblioteca.' WHERE entry=2000005649; /* content: Of course. He awaits you in the library. */
UPDATE db_script_string SET content_loc7='Gracias. Que la Luz os acompañe a los dos.' WHERE entry=2000005650; /* content: Thank you. The Light be with you both. */
UPDATE db_script_string SET content_loc7='Señor, su invitado ha llegado. Le está esperando.' WHERE entry=2000005651; /* content: Milord, your guest has arrived. He awaits your presence. */
UPDATE db_script_string SET content_loc7='Ah, muchas gracias. Te acompaño hasta la biblioteca mientras me ocupo de este asuntillo.' WHERE entry=2000005652; /* content: Ah, thank you kindly. I will leave you to the library while I tend to this small matter. */
UPDATE db_script_string SET content_loc7='He de meditar, dejadme a solas.' WHERE entry=2000005654; /* content: It\'s time for my meditation, leave me. */
UPDATE db_script_string SET content_loc7='¡Enseguida!' WHERE entry=2000005655; /* content: Yes, sir! */
UPDATE db_script_string SET content_loc7='%s espera hasta perder de vista a los guardias.' WHERE entry=2000005656; /* content: %s waits for the guards to be out of sight. */
UPDATE db_script_string SET content_loc7='Aquí estás. ¿Traes noticias de los Páramos de Poniente?' WHERE entry=2000005657; /* content: There you are. What news from Westfall? */
UPDATE db_script_string SET content_loc7='VanCleef manda decir que los planes siguen en pie. Sin embargo, ha oído rumores de que alguien anda husmeando por ahí.' WHERE entry=2000005658; /* content: VanCleef sends word that the plans are underway. But he\'s heard rumors about someone snooping about. */
UPDATE db_script_string SET content_loc7='Mmm, podría ser ese entrometido de Shaw. Veré lo que puedo descubrir. Y ahora, vete. Me pondré en contacto contigo pronto.' WHERE entry=2000005659; /* content: Hmm, it could be that meddler Shaw. I will see what I can discover. Be off with you. I\'ll contact you again soon. */
UPDATE db_script_string SET content_loc7='Muy bien. Entonces regresaré.' WHERE entry=2000005660; /* content: Very well. I will return then. */
UPDATE db_script_string SET content_loc7='¡Eso es! ¡Eso es lo que estabas esperando! ¡MÁTALOS!' WHERE entry=2000005661; /* content: That\'s it! That\'s what you were waiting for! KILL THEM! */
UPDATE db_script_string SET content_loc7='Lamentable mortal, ¡Onyxia no responde ante nadie!' WHERE entry=2000005662; /* content: Pitiful mortal, Onyxia answers to no one! */
UPDATE db_script_string SET content_loc7='Tu patético reto no ha pasado desapercibido. Disfrutaré jugando contigo antes de que mueras.' WHERE entry=2000005663; /* content: Your pathetic challenge has not gone unnoticed. I shall enjoy toying with you before you die. */
UPDATE db_script_string SET content_loc7='Quedan muy pocos en el mundo que puedan preparar el Resguardo de despertar, $n. Tenemos la fortuna de yo sea uno de ellos.' WHERE entry=2000005665; /* content: There are few left in the world who can prepare the Ward of Waking, $N. We are fortunate that I am among them. */
UPDATE db_script_string SET content_loc7='%s se arrodilla, llena el vial con agua de la Poza de la Luna' WHERE entry=2000005666; /* content: %s kneels, filling the vial with water from the moonwell. */
UPDATE db_script_string SET content_loc7='La poción está lista. Ahora solo queda esperar que Clintar nos perdone por interrumpir su estancia en el Sueño Esmeralda.' WHERE entry=2000005668; /* content: The potion is finished. I can only hope Clintar forgives us for interrupting his sojourn in the Emerald Dream. */
UPDATE db_script_string SET content_loc7='%s se pone de rodillas, pero parece estar en otro lado.' WHERE entry=2000005669; /* content: %s snaps up to a kneeling position, but seems as though he\'s somewhere else. */
UPDATE db_script_string SET content_loc7='%s se levanta y empieza a reconocer su entorno.' WHERE entry=2000005670; /* content: %s gets to his feet, beginning to recognize his surroundings. */
UPDATE db_script_string SET content_loc7='Acércate más, joven. No hay nada que temer en mí ni en $n.' WHERE entry=2000005671; /* content: Come closer, young one. There\'s nothing to fear from me or $n. */
UPDATE db_script_string SET content_loc7='Cuando hagas falta, oirás la llamada. Vuela hacia el este, a Skettis, y espera la señal de $n.' WHERE entry=2000005674; /* content: You will hear the call when you are needed. Fly east, to Skettis, and wait there for $N\'s signal. */
UPDATE db_script_string SET content_loc7='%s saluda al gavilán que ayudó a encontrar las Piedras del Cuervo.' WHERE entry=2000005675; /* content: %s greets the sparrowhawk that helped locate the raven stones. */
UPDATE db_script_string SET content_loc7='Tus heridas son graves, pero vivirás.' WHERE entry=2000005677; /* content: Your wounds are severe, but you will live. */
UPDATE db_script_string SET content_loc7='Ahora descansa, has luchado bien hoy.' WHERE entry=2000005678; /* content: Rest now, you have fought well today. */
UPDATE db_script_string SET content_loc7='¡Hombres!' WHERE entry=2000005681; /* content: Men! */
UPDATE db_script_string SET content_loc7='¡Mujeres!' WHERE entry=2000005682; /* content: Women! */
UPDATE db_script_string SET content_loc7='Eh, ¿has leído lo del botín en este cartel de Se Busca?' WHERE entry=2000005683; /* content: Hey, did you read the bounty on this wanted poster? */
UPDATE db_script_string SET content_loc7='¿Te vas a pasar todo el día hablando de ese cartel de Se Busca?' WHERE entry=2000005686; /* content: Is that wanted poster all that you\'re going to talk about today? */
UPDATE db_script_string SET content_loc7='Me vendría bien el dinero extra.' WHERE entry=2000005687; /* content: I could use the extra money. */
UPDATE db_script_string SET content_loc7='¡Dinos dónde se esconde tu líder!' WHERE entry=2000005689; /* content: Tell us!  Where does your leader hide? */
UPDATE db_script_string SET content_loc7='¡No te daremos nada hasta que hables, escoria!' WHERE entry=2000005690; /* content: You\'re not getting anything until you speak, scum! */
UPDATE db_script_string SET content_loc7='¡Madre!' WHERE entry=2000005691; /* content: Mother! */
UPDATE db_script_string SET content_loc7='Mordenaku... Tus aliados nos han salvado y han destruido al desdichado, Zuluhed. Siempre serán amigos del Ala Abisal.' WHERE entry=2000005692; /* content: Mordenaku... Your allies have saved us and destroyed the wretch, Zuluhed. Forever they will be a friend to the Netherwing. */
UPDATE db_script_string SET content_loc7='Con el tiempo, necesitaremos de nuevo la ayuda de los mortales. En este mundo, nuestro derecho de nacimiento está destruido. Serán las razas mortales las que nos ayuden a recuperar nuestro legítimo lugar en Draenor. Protégelos, Mordenaku...' WHERE entry=2000005693; /* content: In time, we will again require the assistance of the mortals. Across this world our birthright is shattered. It will be the mortal races that help us regain our rightful place on Draenor. Watch over them, Mordenaku... */
UPDATE db_script_string SET content_loc7='Estúpidos mortales. ¿Pensaron que no les quitaría la vida por sus transgresiones?' WHERE entry=2000005695; /* content: Foolish mortals. Did you think that I would not strike you down for your transgressions? */
UPDATE db_script_string SET content_loc7='Efectivamente, ha llegado la hora de acabar con esta farsa.' WHERE entry=2000005696; /* content: Indeed, the time has come to end this charade. */
UPDATE db_script_string SET content_loc7='¡Destrúyanlos! ¡Destrúyanlos a todos!' WHERE entry=2000005697; /* content: Destroy them! Destroy them all! */
UPDATE db_script_string SET content_loc7='Aparta si no quieres que te convierta en otro adorno para mi armadura. Tu líder y yo tenemos asuntos que atender...' WHERE entry=2000005698; /* content: Step aside lest I add you as another adornment to my armor. Your leader and I have matters to attend... */
UPDATE db_script_string SET content_loc7='%s sonríe.' WHERE entry=2000005699; /* content: %s smiles. */
UPDATE db_script_string SET content_loc7='Creí que lo verías como yo...' WHERE entry=2000005700; /* content: I thought you would see it my way... */
UPDATE db_script_string SET content_loc7='Soy Lady Sinestra' WHERE entry=2000005702; /* content: I am Lady Sinestra. */
UPDATE db_script_string SET content_loc7='No alargaré esto más de lo necesario Lady Siniestra. Hemos hablado largo y tendido, ahora dime qué quieres de los Faucedraco.' WHERE entry=2000005703; /* content: I will not drag this out any further than it needs, Lady Sinestra. You have bent my ear, now tell me what it is that you want from Dragonmaw. */
UPDATE db_script_string SET content_loc7='Sin duda habrás oído hablar de los fracasos de Nefarian en Azeroth... A pesar de que haya caído, los experimentos continúan. Mi maestro... continúa el trabajo que su progenie comenzó.' WHERE entry=2000005704; /* content: You have no doubt heard about Nefarian\'s failures on Azeroth... While he has fallen, the experiments continue. My master... He continues the work that his progeny began. */
UPDATE db_script_string SET content_loc7='¿El... el maestro? ¿Vive?' WHERE entry=2000005705; /* content: The... The master? He lives? */
UPDATE db_script_string SET content_loc7='%s asiente.' WHERE entry=2000005706; /* content: %s nods. */
UPDATE db_script_string SET content_loc7='Fuiste un teniente jefe, Mor\'ghor. Tu trabajo en Grim Batol no se olvidará fácilmente.' WHERE entry=2000005707; /* content: You were once a chief lieutenant, Mor\'ghor. Your work in Grim Batol is not easily forgotten. */
UPDATE db_script_string SET content_loc7='Ahora... Necesitamos los huevos que has recuperado. Los huevos de Ala Abisal. Al fin y al cabo, son un producto del maestro. Pagaremos el precio que pidas.' WHERE entry=2000005708; /* content: Now... We need the eggs that you recover. The Netherwing eggs. They are, after all, a product of the master. We will pay whatever price that you ask. */
UPDATE db_script_string SET content_loc7='%s tartamudea.' WHERE entry=2000005709; /* content: %s stammers. */
UPDATE db_script_string SET content_loc7='Ss... Sí, sí... por supuesto. Solo necesitamos los cristales y minerales de este lugar. Hay... necesitaremos monturas.' WHERE entry=2000005710; /* content: Ye... Yes, yes... Of course. We need only the crystals and ore from this place. There is... We will need mounts. */
UPDATE db_script_string SET content_loc7='El maestro estará muy satisfecho con estas noticias. El Vuelo Negro os proporcionará todo lo que pidáis. Podréis cabalgar en nuestros dracos si lo necesitáis.' WHERE entry=2000005711; /* content: The master will be most pleased with this news. The Black Dragonflight will provide you all that you ask. You will be allowed to ride upon the backs of our drakes as needed. */
UPDATE db_script_string SET content_loc7='Agradezco tu atención, Mor\'ghor. Ahora debo irme.' WHERE entry=2000005712; /* content: I thank you for your graciousness, Mor\'ghor. I must now take my leave. */
UPDATE db_script_string SET content_loc7='Así que mi sangre no fue pago suficiente, ¿eh? Bien, deja que recuperemos tu esencia arcana. Al fin y al cabo no le debo nada a Balthas.' WHERE entry=2000005713; /* content: So my blood was not a sufficient payment, eh? Fine, let us recover your arcane essence. After this, I owe Balthas nothing. */
UPDATE db_script_string SET content_loc7='%s coloca un fetiche en la pila ritual.' WHERE entry=2000005714; /* content: %s places a fetish at the ritual pile. */
UPDATE db_script_string SET content_loc7='El proceso es arduo. Primero debemos invocar a los acólitos de los elementos. Entonces debes destruir a estos acólitos para que mis secuaces puedan preparase.' WHERE entry=2000005715; /* content: The process is arduous. We must first summon forth acolytes of the elements. You must then destroy these acolytes so that my minions can make preparations. */
UPDATE db_script_string SET content_loc7='¡Bien hecho! Continuemos...' WHERE entry=2000005716; /* content: Well done!  Let\'s continue. */
UPDATE db_script_string SET content_loc7='¡Preparaos! Pronto llegará el acólito de agua.' WHERE entry=2000005717; /* content: Prepare yourself! The acolyte of water is soon to come... */
UPDATE db_script_string SET content_loc7='¡Ven, acólito de tierra!' WHERE entry=2000005718; /* content: Come forth, acolyte of earth! */
UPDATE db_script_string SET content_loc7='¡Muéstrate, fuego!' WHERE entry=2000005719; /* content: Fire, show yourself! */
UPDATE db_script_string SET content_loc7='¡Es tuyo mi Señor!' WHERE entry=2000005721; /* content: It is yours my Lord! */
UPDATE db_script_string SET content_loc7='Tengo el ojo puesto en ti, Faranell.' WHERE entry=2000005722; /* content: I\'ve got my eye on you, Faranell. */
UPDATE db_script_string SET content_loc7='Sin abominaciones que te protejan. Sin secretismos. Sin conspirar en la oscuridad.' WHERE entry=2000005723; /* content: No abominations protecting you. No secrecy. No plotting in the dark. */
UPDATE db_script_string SET content_loc7='Si escupes sin mi permiso este lugar quedará cerrado. Por la fuerza.' WHERE entry=2000005724; /* content: If you so much as spit without my permission, this place shuts down. Forcefully. */
UPDATE db_script_string SET content_loc7='Te aseguro, supervisor, que la Sociedad Real de Boticarios desea fervientemente compensar la trágica equivocación que acabó con tantas vidas.' WHERE entry=2000005725; /* content: I assure you, overseer, the Royal Apothecary Society dearly wishes to make up for the tragic misguidance which ended so many lives. */
UPDATE db_script_string SET content_loc7='No te causaremos problemas. Solo queremos continuar con nuestra investigación en paz.' WHERE entry=2000005726; /* content: We will cause you no trouble. We seek only to continue our research in peace. */
UPDATE db_script_string SET content_loc7='Ya veremos, Perelli. Ya veremos.' WHERE entry=2000005727; /* content: We\'ll see about that, Perelli. We\'ll see about that. */
UPDATE db_script_string SET content_loc7='¡Haz que ese loco podrido cierre la bocaza!' WHERE entry=2000005728; /* content: Get that rotten nut to shut his trap! */
UPDATE db_script_string SET content_loc7='%s localiza al orco...' WHERE entry=2000005730; /* content: %s picks the orc target... */
UPDATE db_script_string SET content_loc7='%s falla...' WHERE entry=2000005731; /* content: %s misses... */
UPDATE db_script_string SET content_loc7='%s afina la puntería...' WHERE entry=2000005732; /* content: %s takes a more careful aim... */
UPDATE db_script_string SET content_loc7='%s falla de nuevo...' WHERE entry=2000005733; /* content: %s misses again... */
UPDATE db_script_string SET content_loc7='%s apunta al orco por casualidad...' WHERE entry=2000005734; /* content: %s casually points at the orc target... */
UPDATE db_script_string SET content_loc7='%s acierta.' WHERE entry=2000005735; /* content: %s hits! */
UPDATE db_script_string SET content_loc7='%s apunta a las botellas...' WHERE entry=2000005736; /* content: %s takes aim at the bottles... */
UPDATE db_script_string SET content_loc7='%s falla...' WHERE entry=2000005737; /* content: %s misses... */
UPDATE db_script_string SET content_loc7='%s afina la puntería con las botellas...' WHERE entry=2000005738; /* content: %s takes aim at the bottles again... */
UPDATE db_script_string SET content_loc7='%s falla de nuevo...' WHERE entry=2000005739; /* content: %s misses again... */
UPDATE db_script_string SET content_loc7='%s dispara rápidamente al punto muerto del blanco central...' WHERE entry=2000005740; /* content: %s quickly shoots the middle target dead center... */
UPDATE db_script_string SET content_loc7='%s entonces apunta a las botellas...' WHERE entry=2000005741; /* content: %s then takes aim at the bottles... */
UPDATE db_script_string SET content_loc7='%s solo le da a un lado de la caja.' WHERE entry=2000005742; /* content: %s only hits the side of the box! */
UPDATE db_script_string SET content_loc7='%s dispara rápidamente al orco de trapo...' WHERE entry=2000005743; /* content: %s quickly shoots the orc dummy... */
UPDATE db_script_string SET content_loc7='%s se vuelve hacia el blanco de papel...' WHERE entry=2000005744; /* content: %s turns to the paper target... */
UPDATE db_script_string SET content_loc7='%s apunta sin desenfundar y dispara rápidamente a las botellas...' WHERE entry=2000005745; /* content: %s shoots from the hip at the bottles... */
UPDATE db_script_string SET content_loc7='¿James? James... No, tú no eres James, pero sé quién eres...' WHERE entry=2000005746; /* content: James? James... No, you\'re not James, but I know who you are... */
UPDATE db_script_string SET content_loc7='Eres $gel que:la que; siguió la pista de los soldados que nos hicieron esto.' WHERE entry=2000005747; /* content: You\'re the one who tracked down the brutes who did this to us. */
UPDATE db_script_string SET content_loc7='Intenté por todos medios decírselo a Jim... decírselo a alguien... quién estaba detrás de esto, pero no encontré la forma...' WHERE entry=2000005748; /* content: I tried so hard to tell Jim... to tell anyone... who was behind this, but I couldn\'t find a way... */
UPDATE db_script_string SET content_loc7='Gracias por ayudarnos y por ayudar a Jim. Si le ves, dile que el pequeño Jimmy y yo le queremos y que le estamos esperando.' WHERE entry=2000005749; /* content: Thank you for helping us and for helping Jim. If you see him, tell him little Jimmy and I love him and that we\'re waiting for him. */
UPDATE db_script_string SET content_loc7='Mami, ¿cuándo volveremos a ver a papi?' WHERE entry=2000005750; /* content: Mommy, when will we see Daddy again? */
UPDATE db_script_string SET content_loc7='No sé si volveremos a ver a papi, Jimmy, pero sé que te quiere y te echa mucho de menos.' WHERE entry=2000005751; /* content: I don\'t know when we\'ll see Daddy again, Jimmy, but I know he loves you and he misses you very much. */
UPDATE db_script_string SET content_loc7='Ahora probémoslo...' WHERE entry=2000005752; /* content: Now let\'s try it... */
UPDATE db_script_string SET content_loc7='Ahora, a empezar con las reparaciones.' WHERE entry=2000005754; /* content: Now to begin the repairs. */
UPDATE db_script_string SET content_loc7='Con un gruñido, %s se aleja del cartel de Se busca y vuelve a su sitio cerca del fuego.' WHERE entry=2000005755; /* content: Grunting, %s turns away from the wanted poster, returning to his warm spot by the fire. */
UPDATE db_script_string SET content_loc7='%s lee el cartel de Se busca.' WHERE entry=2000005756; /* content: %s begins to read the wanted poster. */
UPDATE db_script_string SET content_loc7='Adelante. Corta la cuerda.' WHERE entry=2000005757; /* content: I say we fire it. Cut the rope. */
UPDATE db_script_string SET content_loc7='Tú has cortado la cuerda. Así que tendrás el cuchillo.' WHERE entry=2000005758; /* content: You cut the rope. You have the knife. */
UPDATE db_script_string SET content_loc7='¡Argg! Cobarde.' WHERE entry=2000005759; /* content: Harumph! Coward. */
UPDATE db_script_string SET content_loc7='¿Sabes? Creo que podemos llegar al Bastión del Honor desde aquí.' WHERE entry=2000005760; /* content: You know, I think we can hit Honor Hold from here. */
UPDATE db_script_string SET content_loc7='Si el general Krakork se entera, nos comerá vivos. No.' WHERE entry=2000005761; /* content: If General Krakork found out, he\'d eat us alive. Don\'t. */
UPDATE db_script_string SET content_loc7='¡Ah! Podemos decirle que Hagash tropezó y lo disparó sin querer.' WHERE entry=2000005762; /* content: Ah! We can tell him Hagash stumbled into it and fired it accidentally! */
UPDATE db_script_string SET content_loc7='Estoy ciego, idiota, no sordo. Ni se te ocurra.' WHERE entry=2000005763; /* content: I\'m blind, you stupid sow-spawn, not deaf. Don\'t even think about it. */
UPDATE db_script_string SET content_loc7='Mientras recoge las botellas, %s suspira.' WHERE entry=2000005764; /* content: Putting the bottles away, %s sighs. */
UPDATE db_script_string SET content_loc7='¡Aquí están!' WHERE entry=2000005765; /* content: There they are! */
UPDATE db_script_string SET content_loc7='Gracias, amable $gamigo:amiga;. Me has liberado de esta prisión de agua de Cicatriz Espiral, pero todavía quedan muchos otros prisioneros en los alrededores. ¿Me ayudarás a liberarlos también?' WHERE entry=2000005766; /* content: Thank you, kind soul. You have freed me from the watery prison of Coilskar but many more are being held prisoner nearby. Will you assist me in freeing them as well? */
UPDATE db_script_string SET content_loc7='¡Ahora, voy a buscar a una arpía desprevenida!' WHERE entry=2000005768; /* content: Now to find an unsuspecting Harpy! */
UPDATE db_script_string SET content_loc7='¡Ahí hay una! ¡Por la Horda!' WHERE entry=2000005769; /* content: There\'s one! For the Horde! */
UPDATE db_script_string SET content_loc7='¡Toma eso, asquerosa arpía! ¡Síiiii!' WHERE entry=2000005770; /* content: Take that you mangy Harpy! Hyaa! */
UPDATE db_script_string SET content_loc7='¡No puedes escaparte de un guerrero de la Horda! ¡Toma esto! ¡Y esto!' WHERE entry=2000005771; /* content: You can\'t get away from a warrior of the Horde! Take that! And that! */
UPDATE db_script_string SET content_loc7='Mogg, entra. No es seguro que estés ahí fuera mientras tu padre no está.' WHERE entry=2000005774; /* content: Mogg, get inside. It is not safe out there while your father is gone. */
UPDATE db_script_string SET content_loc7='¡Ya habéis oído al ser de carne! ¡VAMOS!' WHERE entry=2000005780; /* content: You heard the fleshling! MOVE OUT! */
UPDATE db_script_string SET content_loc7='Avísame cuando estés $glisto:lista;, $n. Atacaremos a tu orden.' WHERE entry=2000005781; /* content: Tell me when you are ready, $N. We will attack on your command. */
UPDATE db_script_string SET content_loc7='¡Ahora o nunca, soldados! ¡Adelante! ¡Por K\'aresh! ¡Por el Protectorado!' WHERE entry=2000005782; /* content: It\'s now or never, soldiers! Let\'s do this! For K\'aresh! For the Protectorate! */
UPDATE db_script_string SET content_loc7='¡Ha llegado la hora de tu destrucción, Dimensius!' WHERE entry=2000005783; /* content: The time for your destruction has finally come, Dimensius! */
UPDATE db_script_string SET content_loc7='El Iluminado...' WHERE entry=2000005785; /* content: The Lightbringer... */
UPDATE db_script_string SET content_loc7='Lord Uther...' WHERE entry=2000005786; /* content: Lord Uther... */
UPDATE db_script_string SET content_loc7='Me alegra que vinieras, Uther.' WHERE entry=2000005787; /* content: Glad you could make it, Uther. */
UPDATE db_script_string SET content_loc7='Modera el tono, chico. Serás el Príncipe, pero yo sigo siendo tu superior como paladín.' WHERE entry=2000005788; /* content: Watch your tone with me, boy. You may be the prince, but I\'m still your superior as a paladin! */
UPDATE db_script_string SET content_loc7='Como si pudiera olvidarlo. Escucha, Uther, hay algo sobre la peste que debes saber…' WHERE entry=2000005789; /* content: As if I could forget. Listen, Uther, there\'s something about the plague you should know... */
UPDATE db_script_string SET content_loc7='Oh, no. Es demasiado tarde. ¡Están todos infectados! Ahora aparentan estar bien, ¡pero es cuestión de tiempo para que se conviertan en no-muertos!' WHERE entry=2000005790; /* content: Oh, no. We\'re too late. These people have all been infected! They may look fine now, but it\'s just a matter of time before they turn into the undead! */
UPDATE db_script_string SET content_loc7='¿Qué?' WHERE entry=2000005791; /* content: What? */
UPDATE db_script_string SET content_loc7='Hay que desinfectar toda esta ciudad.' WHERE entry=2000005792; /* content: This entire city must be purged. */
UPDATE db_script_string SET content_loc7='¡Ni lo pienses! ¡Tiene que haber alguna otra manera!' WHERE entry=2000005793; /* content: How can you even consider that? There\'s got to be some other way. */
UPDATE db_script_string SET content_loc7='¡Maldición, Uther! ¡Como tu futuro rey, te ordeno desinfectar esta ciudad!' WHERE entry=2000005794; /* content: Damn it, Uther! As your future king, I order you to purge this city! */
UPDATE db_script_string SET content_loc7='Aún no eres mi rey, muchacho. ¡Y tampoco acataría esa orden si lo fueras!' WHERE entry=2000005795; /* content: You are not my king yet, boy! Nor would I obey that command even if you were! */
UPDATE db_script_string SET content_loc7='Entonces debo estimar este acto una traición.' WHERE entry=2000005796; /* content: Then I must consider this an act of treason. */
UPDATE db_script_string SET content_loc7='¿Traición? ¿Te has vuelto loco, Arthas?' WHERE entry=2000005797; /* content: Treason? Have you lost your mind, Arthas? */
UPDATE db_script_string SET content_loc7='¿Eso crees? Lord Uther, por mi derecho de sucesión y la soberanía de mi corona, te relevo de tu mando y suspendo a tus paladines del servicio.' WHERE entry=2000005798; /* content: Have I? Lord Uther, by my right of succession and the sovereignty of my crown, I hereby relieve you of your command and suspend your paladins from service. */
UPDATE db_script_string SET content_loc7='¡Arthas! No puedes...' WHERE entry=2000005799; /* content: Arthas! You can\'t just-- */
UPDATE db_script_string SET content_loc7='¡Listo! ¡Quienes tengan la fuerza para salvar esta tierra, síganme! Los demás... ¡desaparezcan de mi vista!' WHERE entry=2000005800; /* content: It\'s done! Those of you who have the will to save this land, follow me! The rest of you... get out of my sight! */
UPDATE db_script_string SET content_loc7='Acabas de atravesar un umbral atroz, Arthas.' WHERE entry=2000005801; /* content: You\'ve just crossed a terrible threshold, Arthas. */
UPDATE db_script_string SET content_loc7='¿Jaina?' WHERE entry=2000005802; /* content: Jaina? */
UPDATE db_script_string SET content_loc7='Lo siento, Arthas. No puedo verte hacerlo.' WHERE entry=2000005803; /* content: I\'m sorry, Arthas. I can\'t watch you do this. */
UPDATE db_script_string SET content_loc7='Colóquense aquí, yo guiaré un pequeño grupo al interior de Stratholme para comenzar el sacrificio. ¡Tenemos que contener y purificar a los infectados por el bien de Lordaeron! ¡Nadie debe escapar o la peste se esparcirá por estas tierras!' WHERE entry=2000005804; /* content: Take position here, and I will lead a small force inside Stratholme to begin the culling. We must contain and purge the infected for the sake of all of Lordaeron! */
UPDATE db_script_string SET content_loc7='Todos los oficiales deben avisarme cuando sus escuadrones estén listos. Entraremos en Stratholme a mi orden.' WHERE entry=2000005805; /* content: All officers should check in with me when their squads are ready. We\'ll enter Stratholme on my order. */
UPDATE db_script_string SET content_loc7='Todos parecen estar listos. Recuerden, esta gente está infectada con la peste y morirá pronto. Precisamos desinfectar Stratholme para proteger de la Plaga al resto de Lordaeron. Vamos.' WHERE entry=2000005806; /* content: Everyone looks ready. Remember, these people are all infected with the plague and will die soon. We must purge Stratholme to protect the remainder of Lordaeron from the Scourge. Let\'s go. */
UPDATE db_script_string SET content_loc7='A todo el mundo que conozco parece estar pasándole algo realmente malo.' WHERE entry=2000005807; /* content: Everyone I know seems to be coming down with something really bad. */
UPDATE db_script_string SET content_loc7='¿Has hablado con alguien que no haya empezado a sentirse enfermo?' WHERE entry=2000005808; /* content: Have you talked to anyone who hasn\'t started getting sick? */
UPDATE db_script_string SET content_loc7='Tiene que ser algo del agua, te lo digo.' WHERE entry=2000005809; /* content: It must be something in the water, I tell you. */
UPDATE db_script_string SET content_loc7='Mi cama tiene muy buena pinta ahora.' WHERE entry=2000005810; /* content: My bed is looking pretty good right now. */
UPDATE db_script_string SET content_loc7='¡Príncipe Arthas, bendita sea la Luz! Muchos ciudadanos han comenzado a enfermar gravemente, ¿nos puedes ayudar?' WHERE entry=2000005811; /* content: Prince Arthas, may the light be praised! Many people in the town have begun to fall seriously ill, can you help us? */
UPDATE db_script_string SET content_loc7='Sólo puedo ayudarte con una muerte limpia.' WHERE entry=2000005812; /* content: I can only help you with a clean death. */
UPDATE db_script_string SET content_loc7='¿Qué? ¡No puede ser!' WHERE entry=2000005813; /* content: What? This can\'t be! */
UPDATE db_script_string SET content_loc7='Oh, no...' WHERE entry=2000005814; /* content: Oh, no... */
UPDATE db_script_string SET content_loc7='¿Has visto eso?' WHERE entry=2000005815; /* content: Did you see that? */
UPDATE db_script_string SET content_loc7='Eso fue sólo el comienzo.' WHERE entry=2000005816; /* content: That was just the beginning. */
UPDATE db_script_string SET content_loc7='Sí, este es el principio. Te he estado esperando, joven Príncipe. Soy Mal\'Ganis.' WHERE entry=2000005817; /* content: Yes, this is the beginning. I\'ve been waiting for you, young prince. I am Mal\'Ganis. */
UPDATE db_script_string SET content_loc7='Como verás, los tuyos ahora me pertenecen. Convertiré esta ciudad casa por casa, hasta que la llama de la vida se haya extinguido... eternamente.' WHERE entry=2000005818; /* content: As you can see, your people are now mine. I will now turn this city household by household, until the flame of life has been snuffed out... forever. */
UPDATE db_script_string SET content_loc7='¡No lo permitiré, Mal\'Ganis! ¡Mejor que esta gente muera a mano mía, que servir como tus esclavos en la muerte!' WHERE entry=2000005819; /* content: I won\'t allow it, Mal\'Ganis! Better that these people die by my hand than serve as your slaves in death! */
UPDATE db_script_string SET content_loc7='Mal\'Ganis enviará algunos de sus lacayos de la Plaga para detenernos. Quienes tengan el acero y la magia más poderosos vayan a destruirlos. Yo guiaré al resto de mis fuerzas para purificar Stratholme de los infectados.' WHERE entry=2000005820; /* content: Mal\'Ganis will send out some of his Scourge minions to interfere with us. Those of you with the strongest steel and magic shall go forth and destroy them. I will lead the rest of my forces in purging Stratholme of the infected. */
UPDATE db_script_string SET content_loc7='Te agradezco que me hayas salvado en esta línea del tiempo. Ahora, tengo que informar a mis superiores. Tienen que saber de inmediato lo que me ha pasado.' WHERE entry=2000005821; /* content: You have my thanks for saving my existence in this timeline. Now I must report back to my superiors. They must know immediately of what I just experienced. */
UPDATE db_script_string SET content_loc7='Tu poder nos fortalece a todos.' WHERE entry=2000005826; /* content: Your power strengthens us all. */
UPDATE db_script_string SET content_loc7='Nuestras vidas son tuyas, Caballero de sangre.' WHERE entry=2000005828; /* content: Our lives are yours, Blood Knight. */
UPDATE db_script_string SET content_loc7='Incluso la Luz no es rival para la voluntad indomable de nuestro pueblo.' WHERE entry=2000005829; /* content: Even the Light is no match for the indomitable will of our people. */
UPDATE db_script_string SET content_loc7='No pensaste en serio que volvería a hacer negocios contigo, ¿verdad? A veces, la mejor forma de volver a tener rentabilidad es saber cuándo recortar tus pérdidas.' WHERE entry=2000005833; /* content: You didn\'t really think I\'d do business with you again, did you? Sometimes, the best way to return to profitability is to know when to cut your losses. */
UPDATE db_script_string SET content_loc7='Mi espada Archeus me sirvió bien en vida, pero al abandonar mi espíritu esta infeliz existencia, ya no la necesito más.' WHERE entry=2000005834; /* content: My sword Archeus served me well in life, but as at last my spirit may pass from the unhappy existence, I need it no longer. */
UPDATE db_script_string SET content_loc7='Me aferraré al amor de mi hija y espero encontrar el perdón bajo la Luz por mis pecados.' WHERE entry=2000005835; /* content: I shall cling to the love of my daughter and hope that I will find forgiveness under the Light for my sins. */

SET NAMES 'latin1';
