UPDATE script_texts SET content_loc6='¡Por fin estoy fuera de esta maldita celda! Te acompañaré al patio...' WHERE entry=-1033003; /* content: Free from this wretched cell at last! Let me show you to the courtyard.... */
UPDATE script_texts SET content_loc6='Realmente eres valiente por querer hacer frente a los horrores que se ocultan tras esta puerta.' WHERE entry=-1033004; /* content: You are indeed courageous for wanting to brave the horrors that lie beyond this door. */
UPDATE script_texts SET content_loc6='¡Allá vamos!' WHERE entry=-1033005; /* content: There we go! */
