UPDATE script_texts SET content_loc6='He cambiado de parecer, mis leales sirvientes, no tenéis que traer al prisionero hasta mi estudio. Me ocuparé de él aquí y ahora.' WHERE entry=-1033009; /* content: I have changed my mind loyal servants, you do not need to bring the prisoner all the way to my study, I will deal with him here and now. */
