UPDATE script_texts SET content_loc6='¡Al fin! ¡Podemos despertar a Naralex! ¡Venid a ayudarme, valientes aventureros!' WHERE entry=-1043000; /* content: At last! Naralex can be awakened! Come aid me, brave adventurers! */
