UPDATE script_texts SET content_loc6='Estas cavernas fueron en su día un templo de promisión para el resurgimiento en Los Baldíos. Ahora se han convertido en los corredores de las pesadillas.' WHERE entry=-1043002; /* content: These caverns were once a temple of promise for regrowth in the Barrens. Now, they are the halls of nightmares. */
