UPDATE script_texts SET content_loc6='Con este círculo de fuego, lanzaré el hechizo para desterrar los espíritus de los Nobles del Colmillo fallecidos.' WHERE entry=-1043004; /* content: Within this circle of fire I must cast the spell to banish the spirits of the slain Fanglords. */
