UPDATE script_texts SET content_loc6='Tras este pasillo, Naralex yace en un sueño intermitente. Vamos a despertarle antes de que sea demasiado tarde.' WHERE entry=-1043006; /* content: Beyond this corridor, Naralex lies in fitful sleep. Let us go awaken him before it is too late. */
UPDATE script_texts SET content_loc6='¡Protegedme, almas valientes, mientras escarbo en el Sueño Esmeralda para rescatar a Naralex y acabar con esta corrupción!' WHERE entry=-1043007; /* content: Protect me brave souls as I delve into the Emerald Dream to rescue Naralex and put an end to this corruption! */
UPDATE script_texts SET content_loc6='%s comienza a realizar el ritual del despertar con Naralex.' WHERE entry=-1043008; /* content: %s begins to perform the awakening ritual on Naralex. */
