UPDATE script_texts SET content_loc6='¡Ju, ju! Al fin voy a salir de aquí. De todas maneras, no va a ser fácil. Mantened los ojos abiertos por si tenemos problemas.' WHERE entry=-1047000; /* content: Woo hoo! Finally getting out of here. It\'s going to be rough though. Keep your eyes peeled for trouble. */
