UPDATE script_texts SET content_loc6='¡En algún lugar de esta trinchera hay una trufa hojazul! ¡Es como oro esperando a ser extraído, créeme!' WHERE entry=-1047002; /* content: There\'s blueleaf tuber in this trench! It\'s like gold waiting to be mined I tell you! */
