UPDATE script_texts SET content_loc6='¡Por fin! Qué alegría cuando salgamos de este lugar.' WHERE entry=-1047007; /* content: Finally! I\'ll be glad to get out of this place. */
UPDATE script_texts SET content_loc6='Creo que descansaré un momento y cogeré un poco de aire antes de regresar a Trinquete. ¡Gracias por tu ayuda!' WHERE entry=-1047008; /* content: I think I\'ll rest a moment and catch my breath before heading back to Ratchet. Thanks for all the help! */
UPDATE script_texts SET content_loc6='¡$n viene en esta dirección a gran velocidad! ¡Coged las armas!' WHERE entry=-1047009; /* content: $N heading this way fast! To arms! */
