UPDATE script_texts SET content_loc6='Qué devastación... qué desastre espantoso...' WHERE entry=-1090002; /* content: Such devastation... what a horrible mess... */
UPDATE script_texts SET content_loc6='Casi hemos llegado...' WHERE entry=-1090003; /* content: It\'s quiet here.... */
UPDATE script_texts SET content_loc6='... hay demasiado silencio.' WHERE entry=-1090004; /* content: ...too quiet. */
UPDATE script_texts SET content_loc6='¡Mira! ¡Allí, en la pared del túnel!' WHERE entry=-1090005; /* content: Look! Over there at the tunnel wall! */
