UPDATE script_texts SET content_loc6='¡Nos disparan!' WHERE entry=-1090021; /* content: FIRE IN THE HOLE! */
UPDATE script_texts SET content_loc6='¡Excelente! Gracias a tu ayuda, mi gente tiene una oportunidad de recuperar su amada ciudad. ¡Tres hurras para ti!' WHERE entry=-1090022; /* content: Superb! Because of your help, my people stand a chance of re-taking our beloved city. Three cheers to you! */
