UPDATE script_texts SET content_loc6='¡Una muerte gloriosa!' WHERE entry=-1564090; /* content: A glorious kill! */
UPDATE script_texts SET content_loc6='¡Belesa menoor!' WHERE entry=-1564092; /* content: Belesa menoor! */
UPDATE script_texts SET content_loc6='El destino... espera.' WHERE entry=-1564095; /* content: Destiny... awaits. */
UPDATE script_texts SET content_loc6='Akama. Tu hipocresía no me sorprende. Debí acabar contigo y con tus malogrados hermanos hace tiempo.' WHERE entry=-1564097; /* content: Akama. Your duplicity is hardly surprising. I should have slaughtered you and your malformed brethren long ago. */
UPDATE script_texts SET content_loc6='¡Ha llegado la hora: es nuestro momento!' WHERE entry=-1564100; /* content: The time has come! The moment is at hand! */
UPDATE script_texts SET content_loc6='¡NO estáis preparados!' WHERE entry=-1564101; /* content: You are not prepared! */
UPDATE script_texts SET content_loc6='¿Esto es todo, mortales? ¿Es esta toda la furia que podéis reunir?' WHERE entry=-1564102; /* content: Is this it, mortals? Is this all the fury you can muster? */
UPDATE script_texts SET content_loc6='Su furia no es nada comparada con la mía, Illidan. Tenemos asuntos pendientes.' WHERE entry=-1564103; /* content: Their fury pales before mine, Illidan. We have some unsettled business between us. */
UPDATE script_texts SET content_loc6='Por fin concluye mi larga caza. ¡Hoy se hará justicia!' WHERE entry=-1564105; /* content: My long hunt is finally over. Today, Justice will be done! */
UPDATE script_texts SET content_loc6='¡Sentid el odio de diez mil años!' WHERE entry=-1564106; /* content: Feel the hatred of ten thousand years! */
UPDATE script_texts SET content_loc6='Se acabó. Has perdido.' WHERE entry=-1564107; /* content: It is finished. You are beaten. */
UPDATE script_texts SET content_loc6='Has ganado... Maiev. Pero la cazadora... no es NADA... sin su presa. TÚ... no eres... nada... sin mí.' WHERE entry=-1564108; /* content: You have won... Maiev. But the huntress... is nothing without the hunt. You... are nothing... without me. */
UPDATE script_texts SET content_loc6='Tiene razón. No siento nada...no soy...nada.' WHERE entry=-1564109; /* content: He\'s right. I feel nothing... I am... nothing. */
UPDATE script_texts SET content_loc6='Adiós, campeones.' WHERE entry=-1564110; /* content: Farewell, champions. */
UPDATE script_texts SET content_loc6='¡No sabéis nada sobre el PODER!' WHERE entry=-1564114; /* content: You know nothing of power! */
UPDATE script_texts SET content_loc6='¡Eso es por Naisha!' WHERE entry=-1564116; /* content: That is for Naisha! */
UPDATE script_texts SET content_loc6='¡Esta vez no habrá prisión para ti!' WHERE entry=-1564118; /* content: There shall be no prison for you this time! */
UPDATE script_texts SET content_loc6='Venid esbirros míos. ¡Dad a este traidor su merecido!' WHERE entry=-1564121; /* content: Come, my minions. Deal with this traitor as he deserves! */
UPDATE script_texts SET content_loc6='¡Esto es demasiado fácil!' WHERE entry=-1564124; /* content: This is too easy! */
UPDATE script_texts SET content_loc6='¡No me tocará una chusma como vosotros!' WHERE entry=-1564125; /* content: I will not be touched by rabble such as you! */
UPDATE script_texts SET content_loc6='¡Contemplad las LLAMAS de Azzinoth!' WHERE entry=-1564126; /* content: Behold the flames of Azzinoth! */
UPDATE script_texts SET content_loc6='¡Mirad a los ojos del Traidor!' WHERE entry=-1564127; /* content: Stare into the eyes of the Betrayer! */
UPDATE script_texts SET content_loc6='¡Observad el poder...del demonio interior!' WHERE entry=-1564128; /* content: Behold the power... of the demon within! */
UPDATE script_texts SET content_loc6='Tábido de la tribu Lengua de ceniza, tu líder habla.' WHERE entry=-1564130; /* content: Broken of the Ashtongue tribe, your leader speaks! */
UPDATE script_texts SET content_loc6='No puedo hacer esto solo...' WHERE entry=-1564132; /* content: I cannot do this alone... */
UPDATE script_texts SET content_loc6='No estás solo, Akama.' WHERE entry=-1564133; /* content: You are not alone, Akama. */
UPDATE script_texts SET content_loc6='¡Tu gente siempre estará contigo!' WHERE entry=-1564134; /* content: Your people will always be with you! */
UPDATE script_texts SET content_loc6='¡No derrotaréis a la mano de Gruul!' WHERE entry=-1565001; /* content: You will not defeat the hand of Gruul! */
UPDATE script_texts SET content_loc6='¡Buá! ¡No demuestra nada!' WHERE entry=-1565003; /* content: Pah! Does not prove anything! */
UPDATE script_texts SET content_loc6='¡Bien, ahora me toca a mí!' WHERE entry=-1565005; /* content: Good, now you fight me! */
UPDATE script_texts SET content_loc6='¡%s crece!' WHERE entry=-1565019; /* content: %s grows in size! */
UPDATE script_texts SET content_loc6='¡Los eh\'píritus del viento serán vueh\'tra perdición!' WHERE entry=-1568000; /* content: Spirits of da wind be your doom! */
UPDATE script_texts SET content_loc6='¡Ahora os quemaré!' WHERE entry=-1568001; /* content: I burn ya now! */
UPDATE script_texts SET content_loc6='¿Dónde está mi criador? ¡A por los huevos!' WHERE entry=-1568002; /* content: Where ma hatcha? Get to work on dem eggs! */
UPDATE script_texts SET content_loc6='Os moh\'traré la fuerza... con números.' WHERE entry=-1568003; /* content: I show you strength... in numbers. */
UPDATE script_texts SET content_loc6='¡Se os acabó el tiempo!' WHERE entry=-1568004; /* content: You done run outta time! */
UPDATE script_texts SET content_loc6='¡Todo ha terminado para ti!' WHERE entry=-1568005; /* content: It all be over now, mon! */
UPDATE script_texts SET content_loc6='¡Tazaga-choo!' WHERE entry=-1568006; /* content: Tazaga-choo! */
UPDATE script_texts SET content_loc6='¡Moveos guardias! ¡Es hora de matar!' WHERE entry=-1568010; /* content: Get da move on, guards! It be killin\' time! */
UPDATE script_texts SET content_loc6='¡Vamos, guardias! ¿Quién os da más miedo, ellos o yo?' WHERE entry=-1568011; /* content: Guards, go already! Who you more afraid of, dem... or me? */
UPDATE script_texts SET content_loc6='¡Cabalgad! ¡Cabalgad ahí fuera y traedme algunas cabezas!' WHERE entry=-1568012; /* content: Ride now! Ride out dere and bring me back some heads! */
UPDATE script_texts SET content_loc6='¡Eh\'toy empezando a perder la paciencia! Id: ¡haced que deseen no haber nacido!' WHERE entry=-1568013; /* content: I be losin\' me patience! Go on: make dem wish dey was never born! */
UPDATE script_texts SET content_loc6='¿Qué podría ser mejor que servir al eh\'píritu del oso eternamente? Acercaos... ¡Traedme vueh\'tras almas!' WHERE entry=-1568014; /* content: What could be better than servin\' da bear spirit for eternity? Come closer now. Bring your souls to me! */
UPDATE script_texts SET content_loc6='¡Moriréis pronto!' WHERE entry=-1568016; /* content: You be dead soon enough! */
UPDATE script_texts SET content_loc6='¡Yo traigo el dolor!' WHERE entry=-1568017; /* content: I bring da pain! */
UPDATE script_texts SET content_loc6='¡Si llamáis a la beh\'tia, vais a recibir más de lo que eh\'peráis!' WHERE entry=-1568018; /* content: You call on da beast, you gonna get more dan you bargain for! */
UPDATE script_texts SET content_loc6='¡Los Amani vuelven a eh\'tar al mando!' WHERE entry=-1568022; /* content: Da Amani gonna rule again! */
UPDATE script_texts SET content_loc6='Eh\'taré... eh\'perando en el más allá...' WHERE entry=-1568023; /* content: I... be waitin\' on da udda side.... */
UPDATE script_texts SET content_loc6='¡Yo soy el depredador! Vosotros la presa...' WHERE entry=-1568026; /* content: I be da predator! You da prey... */
UPDATE script_texts SET content_loc6='¡Alimentaos, hermanos míos!' WHERE entry=-1568027; /* content: Feed, me bruddahs! */
UPDATE script_texts SET content_loc6='¡No tienes nada!' WHERE entry=-1568030; /* content: Ya got nothin\'! */
UPDATE script_texts SET content_loc6='¡Basta ya de tanto lloriqueo!' WHERE entry=-1568031; /* content: Stop your cryin\'! */
UPDATE script_texts SET content_loc6='¡No podéis... matar... mi espíritu!' WHERE entry=-1568032; /* content: You can\'t... kill... me spirit! */
UPDATE script_texts SET content_loc6='¡Aparece una tormenta eléctrica!' WHERE entry=-1568033; /* content: An Electrical Storm Appears! */
UPDATE script_texts SET content_loc6='¡Arrodillaos... ante la garra y el colmillo!' WHERE entry=-1568034; /* content: Get on ya knees and bow.... to da fang and claw! */
UPDATE script_texts SET content_loc6='Lucho con libertad de eh\'píritu...' WHERE entry=-1568035; /* content: I fight wit\' untamed spirit.... */
UPDATE script_texts SET content_loc6='¡Eh\'píritu, vuelve a mí!' WHERE entry=-1568036; /* content: Spirit, come back to me! */
UPDATE script_texts SET content_loc6='¡Os voy a hacer picadillo!' WHERE entry=-1568037; /* content: Me gonna carve ya now! */
UPDATE script_texts SET content_loc6='¡Vais a acabar hechos pedazos!' WHERE entry=-1568038; /* content: You gonna leave in pieces! */
UPDATE script_texts SET content_loc6='Chaga... choka\'jinn.' WHERE entry=-1568042; /* content: Chaga... choka\'jinn. */
UPDATE script_texts SET content_loc6='Estoy esperando, forasteros. ¡Vuestras muertes me harán más fuerte!' WHERE entry=-1568044; /* content: I be waitin, strangers. Your deaths gonna make me stronger! */
UPDATE script_texts SET content_loc6='¡Esta es una pesadilla de la que no despertarás!' WHERE entry=-1568047; /* content: Dis a nightmare ya don\' wake up from! */
UPDATE script_texts SET content_loc6='¡Azzaga choogo zinn!' WHERE entry=-1568048; /* content: Azzaga choogo zinn! */
UPDATE script_texts SET content_loc6='¡Vuestra voluntad me pertenece ahora!' WHERE entry=-1568049; /* content: Your will belong ta me now! */
UPDATE script_texts SET content_loc6='¡Vuestra alma sangrará!' WHERE entry=-1568051; /* content: Your soul gonna bleed! */
UPDATE script_texts SET content_loc6='No va a cambiar nada.' WHERE entry=-1568052; /* content: It not gonna make no difference. */
UPDATE script_texts SET content_loc6='Vuestra muerte será más dolorosa.' WHERE entry=-1568053; /* content: You gonna die worse dan him. */
UPDATE script_texts SET content_loc6='No me molesta.' WHERE entry=-1568054; /* content: Dat no bodda me. */
UPDATE script_texts SET content_loc6='Oh. Tengo algunos trucos nuevos... como mi hermano oso...' WHERE entry=-1568058; /* content: Got me some new tricks... like me brudda bear.... */
UPDATE script_texts SET content_loc6='¡No hay forma de esconderse del águila!' WHERE entry=-1568059; /* content: Dere be no hidin\' from da eagle! */
UPDATE script_texts SET content_loc6='¡Os presento a mis nuevos hermanos: colmillo y garra!' WHERE entry=-1568060; /* content: Let me introduce you to me new bruddas: fang and claw! */
UPDATE script_texts SET content_loc6='¡No hace falta mirar al cielo pa\' ver al dracohalcón!' WHERE entry=-1568061; /* content: Ya don\' have to look to da sky to see da dragonhawk! */
UPDATE script_texts SET content_loc6='¡El fuego mata igual de rápido!' WHERE entry=-1568062; /* content: Fire kill you just as quick! */
UPDATE script_texts SET content_loc6='¡Los eh\'píritus eh\'tán de celebración hoy! ¡Comenzad con las ceremonias, sacrificad a los prisioneros... haced sitio pa\' nueh\'tros nuevos invitados!' WHERE entry=-1568068; /* content: Da spirits gonna feast today! Begin da ceremonies, sacrifice da prisoners... make room for our new guests! */
UPDATE script_texts SET content_loc6='¡Elegid, intrusos! Mis sacerdotes estará encantados de buscaros alojamiento...' WHERE entry=-1568069; /* content: Take your pick, trespassers! Any of ma priests be happy to accommodate ya. */
UPDATE script_texts SET content_loc6='No seáis tímidos. Han venido miles antes que vosotros. No eh\'taréis solos en el sacrificio.' WHERE entry=-1568070; /* content: Don\'t be shy. Thousands have come before you. Ya not be alone in your service. */
UPDATE script_texts SET content_loc6='Vais a fracasar, forah\'teros. ¡Muchos lo intentaron antes que vosotros, pero solo nos volvieron más fueh\'tes!' WHERE entry=-1568071; /* content: Ya gonna fail, strangers. Many try before you, but dey only make us stronger! */
UPDATE script_texts SET content_loc6='Vuestros esfuerzos han sido en vano, intrusos. Los rituales casi se han completado.' WHERE entry=-1568072; /* content: Your efforts was in vain, trespassers. The rituals nearly be complete. */
UPDATE script_texts SET content_loc6='Pronto las jaulas estarán vacías, los sacrificios finalizados y vosotros ocuparéis sus puestos.' WHERE entry=-1568073; /* content: Soon da cages gonna be empty, da sacrifices be complete, and you gonna take dere places. */
UPDATE script_texts SET content_loc6='Se os acaba el tiempo, forah\'teros. Pronto os uniréis a las almas de los que no lograh\'teis salvar.' WHERE entry=-1568074; /* content: Time be running low, strangers. Soon you gonna join da souls of dem ya failed to save. */
UPDATE script_texts SET content_loc6='¡Daos prisa, sacerdotes míos! ¡Los rituales no deben interrumpirse!' WHERE entry=-1568075; /* content: Make haste, ma priests! Da rituals must not be interrupted! */
UPDATE script_texts SET content_loc6='Buen intento... pero ahora os vais a unir a las almas de los que ya han caído.' WHERE entry=-1568076; /* content: Ya make a good try... but now you gonna join da ones who already fall. */
UPDATE script_texts SET content_loc6='¡Mirad! Cada ofrenda fortalecerá nuestros lazos con el mundo de los espíritus. ¡Pronto seremos imparables!' WHERE entry=-1568078; /* content: Watch now. Every offering gonna strengthen our ties to da spirit world. Soon, we gonna be unstoppable! */
UPDATE script_texts SET content_loc6='Según mis cálculos si varios de nosotros golpeamos el gong a la vez, el sello de estas puertas se romperá y podremos entrar.' WHERE entry=-1568080; /* content: According to my calculations, if enough of us bang the gong at once the seal on these doors will break and we can enter. */
UPDATE script_texts SET content_loc6='¡%s absorbe la esencia del espíritu de oso!' WHERE entry=-1568082; /* content: %s absorbs the essence of the bear spirit! */
UPDATE script_texts SET content_loc6='¡%s absorbe la esencia del espíritu de águila!' WHERE entry=-1568083; /* content: %s absorbs the essence of the eagle spirit! */
UPDATE script_texts SET content_loc6='¡%s absorbe la esencia del espíritu de lince!' WHERE entry=-1568084; /* content: %s absorbs the essence of the lynx spirit! */
UPDATE script_texts SET content_loc6='¡%s absorbe la esencia del espíritu de dracohalcón!' WHERE entry=-1568085; /* content: %s absorbs the essence of the dragonhawk spirit! */
UPDATE script_texts SET content_loc6='¡Vuestra sangre es mía!' WHERE entry=-1574000; /* content: Your blood is mine! */
UPDATE script_texts SET content_loc6='No tan rápido.' WHERE entry=-1574001; /* content: Not so fast. */
UPDATE script_texts SET content_loc6='Me reúno... con la noche.' WHERE entry=-1574004; /* content: I join... the night. */
UPDATE script_texts SET content_loc6='¡Me pintaré el rostro con vuestra sangre!' WHERE entry=-1574005; /* content: I\'ll paint my face with your blood! */
UPDATE script_texts SET content_loc6='Mi vida por el... ¡dios de la muerte!' WHERE entry=-1574007; /* content: My life for the... death god! */
UPDATE script_texts SET content_loc6='¡No! Puedo hacerlo... ¡mejor! Puedo...' WHERE entry=-1574008; /* content: No! I can do... better! I can... */
UPDATE script_texts SET content_loc6='¡Mjul orm agn gjor!' WHERE entry=-1574009; /* content: Mjul orm agn gjor! */
UPDATE script_texts SET content_loc6='¡Soy un guerrero nato!' WHERE entry=-1574010; /* content: I am a warrior born! */
UPDATE script_texts SET content_loc6='¡Dalronn! ¡A ver si puedes reunir el valor para unirte a mi ataque!' WHERE entry=-1574011; /* content: Dalronn! See if you can muster the nerve to join my attack! */
UPDATE script_texts SET content_loc6='No he... terminado... aún.' WHERE entry=-1574012; /* content: Not... over... yet. */
UPDATE script_texts SET content_loc6='Una muerte de un guerrero.' WHERE entry=-1574013; /* content: A warrior\'s death. */
UPDATE script_texts SET content_loc6='¡No te pongas a valorar la situación, imbécil! ¡Solo pelea!' WHERE entry=-1574016; /* content: By all means, don\'t assess the situation, you halfwit! Just jump into the fray! */
UPDATE script_texts SET content_loc6='Hasta... la… vista…' WHERE entry=-1574017; /* content: See... you... soon. */
UPDATE script_texts SET content_loc6='No hay... mayor... gloria.' WHERE entry=-1574018; /* content: There\'s no... greater... glory. */
UPDATE script_texts SET content_loc6='Puede que aún me sirvas.' WHERE entry=-1574019; /* content: You may serve me yet. */
UPDATE script_texts SET content_loc6='Skarvald, ¡babosa incompetente! ¡Vuelve y haz algo útil!' WHERE entry=-1574020; /* content: Skarvald, you incompetent slug! Return and make yourself useful! */
UPDATE script_texts SET content_loc6='¡%s ruge!' WHERE entry=-1574022; /* content: %s roars! */
UPDATE script_texts SET content_loc6='¡Mi señor! He hecho lo que pedisteis, ¡y ahora suplico vuestra bendición!' WHERE entry=-1575000; /* content: My liege! I have done as you asked, and now beseech you for your blessing! */
UPDATE script_texts SET content_loc6='Esta sensación... va más allá de mi imaginación. Estoy a vuestra disposición, mi rey.' WHERE entry=-1575002; /* content: The sensation is... beyond my imagining. I am yours to command, my king. */
UPDATE script_texts SET content_loc6='Tu primera prueba te espera. Acaba con nuestros invitados indeseados.' WHERE entry=-1575003; /* content: Your first test awaits you. Destroy our uninvited guests. */
UPDATE script_texts SET content_loc6='¡Será un placer matarlos en vuestro nombre! ¡Acercaos, enemigos de la Plaga! ¡Os enseñaré el poder del Rey Exánime!' WHERE entry=-1575004; /* content: I will be happy to slaughter them in your name! Come, enemies of the Scourge! I will show you the might of the Lich King! */
UPDATE script_texts SET content_loc6='¡Someteré vuestra alma!' WHERE entry=-1575005; /* content: I will vanquish your soul! */
UPDATE script_texts SET content_loc6='¡Qué estupidez desafiar el poder del Rey Exánime!' WHERE entry=-1575006; /* content: You were a fool to challenge the power of the Lich King! */
UPDATE script_texts SET content_loc6='Tu fin es inevitable.' WHERE entry=-1575011; /* content: Your end is inevitable. */
UPDATE script_texts SET content_loc6='¡Yor-guul mak!' WHERE entry=-1575012; /* content: Yor-guul mak! */
UPDATE script_texts SET content_loc6='¿Tus últimas palabras?' WHERE entry=-1575013; /* content: Any last words? */
UPDATE script_texts SET content_loc6='¡Nooo! No he llegado tan lejos... para...' WHERE entry=-1575014; /* content: Nooo! I did not come this far... to... */
UPDATE script_texts SET content_loc6='¿Pero qué… dónde? ¡Acabaré con vosotros!' WHERE entry=-1575015; /* content: What this place? I will destroy you! */
UPDATE script_texts SET content_loc6='¡Juego de niños!' WHERE entry=-1575017; /* content: An easy task! */
UPDATE script_texts SET content_loc6='Ya no eres tan valiente, ¿a que no?' WHERE entry=-1575026; /* content: Not so brash now, are you? */
UPDATE script_texts SET content_loc6='¡Bastardos malnacidos! ¡Vuestros cadáveres serán un buen bocado para mis nuevos dracos!' WHERE entry=-1575029; /* content: You motherless knaves! Your corpses will make fine morsels for my new drake! */
UPDATE script_texts SET content_loc6='¡Bjorn de la Tormenta Negra! ¡Hónrame ahora con tu presencia!' WHERE entry=-1575032; /* content: Bjorn of the Black Storm! Honor me now with your presence! */
UPDATE script_texts SET content_loc6='¿Qué... me aguarda... ahora?' WHERE entry=-1575040; /* content: What... awaits me... now? */
UPDATE script_texts SET content_loc6='%s respira hondo.' WHERE entry=-1575041; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc6='Tengo para dar y tomar.' WHERE entry=-1576002; /* content: There\'s plenty of me to go around. */
UPDATE script_texts SET content_loc6='Ahora, ¡a terminar el trabajo!' WHERE entry=-1576003; /* content: Now to finish the job! */
UPDATE script_texts SET content_loc6='Maldita la... suerte.' WHERE entry=-1576005; /* content: Damn the... luck. */
UPDATE script_texts SET content_loc6='El caos se avecina.' WHERE entry=-1576006; /* content: Chaos beckons. */
UPDATE script_texts SET content_loc6='La realidad... desvelada.' WHERE entry=-1576007; /* content: Reality... unwoven. */
UPDATE script_texts SET content_loc6='Indestructible.' WHERE entry=-1576008; /* content: Indestructible. */
UPDATE script_texts SET content_loc6='Claro.' WHERE entry=-1576010; /* content: Of course. */
UPDATE script_texts SET content_loc6='¡Noo!' WHERE entry=-1576011; /* content: Noo! */
UPDATE script_texts SET content_loc6='¡Aaaagh!' WHERE entry=-1576015; /* content: Aaggh! */
UPDATE script_texts SET content_loc6='¿Conservar? ¿Para qué? No hay verdad en ello. No, no, no... ¡Solo en lo que se quita! Ahora lo veo claro.' WHERE entry=-1576016; /* content: Preserve? Why? There\'s no truth in it. No no no... only in the taking! I see that now! */
UPDATE script_texts SET content_loc6='Quedaos. Disfrutad de vuestros últimos momentos.' WHERE entry=-1576017; /* content: Stay. Enjoy your final moments. */
UPDATE script_texts SET content_loc6='¡Dadle fin! ¡Dadle fin! ¡Matadme o juro por la reina de los dragones que nunca volveréis a ver la luz del día!' WHERE entry=-1576018; /* content: Finish it! Finish it! Kill me, or I swear by the Dragonqueen you\'ll never see daylight again! */
UPDATE script_texts SET content_loc6='¡Ha llegado el momento de la verdad!' WHERE entry=-1576019; /* content: Now we\'ve come to the truth! */
UPDATE script_texts SET content_loc6='Reina de los dragones... Vinculadora de vida... conserva... me' WHERE entry=-1576020; /* content: Dragonqueen... Life-Binder... preserve... me. */
UPDATE script_texts SET content_loc6='¡%s abre una falla caótica!' WHERE entry=-1576021; /* content: %s opens a Chaotic Rift! */
UPDATE script_texts SET content_loc6='Es evidente que mis mascotas han fracasado. Quizá sea necesaria otra demostración.' WHERE entry=-1578001; /* content: Clearly my pets failed. Perhaps another demonstration is in order. */
UPDATE script_texts SET content_loc6='Seguís luchando. Seguís aferrándoos a principios erróneos. Si sobrevivís, me encontraréis en el anillo central.' WHERE entry=-1578002; /* content: Still you fight. Still you cling to misguided principles. If you survive, you\'ll find me in the center ring. */
UPDATE script_texts SET content_loc6='¡Pobres necios ciegos!' WHERE entry=-1578003; /* content: Poor blind fools! */
UPDATE script_texts SET content_loc6='Una muestra... ¡una pequeña muestra del poder del Tejehechizos!' WHERE entry=-1578004; /* content: A taste... just a small taste... of the Spell-Weaver\'s power! */
UPDATE script_texts SET content_loc6='Tanta energía inestable... ¡pero vale la pena correr el riesgo de destruiros!' WHERE entry=-1578005; /* content: So much unstable energy... but worth the risk to destroy you! */
UPDATE script_texts SET content_loc6='¡Si pudieras entenderlo!' WHERE entry=-1578006; /* content: If only you understood! */
UPDATE script_texts SET content_loc6='Ahora lo ves, ¿verdad?' WHERE entry=-1578007; /* content: Now do you see? Do you?! */
UPDATE script_texts SET content_loc6='Desafortunado, pero necesario.' WHERE entry=-1578008; /* content: Unfortunate, but necessary. */
UPDATE script_texts SET content_loc6='¡Simplones! No podéis comprender las fuerzas que habéis puesto en marcha. ¡El conducto de líneas Ley no será interrumpido! ¡Vuestra derrota será total!' WHERE entry=-1578010; /* content: Simpletons! You cannot comprehend the forces you have set in motion. The ley line conduit will not be disrupted! Your defeat shall be absolute! */
UPDATE script_texts SET content_loc6='¡Entrometidos descarados, estáis fuera de vuestro elemento! ¡Os enterraré!' WHERE entry=-1578011; /* content: You brash interlopers are out of your element! I will ground you! */
UPDATE script_texts SET content_loc6='¡Es absurdo intentar extinguir tu fuego!' WHERE entry=-1578013; /* content: It is trivial to extinguish your fire! */
UPDATE script_texts SET content_loc6='¡La magia de la naturaleza no os ayudará ahora!' WHERE entry=-1578014; /* content: No magic of nature will help you now! */
UPDATE script_texts SET content_loc6='Tanta insolencia... tanta arrogancia... ¡deben ser CASTIGADAS!' WHERE entry=-1578015; /* content: Such insolence... such arrogance... must be PUNISHED! */
UPDATE script_texts SET content_loc6='¡REGRESAD a la tierra!' WHERE entry=-1578017; /* content: Back to the earth with you! */
UPDATE script_texts SET content_loc6='¡Disfruta de la caída!' WHERE entry=-1578018; /* content: Enjoy the fall! */
UPDATE script_texts SET content_loc6='¡No habrá piedad!' WHERE entry=-1578020; /* content: There will be no mercy! */
UPDATE script_texts SET content_loc6='¡Reventadlos! ¡Acabad con ellos!' WHERE entry=-1578021; /* content: Blast them! Destroy them! */
UPDATE script_texts SET content_loc6='¡No toméis prisioneros! ¡Atacad!' WHERE entry=-1578022; /* content: Take no prisoners! Attack! */
UPDATE script_texts SET content_loc6='¡Atacad ahora! ¡Borradlos del mapa!' WHERE entry=-1578023; /* content: Strike now! Obliterate them! */
UPDATE script_texts SET content_loc6='¡Cuando %s pasa al plano astral se forman anomalías!' WHERE entry=-1578024; /* content: Anomalies form as %s shifts into the Astral Plane! */
