UPDATE script_texts SET content_loc6='¡Estabas avisado!' WHERE entry=-1578026; /* content: You were warned! */
UPDATE script_texts SET content_loc6='¡El Oculus es nuestro!' WHERE entry=-1578027; /* content: The Oculus is ours! */
UPDATE script_texts SET content_loc6='¡Son demasiado... fuertes! Hemos subestimado su... fortaleza.' WHERE entry=-1578028; /* content: They are... too strong! Underestimated their... fortitude. */
UPDATE script_texts SET content_loc6='¡%s llama a un capitán del anillo azur!' WHERE entry=-1578029; /* content: %s calls an Azure Ring Captain! */
UPDATE script_texts SET content_loc6='¡%s se aleja volando!' WHERE entry=-1578030; /* content: %s flies away. */
UPDATE script_texts SET content_loc6='¡Os purgaré!' WHERE entry=-1580001; /* content: I will purge you! */
UPDATE script_texts SET content_loc6='¡Vuestro dolor no ha hecho más que comenzar!' WHERE entry=-1580002; /* content: Your pain has only begun! */
UPDATE script_texts SET content_loc6='¡En el nombre de Kil\'jaeden!' WHERE entry=-1580003; /* content: In the name of Kil\'jaeden! */
UPDATE script_texts SET content_loc6='¡Estabas avisado!' WHERE entry=-1580004; /* content: You were warned! */
UPDATE script_texts SET content_loc6='¡Mi despertar se ha completado! ¡Moriréis!' WHERE entry=-1580005; /* content: My awakening is complete! You shall all perish! */
UPDATE script_texts SET content_loc6='¡Rápido! ¡No queda mucho tiempo!' WHERE entry=-1580008; /* content: Hurry! There is not much of me left! */
UPDATE script_texts SET content_loc6='Estaré en deuda con vosotros eternamente. Cuando hayamos triunfado sobre Kil\'jaeden, este mundo también lo estará.' WHERE entry=-1580009; /* content: I am forever in your debt. Once we have triumphed over Kil\'jaeden, this entire world will be in your debt as well. */
UPDATE script_texts SET content_loc6='¡Me deleito en vuestra miseria!' WHERE entry=-1580012; /* content: Your misery is my delight! */
UPDATE script_texts SET content_loc6='¡Miraré mientras sangráis!' WHERE entry=-1580013; /* content: I will watch you bleed! */
UPDATE script_texts SET content_loc6='¿No te has enterado? ¡Yo siempre gano!' WHERE entry=-1580015; /* content: Haven\'t you heard? I always win! */
UPDATE script_texts SET content_loc6='¡Basta ya de juegos!' WHERE entry=-1580016; /* content: I have toyed with you long enough! */
UPDATE script_texts SET content_loc6='¡Endebles lagartijas! ¡La muerte es la única respuesta que encontraréis aquí!' WHERE entry=-1580017; /* content: Puny lizard! Death is the only answer you\'ll find here! */
UPDATE script_texts SET content_loc6='¡Os aplastaré!' WHERE entry=-1580019; /* content: I will crush you! */
UPDATE script_texts SET content_loc6='¡Ha sido divertido, pero sigo esperando un auténtico reto!' WHERE entry=-1580020; /* content: That was fun, but I still await a true challenge! */
UPDATE script_texts SET content_loc6='¡Muere, insecto!' WHERE entry=-1580023; /* content: Perish, insect! */
UPDATE script_texts SET content_loc6='¡Eres carne!' WHERE entry=-1580024; /* content: You are meat! */
UPDATE script_texts SET content_loc6='¡Demasiado fácil!' WHERE entry=-1580025; /* content: Too easy! */
UPDATE script_texts SET content_loc6='¡Pelead contra mí!' WHERE entry=-1580026; /* content: Bring the fight to me! */
UPDATE script_texts SET content_loc6='¡Otro día, otra gloriosa batalla!' WHERE entry=-1580027; /* content: Another day, another glorious battle! */
UPDATE script_texts SET content_loc6='¡Vivo para esto!' WHERE entry=-1580028; /* content: I live for this! */
UPDATE script_texts SET content_loc6='¡Esperad, amigos! ¡Tenemos información que obtener antes de que este demonio se enfrente a su destino!' WHERE entry=-1580031; /* content: Hold, friends! There is information to be had before this devil meets his fate! */
UPDATE script_texts SET content_loc6='¿Dónde está Anveena, demonio? ¿Qué ha sido de Kalec?' WHERE entry=-1580032; /* content: Where is Anveena, demon? What has become of Kalec? */
UPDATE script_texts SET content_loc6='¡Me dirás dónde están!' WHERE entry=-1580033; /* content: You will tell me where they are! */
UPDATE script_texts SET content_loc6='¡Gloria a Kil\'jaeden! ¡Muerte a sus oponentes!' WHERE entry=-1580036; /* content: Glory to Kil\'jaeden! Death to all who oppose! */
UPDATE script_texts SET content_loc6='¡Mato por el maestro!' WHERE entry=-1580037; /* content: I kill for the master! */
UPDATE script_texts SET content_loc6='Caos...' WHERE entry=-1580049; /* content: Chaos... */
UPDATE script_texts SET content_loc6='¡Sombras para ayudar al fuego!' WHERE entry=-1580052; /* content: Shadow to the aid of fire! */
UPDATE script_texts SET content_loc6='¡Alythess! ¡Tu fuego arde dentro de mí!' WHERE entry=-1580053; /* content: Alythess! Your fire burns within me! */
UPDATE script_texts SET content_loc6='¡Ee-nok Kryul!' WHERE entry=-1580055; /* content: Ee-nok Kryul! */
UPDATE script_texts SET content_loc6='Me... desvanezco.' WHERE entry=-1580056; /* content: I... fade. */
UPDATE script_texts SET content_loc6='¡El tiempo es un lujo que ya no podéis permitiros!' WHERE entry=-1580057; /* content: Time is a luxury you no longer possess! */
UPDATE script_texts SET content_loc6='¡Fuego para ayudar a las sombras!' WHERE entry=-1580058; /* content: Fire to the aid of shadow! */
UPDATE script_texts SET content_loc6='¡Sacrolash!' WHERE entry=-1580059; /* content: Sacrolash! */
UPDATE script_texts SET content_loc6='¡Vuestra racha de suerte ha acabado!' WHERE entry=-1580063; /* content: Your luck has run its course! */
UPDATE script_texts SET content_loc6='¡Todos mis planes para esto!' WHERE entry=-1580064; /* content: All my plans have led to this! */
UPDATE script_texts SET content_loc6='¡Ya he esperado suficiente!' WHERE entry=-1580066; /* content: I have waited long enough! */
UPDATE script_texts SET content_loc6='¡Falladme y sufriréis eternamente!' WHERE entry=-1580067; /* content: Fail me and suffer for eternity! */
UPDATE script_texts SET content_loc6='¡Vaciad a la chica! ¡Drenad su poder hasta que no quede más que una cáscara vacía!' WHERE entry=-1580068; /* content: Drain the girl! Drain her power until there is nothing but a vacant shell! */
UPDATE script_texts SET content_loc6='¡CAOS!' WHERE entry=-1580074; /* content: Chaos! */
UPDATE script_texts SET content_loc6='¡DESTRUCCIÓN!' WHERE entry=-1580075; /* content: Destruction! */
UPDATE script_texts SET content_loc6='¡AL OLVIDO!' WHERE entry=-1580076; /* content: Oblivion! */
UPDATE script_texts SET content_loc6='¡No seré rechazado! ¡Este mundo caerá!' WHERE entry=-1580077; /* content: I will not be denied! This world shall fall! */
UPDATE script_texts SET content_loc6='¡Aggghh! ¡Los poderes de La Fuente del Sol... se vuelven... contra mí! ¿Qué habéis hecho? ¿¿¿Qué habéis hecho???' WHERE entry=-1580079; /* content: Aggghh! The powers of the Sunwell... turn... against me! What have you done? What have you done??? */
UPDATE script_texts SET content_loc6='Ahora solo sirvo al maestro.' WHERE entry=-1580082; /* content: I serve only the Master now. */
UPDATE script_texts SET content_loc6='Pero estoy… perdida. No encuentro el camino de vuelta.' WHERE entry=-1580084; /* content: But I\'m... lost. I cannot find my way back. */
UPDATE script_texts SET content_loc6='Kalec... ¿Kalec?' WHERE entry=-1580086; /* content: Kalec... Kalec? */
UPDATE script_texts SET content_loc6='¡He otorgado mi poder a otro orbe! ¡Usadlo rápido!' WHERE entry=-1580092; /* content: I have empowered another orb! Use it quickly! */
UPDATE script_texts SET content_loc6='¡Otro orbe preparado! ¡Daos prisa!' WHERE entry=-1580093; /* content: Another orb is ready! Make haste! */
UPDATE script_texts SET content_loc6='¡He canalizado todo lo que puedo! ¡El poder está en vuestras manos!' WHERE entry=-1580094; /* content: I have channeled all I can! The power is in your hands! */
UPDATE script_texts SET content_loc6='Héroes mortales, vuestra victoria de hoy aquí fue profetizada hace mucho. El angustiado grito de derrota de mi hermano resonará en el universo y llevará una esperanza renovada a todos los que aún se enfrentan a la Legión Ardiente.' WHERE entry=-1580095; /* content: Mortal heroes - your victory here today was foretold long ago. My brother\'s anguished cry of defeat will echo across the universe - bringing renewed hope to all those who still stand against the Burning Crusade. */
UPDATE script_texts SET content_loc6='La criatura Entropius, a quien os habéis visto obligados a destruir, fue otrora el noble naaru M\'uru. En vida, M\'uru canalizó grandes energías de LUZ y ESPERANZA. Durante algún tiempo, algunos descaminados intentaron robar esas energías...' WHERE entry=-1580098; /* content: The creature Entropius, whom you were forced to destroy, was once the noble naaru, M\'uru. In life, M\'uru channeled vast energies of LIGHT and HOPE. For a time, a misguided few sought to steal those energies... */
UPDATE script_texts SET content_loc6='Nuestra arrogancia fue imperdonable. Condenamos a uno de los seres más nobles jamás conocidos. Nunca expiaremos este pecado.' WHERE entry=-1580099; /* content: Our arrogance was unpardonable. We damned one of the most noble beings of all. We may never atone for this sin. */
UPDATE script_texts SET content_loc6='¡Benditos antepasados! Lo siento... tanto amor... tanta gracia... no hay... palabras... es imposible describirlo...' WHERE entry=-1580104; /* content: Blessed ancestors! I feel it... so much love... so much grace... there are... no words... impossible to describe... */
UPDATE script_texts SET content_loc6='La salvación, joven. Es lo que nos espera a todos.' WHERE entry=-1580105; /* content: Salvation, young one. It waits for us all. */
UPDATE script_texts SET content_loc6='%s respira hondo.' WHERE entry=-1580107; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc6='¡Tan solo malgastáis mi tiempo!' WHERE entry=-1585000; /* content: You only waste my time! */
UPDATE script_texts SET content_loc6='¡Mi hambre no conoce límites!' WHERE entry=-1585001; /* content: My hunger knows no bounds! */
UPDATE script_texts SET content_loc6='¡Basta de distracciones!' WHERE entry=-1585003; /* content: Enough distractions! */
UPDATE script_texts SET content_loc6='¡Soy invencible!' WHERE entry=-1585004; /* content: I am invincible! */
UPDATE script_texts SET content_loc6='In... contenible.' WHERE entry=-1585008; /* content: Un...con...tainable. */
UPDATE script_texts SET content_loc6='Con... sumir.' WHERE entry=-1585010; /* content: Con...sume. */
UPDATE script_texts SET content_loc6='Ahora me estoy enfadando.' WHERE entry=-1585015; /* content: Now I\'m getting annoyed. */
UPDATE script_texts SET content_loc6='Esto empieza bien.' WHERE entry=-1585017; /* content: I call that a good start. */
UPDATE script_texts SET content_loc6='El uno es un número tan solitario.' WHERE entry=-1585020; /* content: One is such a lonely number. */
UPDATE script_texts SET content_loc6='Ha sido genial, en serio.' WHERE entry=-1585021; /* content: It\'s been a kick, really. */
UPDATE script_texts SET content_loc6='¡La venganza arde!' WHERE entry=-1585024; /* content: Vengeance burns! */
UPDATE script_texts SET content_loc6='Todos los soldados de Lordaeron deben presentarse inmediatamente en la entrada de Stratholme y esperar las órdenes del príncipe Arthas.' WHERE entry=-1595000; /* content: All soldiers of Lordaeron should immediately report to the entrance of Stratholme, and await further orders from Prince Arthas. */
UPDATE script_texts SET content_loc6='¡Buen trabajo con las cajas! ¡Ven a hablar conmigo delante de Stratholme para tu siguiente encargo!' WHERE entry=-1595001; /* content: Good work with the crates! Come talk to me in front of Stratholme for your next assignment! */
UPDATE script_texts SET content_loc6='¡Se han avistado tropas de la Plaga cerca de la puerta de la calle del Festival!' WHERE entry=-1595003; /* content: Scourge forces have been spotted near the Festival Lane Gate! */
UPDATE script_texts SET content_loc6='¡Se han avistado tropas de la Plaga cerca de la fuente de la Plaza del Rey!' WHERE entry=-1595004; /* content: Scourge forces have been spotted near the King\'s Square fountain! */
UPDATE script_texts SET content_loc6='¡Se han avistado tropas de la Plaga cerca de la puerta de la fila del mercado!' WHERE entry=-1595005; /* content: Scourge forces have been spotted near the Market Row Gate! */
UPDATE script_texts SET content_loc6='¡Se han avistado tropas de la Plaga cerca del Concejo!' WHERE entry=-1595006; /* content: Scourge forces have been spotted near the Town Hall! */
UPDATE script_texts SET content_loc6='¡Se han avistado tropas de la Plaga cerca de la puerta de la Plaza de los Ancestros!' WHERE entry=-1595007; /* content: Scourge forces have been spotted near the Elder\'s Square Gate! */
UPDATE script_texts SET content_loc6='Campeones, reuníos conmigo en el Concejo inmediatamente. ¡Tenemos que enfrentarnos a Mal\'Ganis!' WHERE entry=-1595008; /* content: Champions, meet me at the Town Hall at once. We must take the fight to Mal\'Ganis. */
UPDATE script_texts SET content_loc6='Seguidme, conozco el camino.' WHERE entry=-1595009; /* content: Follow me, I know the way through. */
UPDATE script_texts SET content_loc6='Ah, por fin has llegado príncipe Arthas. Llegas justo a tiempo.' WHERE entry=-1595010; /* content: Ah, you\'ve finally arrived Prince Arthas. You\'re here just in the nick of time. */
UPDATE script_texts SET content_loc6='Sí, me alegro de haber llegado hasta ti antes que la Plaga.' WHERE entry=-1595011; /* content: Yes, I\'m glad I could get to you before the plague. */
UPDATE script_texts SET content_loc6='¿Qué es esta brujería?' WHERE entry=-1595012; /* content: What is this sorcery? */
UPDATE script_texts SET content_loc6='No es necesario que lo entiendas, Arthas. Tan solo tienes que morir.' WHERE entry=-1595013; /* content: There\'s no need for you to understand, Arthas. All you need to do is die. */
UPDATE script_texts SET content_loc6='Parece que Mal\'ganis tiene más que a la Plaga en su arsenal. Deberíamos darnos prisa.' WHERE entry=-1595014; /* content: Mal\'Ganis appears to have more than Scourge in his arsenal. We should make haste. */
UPDATE script_texts SET content_loc6='¡Más brujería vil! ¡Preparaos para cualquier cosa!' WHERE entry=-1595015; /* content: More vile sorcery! Be ready for anything! */
UPDATE script_texts SET content_loc6='En marcha.' WHERE entry=-1595016; /* content: Let\'s move on. */
UPDATE script_texts SET content_loc6='Estad atentos, nos tienen acorralados en esta sala.' WHERE entry=-1595017; /* content: Watch your backs: they have us surrounded in this hall. */
UPDATE script_texts SET content_loc6='Mal\'ganis no nos lo está poniendo fácil.' WHERE entry=-1595018; /* content: Mal\'Ganis is not making this easy. */
UPDATE script_texts SET content_loc6='Son perseverantes.' WHERE entry=-1595019; /* content: They\'re very persistent. */
UPDATE script_texts SET content_loc6='¿Qué más puede poner en mi camino?' WHERE entry=-1595020; /* content: What else can he put in my way? */
UPDATE script_texts SET content_loc6='Príncipe Arthas Menethil, una gran oscuridad se ha apoderado de tu alma hoy. La muerte que estás destinado a causarles a otros, hoy será la tuya.' WHERE entry=-1595021; /* content: Prince Arthas Menethil, on this day, a powerful darkness has taken hold of your soul. The death you are destined to visit upon others will this day be your own. */
UPDATE script_texts SET content_loc6='Hago lo que debo por Lordaeron y ni vuestras palabras, ni vuestras acciones me detendrán.' WHERE entry=-1595022; /* content: I do what I must for Lordaeron, and neither your words nor your actions will stop me. */
UPDATE script_texts SET content_loc6='El camino más rápido hasta Mal\'ganis se encuentra detrás de esa estantería de ahí.' WHERE entry=-1595023; /* content: The quickest path to Mal\'Ganis lies behind that bookshelf ahead. */
UPDATE script_texts SET content_loc6='Esto solo será un momento.' WHERE entry=-1595024; /* content: This will only take a moment. */
UPDATE script_texts SET content_loc6='Me alivia que este pasadizo secreto aún funcione.' WHERE entry=-1595025; /* content: I\'m relieved this secret passage still works. */
UPDATE script_texts SET content_loc6='Movámonos lo más deprisa posible por aquí. Si los no-muertos no nos matan, lo hará el fuego.' WHERE entry=-1595026; /* content: Let\'s move through here as quickly as possible. If the undead don\'t kill us, the fires might. */
UPDATE script_texts SET content_loc6='Descansad un momento y limpiad vuestros pulmones, debemos volver a movernos pronto.' WHERE entry=-1595027; /* content: Rest a moment and clear your lungs, but we must move again soon. */
UPDATE script_texts SET content_loc6='Ya basta, debemos seguir moviéndonos. Mal\'ganis espera.' WHERE entry=-1595028; /* content: That\'s enough; we must move again. Mal\'Ganis awaits. */
UPDATE script_texts SET content_loc6='Al fin un poco de suerte. La Fila del Mercado todavía no se ha incendiado. Se supone que Mal\'ganis está en la Plaza de los Cruzados, que está justo delante. Cuando estéis listos para continuar, decídmelo.' WHERE entry=-1595029; /* content: At last some good luck. Market Row has not caught fire yet. Mal\'Ganis is supposed to be in Crusaders\' Square, which is just ahead. Tell me when you\'re ready to move forward. */
UPDATE script_texts SET content_loc6='Se hará justicia.' WHERE entry=-1595030; /* content: Justice will be done. */
UPDATE script_texts SET content_loc6='Vamos a poner fin a esto ahora, Mal\'Ganis. Solos tú y yo.' WHERE entry=-1595031; /* content: We\'re going to finish this right now, Mal\'Ganis. Just you... and me. */
UPDATE script_texts SET content_loc6='Tu viaje acaba de comenzar, joven Príncipe. Reúne a tus tropas y ven a verme en las árticas tierras de Rasganorte. Allí ajustaremos cuentas. Allí es donde se desvelará tu verdadero destino.' WHERE entry=-1595032; /* content: Your journey has just begun, young prince. Gather your forces and meet me in the arctic land of Northrend. It is there that we shall settle the score between us. It is there that your true destiny will unfold. */
UPDATE script_texts SET content_loc6='¡Te perseguiré hasta el fin del mundo si debo! ¿Me oyes? ¡Hasta el fin del mundo!' WHERE entry=-1595033; /* content: I\'ll hunt you to the ends of the earth if I have to! Do you hear me? To the ends of the earth! */
UPDATE script_texts SET content_loc6='Habéis actuado bien hoy. Cualquier cosa que Mal\'Ganis haya dejado atrás es vuestra. Tomadla como recompensa. Ahora he de comenzar a planear una expedición a Rasganorte.' WHERE entry=-1595034; /* content: You performed well this day. Anything that Mal\'Ganis has left behind is yours. Take it as your reward. I must now begin plans for an expedition to Northrend. */
UPDATE script_texts SET content_loc6='¡Proteged a vuestro Príncipe, soldados de Lordaeron! ¡Necesito ayuda!' WHERE entry=-1595035; /* content: Protect your prince, soldiers of Lordaeron! I am in need of aid! */
UPDATE script_texts SET content_loc6='Mal\'Ganis pagará por esto.' WHERE entry=-1595037; /* content: Mal\'Ganis will pay for this. */
UPDATE script_texts SET content_loc6='No puedo dejarte con vida.' WHERE entry=-1595038; /* content: I can\'t afford to spare you. */
UPDATE script_texts SET content_loc6='Un obstáculo menos del que preocuparse.' WHERE entry=-1595039; /* content: One less obstacle to deal with. */
UPDATE script_texts SET content_loc6='¡La carne no es rival para el hierro!' WHERE entry=-1599002; /* content: Flesh is no match for iron! */
UPDATE script_texts SET content_loc6='¡Los ejércitos de hierro asfixiarán el mundo!' WHERE entry=-1599003; /* content: Armies of iron will smother the world! */
UPDATE script_texts SET content_loc6='¿Por qué tiene que ser así?' WHERE entry=-1599006; /* content: Why must it be this way? */
UPDATE script_texts SET content_loc6='¡Tú te lo has buscado!' WHERE entry=-1599007; /* content: You had it coming! */
UPDATE script_texts SET content_loc6='Tanto tiempo perdido... ¡que nunca recuperarás!' WHERE entry=-1599010; /* content: So much lost time... that you\'ll never get back! */
UPDATE script_texts SET content_loc6='¡Espero que esto valga la pena!' WHERE entry=-1599019; /* content: I hope this is all worth it! */
UPDATE script_texts SET content_loc6='¡Hora de conseguir respuestas! ¡Que empiece el espectáculo!' WHERE entry=-1599020; /* content: Time to get some answers! Let\'s get this show on the road! */
UPDATE script_texts SET content_loc6='En el nombre de Madoran, ¿qué ha hecho eso? ¡Oh! Esperad, creo que lo tengo...' WHERE entry=-1599023; /* content: What in the name o\' Madoran did THAT do? Oh! Wait: I just about got it... */
UPDATE script_texts SET content_loc6='¡Atentos! Tendré esto listo en un par de...' WHERE entry=-1599026; /* content: Now keep an eye out! I\'ll have this licked in two shakes of a-- */
UPDATE script_texts SET content_loc6='Oh, eso no suena nada bien. Puede que tengamos alguna que otra complicación...' WHERE entry=-1599028; /* content: Oh, that doesn\'t sound good. We might have a complication or two... */
UPDATE script_texts SET content_loc6='Ah, quieres jugar duro, ¿eh? ¡A mí me encanta!' WHERE entry=-1599030; /* content: Ah, you want to play hardball, eh? That\'s just my game! */
UPDATE script_texts SET content_loc6='Un par de minutos más y … Oh, oh.' WHERE entry=-1599031; /* content: Couple more minutes and I\'ll-- */
UPDATE script_texts SET content_loc6='¿Aumentado? ¿Cuál es la buena noticia?' WHERE entry=-1599033; /* content: Heightened? What\'s the good news? */
UPDATE script_texts SET content_loc6='¡Ese era el problema! Ahora, estoy avanzando…' WHERE entry=-1599034; /* content: So that was the problem? Now I\'m makin\' progress... */
UPDATE script_texts SET content_loc6='Índice de amenaza crítico. Análisis del vacío desviado. Iniciando protocolo de higienización.' WHERE entry=-1599035; /* content: Critical threat index. Void analysis diverted. Initiating sanitization protocol. */
UPDATE script_texts SET content_loc6='¡Espera! ¡Mientras yo esté al mando, no se va a higienizar a nadie!' WHERE entry=-1599036; /* content: Hang on! Nobody\'s gonna\' be sanitized as long as I have a say in it! */
UPDATE script_texts SET content_loc6='¿Qué protectores?' WHERE entry=-1599052; /* content: What protectors? */
UPDATE script_texts SET content_loc6='¡Alto! ¿Los Aesir y los Vanir fueron a la guerra? ¿Por qué?' WHERE entry=-1599056; /* content: Hold everything! The Aesir and Vanir went to war? Why? */
UPDATE script_texts SET content_loc6='Eso es esencialmente correcto.' WHERE entry=-1599059; /* content: Essentially that is correct. */
UPDATE script_texts SET content_loc6='¡¿Loken?! Ya me estoy hartando... puede que hayamos neutralizado a los enanos férreos, ¡pero apostaría a que en otra parte hay otra máquina escupiendo un montón de esos vrykuls férreos!' WHERE entry=-1599062; /* content: Loken?! That\'s downright bothersome... We might\'ve neutralized the iron dwarves, but I\'d lay odds there\'s another machine somewhere else churnin\' out a whole mess o\' these iron vrykul! */
UPDATE script_texts SET content_loc6='Es hora de ver qué hay detrás de la puerta cercana a la entrada. Voy a infiltrarme por allí, con cuidado y en silencio. Reúnete conmigo en la puerta y haré que entremos.' WHERE entry=-1599064; /* content: I think it\'s time to see what\'s behind the door near the entrance. I\'m going to sneak over there, nice and quiet. Meet me at the door and I\'ll get us in. */
UPDATE script_texts SET content_loc6='¡Tanta hambe! ¡Debé coméh!' WHERE entry=-1600001; /* content: So hungry! Must feed! */
UPDATE script_texts SET content_loc6='¡Cueh\'po háse bum!' WHERE entry=-1600003; /* content: Corpse go boom! */
UPDATE script_texts SET content_loc6='Vuestros esfuerzos... son en vano.' WHERE entry=-1600006; /* content: Your efforts... are in vain. */
UPDATE script_texts SET content_loc6='¡Refuerza mis defensas! ¡Deprisa, maldito!' WHERE entry=-1600008; /* content: Bolster my defenses! Hurry, curse you! */
UPDATE script_texts SET content_loc6='¡Seguro que ahora veis la inutilidad de todo ello!' WHERE entry=-1600009; /* content: Surely you can see the futility of it all! */
UPDATE script_texts SET content_loc6='¡Rendíos ya y morid!' WHERE entry=-1600010; /* content: Just give up and die already! */
UPDATE script_texts SET content_loc6='%s pide ayuda.' WHERE entry=-1600011; /* content: %s calls for assistance. */
UPDATE script_texts SET content_loc6='¡Tharon\'ja lo ve todo! ¡El esfuerzo de los mortales no acabará con la dinastía eterna!' WHERE entry=-1600012; /* content: Tharon\'ja sees all! The work of mortals shall not end the eternal dynasty! */
UPDATE script_texts SET content_loc6='¡Tu carne sirve ahora a Tharon\'ja!' WHERE entry=-1600015; /* content: Your flesh serves Tharon\'ja now! */
UPDATE script_texts SET content_loc6='¡Tharon\'ja ha encontrado uso para tu cuerpo!' WHERE entry=-1600016; /* content: Tharon\'ja has a use for your mortal shell! */
UPDATE script_texts SET content_loc6='¡No! Un regocijo... ¡demasiado breve!' WHERE entry=-1600017; /* content: No! A taste... all too brief! */
UPDATE script_texts SET content_loc6='Has sido un insensato al venir.' WHERE entry=-1601002; /* content: You were foolish to come. */
UPDATE script_texts SET content_loc6='¡Como Anub\'arak ordena!' WHERE entry=-1601003; /* content: As Anub\'Arak commands! */
UPDATE script_texts SET content_loc6='Debemos proteger la puerta. ¡Atacad! ¡Desmembradlos poco a poco!' WHERE entry=-1601005; /* content: We must hold the gate. Attack! Tear them limb from limb! */
