UPDATE script_texts SET content_loc6='Ya... ya no me queda tiempo. Estoy acabado. Acaba conmigo, $n. Hazlo o nos matarán a los dos. $n... ¡Por la Horda! Vale la pena salvar este mundo.' WHERE entry=-1609074; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... For the Horde! This world is worth saving. */
UPDATE script_texts SET content_loc6='Ya... ya no me queda tiempo. Estoy acabado. Acaba conmigo, $n. Hazlo o nos matarán a los dos. $n... Recuerda Mulgore. Vale la pena salvar este mundo.' WHERE entry=-1609075; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... Remember Mulgore. This world is worth saving. */
UPDATE script_texts SET content_loc6='¡Hazlo, $n! ¡Pon fin a mi sufrimiento!' WHERE entry=-1609077; /* content: Do it, $N! Put me out of my misery! */
UPDATE script_texts SET content_loc6='Tengo que coger mi hojarruna y mi armadura... Solo necesito un poco más de tiempo.' WHERE entry=-1609079; /* content: I\'ll need to get my runeblade and armor... Just need a little more time. */
UPDATE script_texts SET content_loc6='Aún estoy débil, pero creo que puedo levantar una barrera antimagia. Quédate en su interior o sus hechizos te destruirán.' WHERE entry=-1609080; /* content: I\'m still weak, but I think I can get an anti-magic barrier up. Stay inside it or you\'ll be destroyed by their spells. */
UPDATE script_texts SET content_loc6='Mantener esta barrerá requerirá toda mi concentración. ¡Mátalos a todos!' WHERE entry=-1609081; /* content: Maintaining this barrier will require all of my concentration. Kill them all! */
UPDATE script_texts SET content_loc6='Vienen más. ¡Defiéndete! ¡No te salgas del campo antimagia! ¡Sin su protección, te destrozarán!' WHERE entry=-1609082; /* content: There are more coming. Defend yourself! Don\'t fall out of the anti-magic field! They\'ll tear you apart without its protection! */
UPDATE script_texts SET content_loc6='¡Viene el Alto Inquisidor! ¡Prepárate, caballero de la Muerte! ¡No dejes que te saque de los límites protectores de mi campo antimagia! ¡Mátalo y coge su cabeza!' WHERE entry=-1609084; /* content: The High Inquisitor comes! Be ready, death knight! Do not let him draw you out of the protective bounds of my anti-magic field! Kill him and take his head! */
UPDATE script_texts SET content_loc6='¡Mantente dentro del campo antimagia! ¡Haz que vayan a por ti!' WHERE entry=-1609085; /* content: Stay in the anti-magic field! Make them come to you! */
UPDATE script_texts SET content_loc6='La muerte del Alto Inquisidor de Nuevo Avalon no pasará desapercibida. ¡Tienes que salir de aquí inmediatamente! Vete antes de que aparezcan más. Yo me las apañaré solo.' WHERE entry=-1609086; /* content: The death of the High Inquisitor of New Avalon will not go unnoticed. You need to get out of here at once! Go, before more of them show up. I\'ll be fine on my own. */
UPDATE script_texts SET content_loc6='Yo atraeré su fuego, tú escapa detrás de mí.' WHERE entry=-1609087; /* content: I\'ll draw their fire, you make your escape behind me. */
UPDATE script_texts SET content_loc6='¡Vuestro Alto Inquisidor no es más que un montón de carne, cruzados! ¡Nadie está al margen del abrazo de la Plaga!' WHERE entry=-1609088; /* content: Your High Inquisitor is nothing more than a pile of meat, Crusaders! There are none beyond the grasp of the Scourge! */
UPDATE script_texts SET content_loc6='¿Mami?' WHERE entry=-1609091; /* content: Mommy? */
UPDATE script_texts SET content_loc6='¡DAME CEREBROS!' WHERE entry=-1609092; /* content: GIVE ME BRAINS! */
UPDATE script_texts SET content_loc6='Deber alimentar...' WHERE entry=-1609093; /* content: Must feed... */
UPDATE script_texts SET content_loc6='Tan hambriento...' WHERE entry=-1609094; /* content: So hungry... */
UPDATE script_texts SET content_loc6='¡O mete al necrófago en el foso o se enfrenta a la correa!' WHERE entry=-1609096; /* content: It puts the ghoul in the pit or else it gets the lash! */
UPDATE script_texts SET content_loc6='%s se encabrita, señalándote que lo montes.' WHERE entry=-1609097; /* content: %s rears up, beckoning you to ride it. */
UPDATE script_texts SET content_loc6='Impresionante, caballero de la Muerte. Vuelve a verme en el mundo de los vivos para obtener tu recompensa.' WHERE entry=-1609098; /* content: Impressive, death knight. Return to me in the world of the living for your reward. */
UPDATE script_texts SET content_loc6='¡Soldados de la Plaga, en pie! ¡Preparaos para desatar vuestra furia sobre El Alba Argenta!' WHERE entry=-1609201; /* content: Soldiers of the Scourge, stand ready! Prepare to unleash your fury upon the Argent Dawn! */
UPDATE script_texts SET content_loc6='¡Caballeros de la Muerte de Acherus, comienza la marcha fúnebre!' WHERE entry=-1609203; /* content: Death knights of Acherus, the death march begins! */
UPDATE script_texts SET content_loc6='Soldados de la Plaga, caballeros de la Muerte de Acherus, esbirros de la oscuridad: ¡escuchad la llamada del Alto Señor!' WHERE entry=-1609204; /* content: Soldiers of the Scourge, death knights of Acherus, minions of the darkness: hear the call of the Highlord! */
UPDATE script_texts SET content_loc6='¡Levántate!' WHERE entry=-1609205; /* content: RISE! */
UPDATE script_texts SET content_loc6='¡Los cielos se vuelven rojos con la sangre de los caídos! ¡El Rey Exánime nos observa, esbirros! ¡Adelante! ¡No dejéis más que cenizas y miseria tras vuestro destructivo paso!' WHERE entry=-1609206; /* content: The skies turn red with the blood of the fallen! The Lich King watches over us, minions! Leave only ashes and misery in your destructive wake! */
UPDATE script_texts SET content_loc6='¡Se aproximan los ejércitos de la Plaga!' WHERE entry=-1609207; /* content: Scourge armies approach! */
UPDATE script_texts SET content_loc6='¡Manteneos firmes, hermanos y hermanas! ¡La Luz prevalecerá!' WHERE entry=-1609208; /* content: Stand fast, brothers and sisters! The Light will prevail! */
UPDATE script_texts SET content_loc6='¡Arrodillaos ante el Alto Señor!' WHERE entry=-1609209; /* content: Kneel before the Highlord! */
UPDATE script_texts SET content_loc6='¡No tenéis la menor oportunidad!' WHERE entry=-1609210; /* content: You stand no chance! */
UPDATE script_texts SET content_loc6='¡La Plaga destruirá este lugar!' WHERE entry=-1609211; /* content: The Scourge will destroy this place! */
UPDATE script_texts SET content_loc6='Tu vida ha terminado.' WHERE entry=-1609212; /* content: Your life is forfeit. */
UPDATE script_texts SET content_loc6='¿Cuánto tiempo más aguantarán vuestras tropas?' WHERE entry=-1609214; /* content: How much longer will your forces hold out? */
UPDATE script_texts SET content_loc6='¡Acabad con ellos!' WHERE entry=-1609216; /* content: Spare no one! */
UPDATE script_texts SET content_loc6='¿Qué es esto? Yo... No puedo atacar...' WHERE entry=-1609217; /* content: What is this?! My... I cannot strike... */
UPDATE script_texts SET content_loc6='¡OBEDÉCEME, HOJA!' WHERE entry=-1609218; /* content: Obey me, blade! */
UPDATE script_texts SET content_loc6='¡Harás lo que yo ordene! ¡Yo mando aquí!' WHERE entry=-1609219; /* content: You will do as I command! I am in control here! */
UPDATE script_texts SET content_loc6='¿Qué me está pasando?' WHERE entry=-1609221; /* content: What is happening to me? */
UPDATE script_texts SET content_loc6='La Crematoria me desafía...' WHERE entry=-1609223; /* content: Ashbringer defies me... */
UPDATE script_texts SET content_loc6='¡Esbirros, venid a ayudarme!' WHERE entry=-1609224; /* content: Minions, come to my aid! */
UPDATE script_texts SET content_loc6='¡No puedes ganar, Darion!' WHERE entry=-1609225; /* content: You cannot win, Darion! */
UPDATE script_texts SET content_loc6='¡Traedlos ante la capilla!' WHERE entry=-1609226; /* content: Bring them before the chapel! */
UPDATE script_texts SET content_loc6='¡Lo que sientes ahora mismo es la angustia de mil almas perdidas! ¡Almas que tú y tu señor habéis traído aquí! ¡La Luz te va a destrozar, Darion!' WHERE entry=-1609230; /* content: What you are feeling right now is the anguish of a thousand lost souls! Souls that you and your master brought here! The Light will tear you apart, Darion! */
UPDATE script_texts SET content_loc6='Ahorra el aliento, viejo. Puede ser el último.' WHERE entry=-1609231; /* content: Save your breath, old man. It might be the last you ever draw. */
UPDATE script_texts SET content_loc6='¡Mi hijo! ¡Mi querido y precioso hijo!' WHERE entry=-1609232; /* content: My son! My dear, beautiful boy! */
UPDATE script_texts SET content_loc6='¡Padre!' WHERE entry=-1609233; /* content: Father! */
UPDATE script_texts SET content_loc6='¡Padre! ¡Has vuelto!' WHERE entry=-1609235; /* content: Father, you have returned! */
UPDATE script_texts SET content_loc6='Padre, has estado fuera mucho tiempo. Pensé que...' WHERE entry=-1609236; /* content: You have been gone a long time, father. I thought... */
UPDATE script_texts SET content_loc6='Nada podría mantenerme lejos de aquí, Darion. No de mi hogar y mi familia.' WHERE entry=-1609237; /* content: Nothing could have kept me away from here, Darion. Not from my home and family. */
UPDATE script_texts SET content_loc6='Padre, quiero unirme a ti en la guerra contra los no-muertos. ¡Quiero luchar! ¡Ya no puedo ser un mero espectador!' WHERE entry=-1609238; /* content: Father, I wish to join you in the war against the undead. I want to fight! I can sit idle no longer! */
UPDATE script_texts SET content_loc6='No lo olvides...' WHERE entry=-1609242; /* content: Do not forget... */
UPDATE script_texts SET content_loc6='Emocionante...' WHERE entry=-1609243; /* content: Touching... */
UPDATE script_texts SET content_loc6='Patético...' WHERE entry=-1609246; /* content: Pathetic... */
UPDATE script_texts SET content_loc6='¡Eres un maldito monstruo, Arthas!' WHERE entry=-1609247; /* content: You\'re a damned monster, Arthas! */
UPDATE script_texts SET content_loc6='Tenías razón, Vadín. Los envié hacia su muerte. Sus vidas no tienen sentido, pero la tuya...' WHERE entry=-1609248; /* content: You were right, Fordring. I did send them in to die. Their lives are meaningless, but yours... */
UPDATE script_texts SET content_loc6='¡¡¡ATACAD!!!' WHERE entry=-1609250; /* content: ATTACK!!! */
UPDATE script_texts SET content_loc6='¡APOCALIPSIS!' WHERE entry=-1609251; /* content: APOCALYPSE! */
UPDATE script_texts SET content_loc6='Hoy no es ese día...' WHERE entry=-1609252; /* content: That day is not today... */
UPDATE script_texts SET content_loc6='¡Tirion!' WHERE entry=-1609253; /* content: Tirion! */
UPDATE script_texts SET content_loc6='¡¡ARTHAS!!' WHERE entry=-1609254; /* content: ARTHAS!!!! */
UPDATE script_texts SET content_loc6='¿Qué es esto?' WHERE entry=-1609255; /* content: What is this? */
UPDATE script_texts SET content_loc6='Tu fin.' WHERE entry=-1609256; /* content: Your end. */
UPDATE script_texts SET content_loc6='Imposible...' WHERE entry=-1609257; /* content: Impossible... */
UPDATE script_texts SET content_loc6='Ahora os hago una promesa, hermanos y hermanas: ¡el Rey Exánime será derrotado! En este día, os pido una unión.' WHERE entry=-1609264; /* content: I make a promise to you now, brothers and sisters: The Lich King will be defeated! On this day, I call for a union. */
UPDATE script_texts SET content_loc6='%s se arrodilla derrotado ante Tirion Vadín.' WHERE entry=-1609273; /* content: %s kneels in defeat before Tirion Fordring. */
UPDATE script_texts SET content_loc6='%s abraza a su padre.' WHERE entry=-1609276; /* content: %s hugs his father. */
UPDATE script_texts SET content_loc6='%s toma aire.' WHERE entry=-1609280; /* content: %s gasps for air. */
UPDATE script_texts SET content_loc6='%s se desploma.' WHERE entry=-1609283; /* content: %s collapses. */
UPDATE script_texts SET content_loc6='¡No temo a nada! ¡Y menos a vosotros!' WHERE entry=-1615000; /* content: I fear nothing! Least of all you! */
UPDATE script_texts SET content_loc6='¡De rodillas!' WHERE entry=-1615007; /* content: On your knees! */
UPDATE script_texts SET content_loc6='¡Un discípulo de Shadron aparece en el Crepúsculo!' WHERE entry=-1615008; /* content: A Shadron Disciple appears in the Twilight! */
UPDATE script_texts SET content_loc6='Sin competencia.' WHERE entry=-1615010; /* content: No contest. */
UPDATE script_texts SET content_loc6='No debería... haberme contenido...' WHERE entry=-1615012; /* content: I should not... have held back... */
UPDATE script_texts SET content_loc6='Mi tarea es cuidar de estos huevos. ¡Arderéis antes de hacerles el más mínimo daño!' WHERE entry=-1615018; /* content: It is my charge to watch over these eggs. I will see you burn before any harm comes to them! */
UPDATE script_texts SET content_loc6='¡Este patético asedio acaba AQUÍ!' WHERE entry=-1615019; /* content: This pathetic siege ends NOW! */
UPDATE script_texts SET content_loc6='¡Arded, desgraciados miserables!' WHERE entry=-1615020; /* content: Burn, you miserable wretches! */
UPDATE script_texts SET content_loc6='¡Tenebron! ¡Tú también tienes que proteger los huevos!' WHERE entry=-1615022; /* content: Tenebron! The eggs are yours to protect as well! */
UPDATE script_texts SET content_loc6='Unos insectos tan inflamables...' WHERE entry=-1615025; /* content: Such flammable little insects.... */
UPDATE script_texts SET content_loc6='¡Vuestras cenizas cubrirán el suelo!' WHERE entry=-1615026; /* content: Your charred bones will litter the floor! */
UPDATE script_texts SET content_loc6='¿Cuánto calor podéis soportar?' WHERE entry=-1615027; /* content: How much heat can you take? */
UPDATE script_texts SET content_loc6='¡Todo quedará reducido a cenizas!' WHERE entry=-1615028; /* content: All will be reduced to ash! */
UPDATE script_texts SET content_loc6='Serás un suculento banquete para las crías.' WHERE entry=-1615029; /* content: You will make a fine meal for the hatchlings. */
UPDATE script_texts SET content_loc6='¡La lava se arremolina alrededor de %s!' WHERE entry=-1615032; /* content: The lava surrounding %s churns! */
UPDATE script_texts SET content_loc6='¿Eso es lo mejor que sabes hacer?' WHERE entry=-1615035; /* content: Was that the best you can do? */
UPDATE script_texts SET content_loc6='Me hurgaré entre los dientes con vuestros huesos.' WHERE entry=-1615037; /* content: I will pick my teeth with your bones! */
UPDATE script_texts SET content_loc6='¡Un discípulo de Vesperon aparece en el Crepúsculo!' WHERE entry=-1615041; /* content: A Vesperon Disciple appears in the Twilight! */
UPDATE script_texts SET content_loc6='¡%s comienza a abrir Portales Crepuscular!' WHERE entry=-1615042; /* content: %s begins to open a Twilight Portal! */
UPDATE script_texts SET content_loc6='¡Seres inferiores inmiscuyéndose aquí! ¡Es una lástima que vuestro exceso de valor no compense vuestra estupidez!' WHERE entry=-1616000; /* content: Lesser beings, intruding here! A shame that your excess courage does not compensate for your stupidity! */
UPDATE script_texts SET content_loc6='¡Soy imparable!' WHERE entry=-1616007; /* content: I AM UNSTOPPABLE! */
UPDATE script_texts SET content_loc6='¡Pocos han experimentado el dolor que voy a infligiros ahora!' WHERE entry=-1616012; /* content: Few have experienced the pain I will now inflict upon you! */
UPDATE script_texts SET content_loc6='¡No lo conseguiréis mientras me quede aliento!' WHERE entry=-1616013; /* content: You will not succeed while I draw breath! */
UPDATE script_texts SET content_loc6='¡Soy el Tejehechizos! ¡Mi poder es infinito!' WHERE entry=-1616016; /* content: I am the spell-weaver! My power is infinite! */
UPDATE script_texts SET content_loc6='¡Tu espíritu se quedará aquí para siempre!' WHERE entry=-1616017; /* content: Your spirit will linger here forever! */
UPDATE script_texts SET content_loc6='¡Someteos!' WHERE entry=-1616020; /* content: SUBMIT! */
UPDATE script_texts SET content_loc6='¡Los poderes que actúan aquí superan cualquier cosa que podáis imaginar!' WHERE entry=-1616024; /* content: The powers at work here exceed anything you could possibly imagine! */
UPDATE script_texts SET content_loc6='¡Es una causa perdida!' WHERE entry=-1616026; /* content: Your cause is lost! */
UPDATE script_texts SET content_loc6='¡Haré pedazos vuestras frágiles mentes!' WHERE entry=-1616027; /* content: Your fragile mind will be shattered! */
UPDATE script_texts SET content_loc6='Hice lo que tenía que hacer, hermano. No me dejaste otra alternativa.' WHERE entry=-1616029; /* content: I did what I had to, brother. You gave me no alternative. */
UPDATE script_texts SET content_loc6='Esta resolución me apena enormemente, pero la destrucción, la monumental pérdida de vida... tenían que acabar. A pesar de las recientes transgresiones de Malygos, lloraré su pérdida. Hubo un tiempo en que fue un guardián, un protector. Hoy ha caído uno de los seres más poderosos del mundo.' WHERE entry=-1616031; /* content: This resolution pains me deeply, but the destruction, the monumental loss of life had to end. Regardless of Malygos\' recent transgressions, I will mourn his loss. He was once a guardian, a protector. This day, one of the world\'s mightiest has fallen. */
UPDATE script_texts SET content_loc6='¡Un Chispazo toma forma a partir de una falla cercana!' WHERE entry=-1616033; /* content: A Power Spark forms from a nearby rift! */
UPDATE script_texts SET content_loc6='%s respira hondo.' WHERE entry=-1616034; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc6='Los secretos de las profundidades permanecerán ocultos.' WHERE entry=-1619000; /* content: The secrets of the deep shall remain hidden. */
UPDATE script_texts SET content_loc6='¡Shhhad ak kereeesshh chak-k-k!' WHERE entry=-1619002; /* content: Shhhad ak kereeesshh chak-k-k! */
UPDATE script_texts SET content_loc6='¡Por el Rey Exánime!' WHERE entry=-1619004; /* content: For the Lich King! */
UPDATE script_texts SET content_loc6='Puede que pronto seamos aliados.' WHERE entry=-1619005; /* content: Perhaps we will be allies soon. */
UPDATE script_texts SET content_loc6='¡Surge un guardián Ahn\'kahar!' WHERE entry=-1619007; /* content: An Ahn\'kahar Guardian hatches! */
UPDATE script_texts SET content_loc6='¿Quién de entre vosotros es devoto?' WHERE entry=-1619018; /* content: Who among you is devoted? */
UPDATE script_texts SET content_loc6='¡Eh, tú! ¡Adelántate!' WHERE entry=-1619019; /* content: You there! Step forward! */
UPDATE script_texts SET content_loc6='¡Maestro, un regalo para ti!' WHERE entry=-1619021; /* content: Master, a gift for you! */
UPDATE script_texts SET content_loc6='¡Gloria a Yogg-Saron!' WHERE entry=-1619022; /* content: Glory to Yogg-Saron! */
UPDATE script_texts SET content_loc6='¡Levántate! ¡Aún no has sufrido bastante!' WHERE entry=-1619024; /* content: Get up! You haven\'t suffered enough. */
UPDATE script_texts SET content_loc6='No esperéis que vuestro sacrilegio... quede impune.' WHERE entry=-1619025; /* content: Do not expect your sacrilege... to go unpunished. */
UPDATE script_texts SET content_loc6='¡Los propios elementos se alzarán contra el mundo civilizado! ¡Solo los fieles se salvarán!' WHERE entry=-1619026; /* content: The elements themselves will rise up against the civilized world! Only the faithful will be spared! */
UPDATE script_texts SET content_loc6='¡He sido elegida!' WHERE entry=-1619031; /* content: I have been chosen! */
UPDATE script_texts SET content_loc6='Me entrego al maestro' WHERE entry=-1619032; /* content: I give myself to the master! */
UPDATE script_texts SET content_loc6='Shgla\'yos plahf mh\'naus.' WHERE entry=-1619033; /* content: Shgla\'yos plahf mh\'naus. */
UPDATE script_texts SET content_loc6='Gul\'kafh an\'shel. Yoq\'al shn ky ywaq nuul.' WHERE entry=-1619034; /* content: Gul\'kafh an\'shel. Yoq\'al shn ky ywaq nuul. */
UPDATE script_texts SET content_loc6='Ywaq puul skshgn: on\'ma yeh\'glu zuq.' WHERE entry=-1619035; /* content: Ywaq puul skshgn: on\'ma yeh\'glu zuq. */
UPDATE script_texts SET content_loc6='Ywaq ma phgwa\'cul hnakf.' WHERE entry=-1619036; /* content: Ywaq ma phgwa\'cul hnakf. */
UPDATE script_texts SET content_loc6='Ywaq maq oou; ywaq maq ssaggh. Ywaq ma shg\'fhn.' WHERE entry=-1619037; /* content: Ywaq maq oou; ywaq maq ssaggh. Ywaq ma shg\'fhn. */
UPDATE script_texts SET content_loc6='Iilth vwah, uhn\'agth fhssh za.' WHERE entry=-1619039; /* content: Iilth vwah, uhn\'agth fhssh za. */
UPDATE script_texts SET content_loc6='Este es el principio Y el final, mortales. ¡Nadie puede entrar en el sagrario del maestro!' WHERE entry=-1631001; /* content: This is the beginning AND the end, mortals. None may enter the master\'s sanctum! */
UPDATE script_texts SET content_loc6='¡La Plaga arrasará este mundo como un enjambre de muerte y destrucción!' WHERE entry=-1631002; /* content: The Scourge will wash over this world as a swarm of death and destruction! */
UPDATE script_texts SET content_loc6='¡TORMENTA DE HUESOS!' WHERE entry=-1631003; /* content: BONE STORM! */
UPDATE script_texts SET content_loc6='¡Unidos por los huesos!' WHERE entry=-1631004; /* content: Bound by bone! */
UPDATE script_texts SET content_loc6='¡Quedaos cerca!' WHERE entry=-1631005; /* content: Stick around! */
UPDATE script_texts SET content_loc6='¡La única salida es la muerte!' WHERE entry=-1631006; /* content: The only escape is death! */
UPDATE script_texts SET content_loc6='¡Más huesos para la ofrenda!' WHERE entry=-1631007; /* content: More bones for the offering! */
UPDATE script_texts SET content_loc6='¡Púdrete en tu condena!' WHERE entry=-1631008; /* content: Languish in damnation! */
UPDATE script_texts SET content_loc6='¡LA RABIA DEL MAESTRO FLUYE DENTRO DE MÍ!' WHERE entry=-1631010; /* content: THE MASTER\'S RAGE COURSES THROUGH ME! */
UPDATE script_texts SET content_loc6='Habéis encontrado el camino hasta aquí, porque estáis entre los pocos dotados con verdadera visión en un mundo ciego.' WHERE entry=-1631011; /* content: You have found your way here, because you are among the few gifted with true vision in a world cursed with blindness. */
UPDATE script_texts SET content_loc6='Podéis ver la niebla que envuelve este mundo como una mortaja y dónde se encuentra el verdadero poder.' WHERE entry=-1631012; /* content: You can see through the fog that hangs over this world like a shroud, and grasp where true power lies. */
UPDATE script_texts SET content_loc6='Fijad los ojos sobre vuestras burdas manos: los tendones, la tierna carne, la oscura sangre que las recorre.' WHERE entry=-1631013; /* content: Fix your eyes upon your crude hands: the sinew, the soft meat, the dark blood coursing within. */
UPDATE script_texts SET content_loc6='Cuanto antes aceptéis vuestra condición como un defecto, antes os encontraréis en posición de superarlo.' WHERE entry=-1631015; /* content: The sooner you come to accept your condition as a defect, the sooner you will find yourselves in a position to transcend it. */
UPDATE script_texts SET content_loc6='Con nuestro maestro, todo es posible. Su poder no tiene límite y su voluntad es inflexible.' WHERE entry=-1631016; /* content: Through our Master, all things are possible. His power is without limit, and his will unbending. */
UPDATE script_texts SET content_loc6='Los que se oponen a él serán destruidos, y los que lo sirven,... los que lo sirven en cuerpo y alma con total y completa devoción, sin cuestionarle, serán elevados más allá de su conocimiento.' WHERE entry=-1631017; /* content: Those who oppose him will be destroyed utterly, and those who serve -- who serve wholly, unquestioningly, with utter devotion of mind and soul -- elevated to heights beyond your ken. */
UPDATE script_texts SET content_loc6='¡Basta! Veo que debo hacer esto yo misma.' WHERE entry=-1631019; /* content: Enough! I see I must take matters into my own hands! */
UPDATE script_texts SET content_loc6='¡Te libero de la maldición de la carne!' WHERE entry=-1631021; /* content: I release you from the curse of flesh! */
UPDATE script_texts SET content_loc6='¡Eres frágil y débil para resistirte a mi voluntad!' WHERE entry=-1631023; /* content: You are weak, powerless to resist my will! */
UPDATE script_texts SET content_loc6='¡POR EL PODER DEL REY EXÁNIME!' WHERE entry=-1631028; /* content: BY THE MIGHT OF THE LICH KING! */
UPDATE script_texts SET content_loc6='¡El suelo se vuelve rojo con tu sangre!' WHERE entry=-1631029; /* content: The ground runs red with your blood! */
UPDATE script_texts SET content_loc6='¡Comed, mis esbirros!' WHERE entry=-1631030; /* content: Feast, my minions! */
UPDATE script_texts SET content_loc6='¡No eres nada!' WHERE entry=-1631031; /* content: You are nothing! */
UPDATE script_texts SET content_loc6='¡Tu alma no encontrará redención!' WHERE entry=-1631032; /* content: Your soul will find no redemption here! */
UPDATE script_texts SET content_loc6='¡Entonces movámonos! ¡Sali...!' WHERE entry=-1631035; /* content: Let\'s get a move on then! Move ou... */
