UPDATE script_texts SET content_loc6='¡La siguiente batalla os enfrentará a los caballeros más poderosos de la Cruzada Argenta! Solo seréis dignos si los derrotáis...' WHERE entry=-1649020; /* content: The next battle will be against the Argent Crusade\'s most powerful knights! Only by defeating them will you be deemed worthy... */
UPDATE script_texts SET content_loc6='¡Han mancillado nuestro honor! Difunden absurdas afirmaciones y nos acusan en falso. ¡Exijo justicia! Permite que mis campeones luchen en vez de tus caballeros, Tirion. ¡Retamos a la Horda!' WHERE entry=-1649022; /* content: Our honor has been besmirched! They make wild claims and false accusations against us. I demand justice! Allow my champions to fight in place of your knights, Tirion. We challenge the Horde! */
UPDATE script_texts SET content_loc6='¡Luchad por la gloria de la Alianza, héroes! ¡Honrad a vuestro Rey y a vuestro pueblo!' WHERE entry=-1649024; /* content: Fight for the glory of the Alliance, heroes! Honor your king and your people! */
