UPDATE script_texts SET content_loc6='Aquí llega el pequeño pero mortal Ambrose Chisparrayo, Gran Campeón de Gnomeregan.' WHERE entry=-1650008; /* content: Here comes the small but deadly Ambrose Boltspark, Grand Champion of Gnomeregan! */
UPDATE script_texts SET content_loc6='Colosos, el enorme Gran Campeón de El Exodar, está saliendo por la puerta.' WHERE entry=-1650009; /* content: Coming out of the gate is Colosos, the towering Grand Champion of the Exodar! */
UPDATE script_texts SET content_loc6='Está entrando en la arena la Gran Campeona de Darnassus, la hábil centinela Jaelyne Unicanto.' WHERE entry=-1650010; /* content: Entering the arena is the Grand Champion of Darnassus, the skilled sentinel Jaelyne Evensong! */
UPDATE script_texts SET content_loc6='Hoy el poder de los enanos está representado por la Gran Campeona de Forjaz, Lana Martillotenaz.' WHERE entry=-1650011; /* content: The might of the dwarves is represented today by the Grand Champion of Ironforge, Lana Stouthammer! */
UPDATE script_texts SET content_loc6='Bienvenidos, campeones. Hoy, ante los ojos de vuestros líderes y coetáneos, demostraréis ser dignos luchadores.' WHERE entry=-1650012; /* content: Welcome, champions. Today, before the eyes of your leaders and peers, you will prove yourselves worthy combatants. */
UPDATE script_texts SET content_loc6='¡Primero os enfrentaréis a tres de los Grandes Campeones del torneo! Estos fieros contrincantes han derrotado a todos los demás para alcanzar la cima de la habilidad en las justas.' WHERE entry=-1650013; /* content: You will first be facing three of the Grand Champions of the Tournament! These fierce contenders have beaten out all others to reach the pinnacle of skill in the joust. */
UPDATE script_texts SET content_loc6='¡Luchad bien, miembros de la Horda!¡Lok\'tar Ogar!' WHERE entry=-1650014; /* content: Fight well, Horde! Lok\'tar Ogar! */
UPDATE script_texts SET content_loc6='Por fin una lucha digna de ser vista.' WHERE entry=-1650015; /* content: Finally, a fight worth watching. */
UPDATE script_texts SET content_loc6='No me gustan estos juegos, Tirion... Aun así, estoy seguro de que su actuación será admirable.' WHERE entry=-1650016; /* content: I have no taste for these games, Tirion. Still... I trust they will perform admirably. */
UPDATE script_texts SET content_loc6='¡Adelante!' WHERE entry=-1650017; /* content: Begin! */
UPDATE script_texts SET content_loc6='Los elfos de sangre de Lunargenta animan a $n.' WHERE entry=-1650018; /* content: The blood elves of Silvermoon cheer for $n. */
UPDATE script_texts SET content_loc6='Los trols del Poblado Sen\'jin comienzan un cántico para festejar a $n.' WHERE entry=-1650019; /* content: The trolls of the Sen\'jin Village begin a chant to celebrate $n. */
UPDATE script_texts SET content_loc6='Los tauren de Cima del Trueno animan a $n.' WHERE entry=-1650020; /* content: The tauren of Thunder Bluff cheer for $n. */
UPDATE script_texts SET content_loc6='Los renegados de Entrañas animan a $n.' WHERE entry=-1650021; /* content: The forsaken of the Undercity cheer for $n. */
UPDATE script_texts SET content_loc6='Los orcos de Orgrimmar animan a $n.' WHERE entry=-1650022; /* content: The orcs of Orgrimmar cheer for $n. */
UPDATE script_texts SET content_loc6='Los enanos de Forjaz comienzan a animar a $n.' WHERE entry=-1650023; /* content: The dwarves of Ironforge begin a cheer for $n. */
UPDATE script_texts SET content_loc6='Los gnomos de Gnomeregan animan a $n.' WHERE entry=-1650024; /* content: The gnomes of Gnomeregan cheer for $n. */
UPDATE script_texts SET content_loc6='Los elfos de la noche de Darnassus animan a $n.' WHERE entry=-1650025; /* content: The night elves of Darnassus cheer for $n. */
UPDATE script_texts SET content_loc6='Los humanos de Ventormenta animan a $n.' WHERE entry=-1650026; /* content: The humans of Stormwind cheer for $n. */
UPDATE script_texts SET content_loc6='Los draenei de El Exodar animan a $n.' WHERE entry=-1650027; /* content: The draenei of the Exodar cheer for $n. */
UPDATE script_texts SET content_loc6='¡Bien luchado! Vuestro próximo reto llega de entre las filas de la propia Cruzada. Se os pondrá a prueba contra su considerable destreza.' WHERE entry=-1650028; /* content: Well fought! Your next challenge comes from the Crusade\'s own ranks. You will be tested against their considerable prowess. */
UPDATE script_texts SET content_loc6='¡Podéis comenzar!' WHERE entry=-1650029; /* content: You may begin! */
UPDATE script_texts SET content_loc6='Entrando en la arena, tenemos a un paladín que no es un extraño para los campos de batalla, ni los Campos del Torneo. ¡El gran campeón de la Cruzada Argenta, Eadric el Puro!' WHERE entry=-1650030; /* content: Entering the arena, a paladin who is no stranger to the battlefield or tournament ground, the Grand Champion of the Argent Crusade, Eadric the Pure! */
UPDATE script_texts SET content_loc6='La siguiente combatiente no tiene rival alguno en su pasión al apoyar a la Luz. ¡Os entrego a la confesora Argenta Cabelloclaro!' WHERE entry=-1650031; /* content: The next combatant is second to none in her passion for upholding the Light. I give you Argent Confessor Paletress! */
UPDATE script_texts SET content_loc6='Los espectadores de la Horda animan a $n.' WHERE entry=-1650032; /* content: The Horde spectators cheer for $n. */
UPDATE script_texts SET content_loc6='Los espectadores de la Alianza animan a $n.' WHERE entry=-1650033; /* content: The Alliance spectators cheer for $n. */
UPDATE script_texts SET content_loc6='¿Aceptáis el reto? No hay vuelta atrás.' WHERE entry=-1650034; /* content: Are you up to the challenge? I will not hold back. */
UPDATE script_texts SET content_loc6='Gracias, buen heraldo. Tus palabras son muy amables.' WHERE entry=-1650035; /* content: Thank you, good herald. Your words are too kind. */
UPDATE script_texts SET content_loc6='Que la Luz me dé fuerzas para ser un reto digno.' WHERE entry=-1650036; /* content: May the Light give me strength to provide a worthy challenge. */
UPDATE script_texts SET content_loc6='Bien hecho. Hoy has demostrado algo...' WHERE entry=-1650037; /* content: Well done. You have proven yourself today- */
UPDATE script_texts SET content_loc6='¿Qué es eso que hay cerca de las vigas?' WHERE entry=-1650038; /* content: What\'s that, up near the rafters? */
UPDATE script_texts SET content_loc6='Has estropeado mi gran entrada, rata.' WHERE entry=-1650039; /* content: You spoiled my grand entrance, rat. */
UPDATE script_texts SET content_loc6='¿Qué significa esto?' WHERE entry=-1650040; /* content: What is the meaning of this? */
UPDATE script_texts SET content_loc6='¿Realmente pensabas que derrotarías a un agente del Rey Exánime en los campos de tu patético torneo?' WHERE entry=-1650041; /* content: Did you honestly think an agent of the Lich King would be bested on the field of your pathetic little tournament? */
UPDATE script_texts SET content_loc6='He venido a terminar mi cometido.' WHERE entry=-1650042; /* content: I\'ve come to finish my task. */
UPDATE script_texts SET content_loc6='Mi enhorabuena, campeones. Habéis triunfado a los largo de estas pruebas, tanto planeadas como inesperadas.' WHERE entry=-1650043; /* content: My congratulations, champions. Through trials both planned and unexpected, you have triumphed. */
UPDATE script_texts SET content_loc6='Id y descansad; os lo habéis ganado.' WHERE entry=-1650044; /* content: Go now and rest; you\'ve earned it. */
UPDATE script_texts SET content_loc6='Habéis luchado bien.' WHERE entry=-1650045; /* content: You fought well. */
UPDATE script_texts SET content_loc6='¡Bien hecho, Horda!' WHERE entry=-1650046; /* content: Well done, Horde! */
UPDATE script_texts SET content_loc6='¡Hazlo picadillo!' WHERE entry=-1650047; /* content: Tear him apart! */
UPDATE script_texts SET content_loc6='Ya basta, Garrosh.' WHERE entry=-1650048; /* content: Garrosh, enough. */
UPDATE script_texts SET content_loc6='¿Admirable? ¡Ja! Disfrutaré viendo como fracasan tus debiluchos campeones, humano.' WHERE entry=-1650049; /* content: Admirably? Hah! I will enjoy watching your weak little champions fail, human. */
UPDATE script_texts SET content_loc6='No os quedéis ahí. ¡Matadlo!' WHERE entry=-1650050; /* content: Don\'t just stand there; kill him! */
UPDATE script_texts SET content_loc6='No he venido aquí para ver animales destrozarse unos a otros absurdamente, Tirion.' WHERE entry=-1650051; /* content: I did not come here to watch animals tear at each other senselessly, Tirion. */
UPDATE script_texts SET content_loc6='¡Héroes de la Alianza, al ataque!' WHERE entry=-1658002; /* content: Heroes of the Alliance, attack! */
UPDATE script_texts SET content_loc6='¡Soldados de la Horda, al ataque!' WHERE entry=-1658003; /* content: Soldiers of the Horde, attack! */
UPDATE script_texts SET content_loc6='Ummm, carnaza. Ni siquiera servís para trabajar en la cantera. Saboread estos últimos momentos, pues pronto no seréis más que no-muertos descerebrados.' WHERE entry=-1658004; /* content: Hrmph, fodder. Not even fit to labor in the quarry. Relish these final moments for soon you will be nothing more than mindless undead. */
UPDATE script_texts SET content_loc6='Vuestro último recuerdo en vida será un dolor lleno de agonía.' WHERE entry=-1658005; /* content: Your last waking memory will be of agonizing pain. */
UPDATE script_texts SET content_loc6='¡NO! ¡MONSTRUO!' WHERE entry=-1658006; /* content: No! You monster! */
UPDATE script_texts SET content_loc6='Esbirros, ¡destruid a estos intrusos!' WHERE entry=-1658008; /* content: Minions, destroy these interlopers! */
UPDATE script_texts SET content_loc6='Cumplo con mi deber. Perdonadme, nobles soldados…' WHERE entry=-1658009; /* content: I do what I must. Please forgive me, noble soldiers. */
UPDATE script_texts SET content_loc6='Tendréis que atravesar esa cantera por vuestra cuenta.' WHERE entry=-1658010; /* content: You will have to make your way across this quarry on your own. */
UPDATE script_texts SET content_loc6='Tendréis que atravesar este foso vosotros solos.' WHERE entry=-1658011; /* content: You will have to battle your way through this cesspit on your own. */
UPDATE script_texts SET content_loc6='Liberad a los esclavos de la Alianza que os encontréis. Seguramente necesitemos su ayuda en la batalla contra Tyrannus. Reuniré refuerzos y nos veremos al otro lado de la cantera.' WHERE entry=-1658012; /* content: Free any Alliance slaves that you come across. We will most certainly need their assistance in battling Tyrannus. I will gather reinforcements and join you on the other side of the quarry. */
UPDATE script_texts SET content_loc6='Liberad a los esclavos de la Horda que os encontréis. Seguramente necesitemos su ayuda en la batalla contra Tyrannus. Reuniré refuerzos y nos veremos al otro lado de la cantera.' WHERE entry=-1658013; /* content: Free any Horde slaves that you come across. We will most certainly need their assistance in battling Tyrannus. I will gather reinforcements and join you on the other side of the quarry. */
UPDATE script_texts SET content_loc6='Bichejos a mis pies. ¡Traéis a Gargelus cosas ricas para comer!' WHERE entry=-1658014; /* content: Tiny creatures under feet, you bring Garfrost something good to eat! */
UPDATE script_texts SET content_loc6='Guardaré para comer. Luego.' WHERE entry=-1658015; /* content: Will save for snack. For later. */
UPDATE script_texts SET content_loc6='Este no tan rico para comer ahora. ¡Gargelus tonto! ¡MALO! ¡MALO!' WHERE entry=-1658016; /* content: That one maybe not so good to eat now. Stupid Garfrost! BAD! BAD! */
UPDATE script_texts SET content_loc6='Gargelus espera llevar calzones limpios. Mucha vergüenza si no. Luego.' WHERE entry=-1658017; /* content: Garfrost hope giant underpants clean. Save boss great shame. For later. */
UPDATE script_texts SET content_loc6='Este no tan rico para comer ahora. ¡Gargelus tonto! ¡MALO! ¡MALO!' WHERE entry=-1658019; /* content: That one maybe not so good to eat now. Stupid Garfrost! BAD! BAD! */
UPDATE script_texts SET content_loc6='Otro ocupará su lugar. Perdéis el tiempo.' WHERE entry=-1658020; /* content: Another shall take his place. You waste your time. */
UPDATE script_texts SET content_loc6='El maestro de la forja está muerto. Preparaos, hombres, tenemos a un Señor de la Plaga al que matar.' WHERE entry=-1658021; /* content: The forgemaster is dead! Get geared up men, we have a Scourgelord to kill. */
UPDATE script_texts SET content_loc6='¡%s te lanza un pedrusco de saronita enorme!' WHERE entry=-1658022; /* content: %s hurls a massive saronite boulder at you! */
UPDATE script_texts SET content_loc6='¡Deja de moverte! Quieto mientras los estallo a todos.' WHERE entry=-1658027; /* content: Enough moving around! Hold still while I blow them all up! */
UPDATE script_texts SET content_loc6='¡Rápido! Envenénalos mientras están cerca.' WHERE entry=-1658028; /* content: Quickly! Poison them all while they\'re still close! */
UPDATE script_texts SET content_loc6='¡%s comienza a invocar minas explosivas!' WHERE entry=-1658032; /* content: %s begins rapidly conjuring explosive mines! */
UPDATE script_texts SET content_loc6='¡%s comienza a desatar una nube de veneno tóxica!' WHERE entry=-1658033; /* content: %s begins to unleash a toxic poison cloud! */
UPDATE script_texts SET content_loc6='¡Esperad! ¡Parad! No me matéis, por favor. Os contaré todo.' WHERE entry=-1658035; /* content: Wait! Stop! Don\'t kill me, please! I\'ll tell you everything! */
UPDATE script_texts SET content_loc6='No soy tan ingenua como para creerte cuando pides clemencia, pero te escucharé.' WHERE entry=-1658036; /* content: I\'m not so naive as to believe your appeal for clemency, but I will listen. */
UPDATE script_texts SET content_loc6='¿Por qué debería perdonarte la vida la Reina alma en pena?' WHERE entry=-1658037; /* content: Why should the Banshee Queen spare your miserable life? */
UPDATE script_texts SET content_loc6='¿La Agonía de Escarcha está sin vigilancia? ¡Imposible!' WHERE entry=-1658039; /* content: Frostmourne lies unguarded? Impossible! */
UPDATE script_texts SET content_loc6='¡Os juro que es cierto! Por favor, ¡¡no me matéis!!' WHERE entry=-1658041; /* content: I swear it is true! Please, don\'t kill me!! */
UPDATE script_texts SET content_loc6='¡Bicho asqueroso! ¡La muerte es lo que te espera!' WHERE entry=-1658042; /* content: Worthless gnat! Death is all that awaits you! */
UPDATE script_texts SET content_loc6='Urgh... ¡No!' WHERE entry=-1658043; /* content: Urg... no!! */
UPDATE script_texts SET content_loc6='No creáis que os voy a permitir la entrada al sagrario de mi maestro tan fácilmente. Seguid si osáis.' WHERE entry=-1658044; /* content: Do not think that I shall permit you entry into my master\'s sanctum so easily. Pursue me if you dare. */
UPDATE script_texts SET content_loc6='Qué final tan cruel. Vamos, héroes. Veamos si la historia del gnomo es cierta. Si separamos a Arthas de la Agonía de Escarcha, puede que logremos detenerle.' WHERE entry=-1658045; /* content: What a cruel end. Come, heroes. We must see if the gnome\'s story is true. If we can separate Arthas from Frostmourne, we might have a chance at stopping him. */
UPDATE script_texts SET content_loc6='Un final propio para un traidor. Vamos, debemos liberar a los esclavos y ver qué hay en la estancia del Rey Exánime.' WHERE entry=-1658046; /* content: A fitting end for a traitor. Come, we must free the slaves and see what is within the Lich King\'s chamber for ourselves. */
UPDATE script_texts SET content_loc6='¡Gusanos persistentes! No alcanzaréis la entrada de la guarida de mi señor. ¡Soldados, destruidlos!' WHERE entry=-1658048; /* content: Persistent whelps! You will not reach the entrance of my lord\'s lair! Soldiers, destroy them! */
UPDATE script_texts SET content_loc6='¡Ay! Valientes aventureros, vuestra intromisión ha llegado a su fin. ¿Oís el ruido de huesos y acero acercándose por ese túnel? Es el sonido de vuestra inminente muerte.' WHERE entry=-1658050; /* content: Alas, brave, brave adventurers, your meddling has reached its end. Do you hear the clatter of bone and steel coming up the tunnel behind you? That is the sound of your impending demise. */
UPDATE script_texts SET content_loc6='¡Héroes! Retendremos a los no-muertos cuanto podamos, hasta nuestro aliento final. ¡Ocupaos del Señor de la Plaga!' WHERE entry=-1658051; /* content: Heroes! We will hold off the undead as long as we can, even to our dying breath. Deal with the Scourgelord! */
UPDATE script_texts SET content_loc6='¡Dientefrío, destruye a este insensato!' WHERE entry=-1658057; /* content: Rimefang, destroy this fool! */
UPDATE script_texts SET content_loc6='Poder... ¡sobrecogedor!' WHERE entry=-1658058; /* content: Power... overwhelming! */
UPDATE script_texts SET content_loc6='¡%s gruñe y se hincha con poder oscuro!' WHERE entry=-1658060; /* content: %s roars and swells with dark might! */
UPDATE script_texts SET content_loc6='¡Héroes, venid!' WHERE entry=-1658063; /* content: Heroes, to me! */
UPDATE script_texts SET content_loc6='¡Protegeos detrás de mí! ¡Rápido!' WHERE entry=-1658064; /* content: Take cover behind me! Quickly! */
UPDATE script_texts SET content_loc6='La Reina de Escarcha se ha ido. Debemos seguir adelante. Nuestro objetivo está cerca.' WHERE entry=-1658065; /* content: The Frost Queen is gone. We must keep moving - our objective is near. */
UPDATE script_texts SET content_loc6='No... pude salvarlos... ¡Maldito seas, Arthas! ¡MALDITO SEAS!' WHERE entry=-1658066; /* content: I... I could not save them... Damn you, Arthas! DAMN YOU! */
UPDATE script_texts SET content_loc6='Pensé que nunca se callaría. Por fin, Sindragosa silenció a ese loco impertinente. ¡A las Cámaras de Reflexión, campeones! Nuestro objetivo está cerca... Lo presiento.' WHERE entry=-1658067; /* content: I thought he\'d never shut up. At last, Sindragosa silenced that long-winded fool. To the Halls of Reflection, champions! Our objective is near... I can sense it. */
UPDATE script_texts SET content_loc6='¡Héroes! Retendremos a los no-muertos cuanto podamos, hasta nuestro aliento final. ¡Ocupaos del Señor de la Plaga!' WHERE entry=-1658068; /* content: Heroes! We will hold off the undead as long as we can, even to our dying breath. Deal with the Scourgelord! */
UPDATE script_texts SET content_loc6='Los dragonantes negros aparecieron de la nada y atacaron antes de que pudiésemos reaccionar.' WHERE entry=-1724004; /* content: The Black Dragonkin materialized from thin air, and set upon us before we could react. */
UPDATE script_texts SET content_loc6='Fue una masacre calculada y eficaz, pero su verdadero objetivo eran los huevos que guardamos en el Sagrario.' WHERE entry=-1724006; /* content: They slaughtered us with cold efficiency, but the true focus of their interest seemed to be the eggs kept here in the sanctum. */
UPDATE script_texts SET content_loc6='Ah, ha llegado la diversión.' WHERE entry=-1724010; /* content: Ah, the entertainment has arrived. */
UPDATE script_texts SET content_loc6='¡Baltharus no deja supervivientes!' WHERE entry=-1724011; /* content: Baltharus leaves no survivors! */
UPDATE script_texts SET content_loc6='Ya hay suficientes héroes...' WHERE entry=-1724012; /* content: This world has enough heroes. */
UPDATE script_texts SET content_loc6='El doble de dolor y la mitad de diversión.' WHERE entry=-1724014; /* content: Twice the pain and half the fun. */
UPDATE script_texts SET content_loc6='Halion estará satisfecho.' WHERE entry=-1724017; /* content: Halion will be pleased. */
UPDATE script_texts SET content_loc6='¡Arded en la llama del maestro!' WHERE entry=-1724018; /* content: Burn in the master\'s flame! */
UPDATE script_texts SET content_loc6='¿Pensabas sobrevivir?' WHERE entry=-1724020; /* content: You thought you stood a chance? */
UPDATE script_texts SET content_loc6='Es por tu bien.' WHERE entry=-1724021; /* content: It\'s for the best. */
UPDATE script_texts SET content_loc6='¡Reducidlos a cenizas, esbirros!' WHERE entry=-1724023; /* content: Turn them to ash, minions! */
UPDATE script_texts SET content_loc6='Vuestro mundo está al borde de la aniquilación. ¡TODOS seréis testigos de la llegada de una nueva era de DESTRUCCIÓN!' WHERE entry=-1724025; /* content: Your world teeters on the brink of annihilation. You will ALL bear witness to the coming of a new age of DESTRUCTION! */
UPDATE script_texts SET content_loc6='¡Los cielos arden!' WHERE entry=-1724029; /* content: The heavens burn! */
UPDATE script_texts SET content_loc6='¡Temed la sombra!' WHERE entry=-1724030; /* content: Beware the shadow! */
UPDATE script_texts SET content_loc6='En el reino del crepúsculo solo encontraréis sufrimiento. ¡Entrad si os atrevéis!' WHERE entry=-1724031; /* content: You will find only suffering within the realm of twilight! Enter if you dare! */
UPDATE script_texts SET content_loc6='¡Yo soy la luz y la oscuridad! ¡Temed, mortales, la llegada de Alamuerte!' WHERE entry=-1724032; /* content: I am the light and the darkness! Cower, mortals, before the herald of Deathwing! */
UPDATE script_texts SET content_loc6='¡Tus esfuerzos obligan a %s a abandonar el reino físico!' WHERE entry=-1724035; /* content: Your efforts force %s further out of the physical realm! */
UPDATE script_texts SET content_loc6='¿Adónde iréis? ¿Qué haréis? Tantas opciones para terminar sufriendo y muriendo de todas formas...' WHERE entry=-1999905; /* content: Where to go? What to do? So many choices that all end in pain, end in death. */
UPDATE script_texts SET content_loc6='Ugh...' WHERE entry=-1999918; /* content: ugh... */
UPDATE script_texts SET content_loc6='¡Hola!' WHERE entry=-1999925; /* content: Hi! */

