UPDATE `db_script_string` SET `content_loc7`='Hermanos y hermanas, escuchadme. Tenemos que hablar de la supervivencia de nuestro pueblo. Hemos sobrevivido a una catástrofe, pero me temo que la siguiente acabará por destruirnos.' WHERE `entry`=2000000571;
