UPDATE `db_script_string` SET `content_loc7`='Estoy encontrando restos de partículas de cronitón. Mmm. Si usamos un inductor de fase cambiado mientras invertimos nuestra cronosfera, podremos eliminar las partículas. Pero claro, eso significa que hay que construir una cronosfera...' WHERE `entry`=2000005258;
UPDATE `db_script_string` SET `content_loc7`='Mmm... Parece que la unidad sincotronal que hemos estado utilizando no ha tenido ningún efecto evidente en las distorsiones gravimétricas o gravitatrónicas de este sector.' WHERE `entry`=2000005259;
