UPDATE `db_script_string` SET `content_loc7`='Según esta lecturas, no tenemos nada que temer. Al menos nada en el futuro más próximo. Necesitaremos más datos para estar seguros.' WHERE `entry`=2000005262;
UPDATE `db_script_string` SET `content_loc7`='¿Hay muchos allí arriba?' WHERE `entry`=2000005263;
UPDATE `db_script_string` SET `content_loc7`='¿Los has encontrado?' WHERE `entry`=2000005264;
