UPDATE `db_script_string` SET `content_loc7`='Mira, cariño. Nadie está diciendo que ataquemos El Castillo de la Tempestad solos, pero cuando veamos una oportunidad, tenemos que aprovecharnos.' WHERE `entry`=2000005272;
