UPDATE `db_script_string` SET `content_loc7`='¿Qué significa esto, Mor\'ghor?' WHERE `entry`=2000005348;
UPDATE `db_script_string` SET `content_loc7`='¡Silencio!' WHERE `entry`=2000005349;
UPDATE `db_script_string` SET `content_loc7`='Idiota charlatán. Bufón incomprensiblemente incompetente...' WHERE `entry`=2000005350;
UPDATE `db_script_string` SET `content_loc7`='¿ESTO es tu héroe?' WHERE `entry`=2000005351;
