UPDATE `db_script_string` SET `content_loc7`='El señor de fatalidad Kazzak estará satisfecho. Vas a aumentar el ritmo de tus ataques. Destruid a los orcos y los bastiones de los enanos se precipitarán.' WHERE `entry`=2000005419;
UPDATE `db_script_string` SET `content_loc7`='Belisario, eso requerirá el uso de todos los infernales del bastión. Puede que nos deje vulnerables ante un contraataque.' WHERE `entry`=2000005420;
UPDATE `db_script_string` SET `content_loc7`='No te preocupes por eso. He aumentado la producción en la Forja Muerta. Tendrás a todos los infernales que quieras para que sigan tus órdenes. No fracases, Jovaan.' WHERE `entry`=2000005421;
UPDATE `db_script_string` SET `content_loc7`='Se hará como dices, belisario. Una última pregunta, si se me permite...' WHERE `entry`=2000005422;
UPDATE `db_script_string` SET `content_loc7`='¿Pero?' WHERE `entry`=2000005423;
UPDATE `db_script_string` SET `content_loc7`='¿Qué hay en la caja?' WHERE `entry`=2000005424;
UPDATE `db_script_string` SET `content_loc7`='¿Caja? No te envié una caja, Jovaan. ¿No tienes cosas más importantes de las que preocuparte? ¡Ve a ocuparte de ellas!' WHERE `entry`=2000005425;
UPDATE `db_script_string` SET `content_loc7`='Estoy harto de pedir las cosas con cortesía, hombre pez.' WHERE `entry`=2000005426;
UPDATE `db_script_string` SET `content_loc7`='¡¿Dónde está la Clave de Condenación?! Sé que un tercio está en este pueblo.' WHERE `entry`=2000005427;
UPDATE `db_script_string` SET `content_loc7`='¿De qué me hablasss, piel ssuave?' WHERE `entry`=2000005428;
UPDATE `db_script_string` SET `content_loc7`='Los nagasss no tienen ninguna cccclave...' WHERE `entry`=2000005429;
UPDATE `db_script_string` SET `content_loc7`='No... Claro que no. ¿En qué estaría pensando? ¿Para qué quieren ustedes un tercio de la Clave de Condenación?' WHERE `entry`=2000005430;
UPDATE `db_script_string` SET `content_loc7`='Qué tonto. Quizás debería soltarte y dejarte marchar...' WHERE `entry`=2000005431;
UPDATE `db_script_string` SET `content_loc7`='Sssí..., sssí... Déjame marcharrr...' WHERE `entry`=2000005432;
UPDATE `db_script_string` SET `content_loc7`='¡¿DE VERDAD CREES QUE SOY TAN ESTÚPIDO, NAGA?! ¡SUFRE!' WHERE `entry`=2000005433;
UPDATE `db_script_string` SET `content_loc7`='%s escupe sobre el naga Cicatriz Espiral.' WHERE `entry`=2000005434;
UPDATE `db_script_string` SET `content_loc7`='¡LEVÁNTATE, BASURA INMUNDA! ¡LEVÁNTATE ANTES DE QUE TE MATE!' WHERE `entry`=2000005435;
UPDATE `db_script_string` SET `content_loc7`='%s recupera la conciencia.' WHERE `entry`=2000005436;
UPDATE `db_script_string` SET `content_loc7`='¡No vuelvas a pegarle al comandante Cicatriz Essspiral, orco! ¡Te lo diré! La clave... essstá essscondido en el Alto Cicatriz Espiral. Los nagasss de allí lo guardan bajo llave...' WHERE `entry`=2000005438;
UPDATE `db_script_string` SET `content_loc7`='¡CICATRIZ ESSSPIRAL!' WHERE `entry`=2000005439;
UPDATE `db_script_string` SET `content_loc7`='Una voz tenebrosa y profunda surge de %s...' WHERE `entry`=2000005440;
UPDATE `db_script_string` SET `content_loc7`='¿Qué es lo que quieres de mí, chamán?' WHERE `entry`=2000005443;
UPDATE `db_script_string` SET `content_loc7`='¿Acaso no tienes ojos? ¿No has visto a los demonios en nuestro mismo umbral? ¡No puedo prescindir de ningún soldado!' WHERE `entry`=2000005444;
UPDATE `db_script_string` SET `content_loc7`='Reza a tus dioses, chamán. Quizás vengan en tu ayuda. Sin embargo, la Horda no puede...' WHERE `entry`=2000005445;
UPDATE `db_script_string` SET `content_loc7`='¡Silencio! ¡Ahora llega la destrucción!' WHERE `entry`=2000005446;
UPDATE `db_script_string` SET `content_loc7`='¡Contempla el innegable poder de nuestro oscuro amo!' WHERE `entry`=2000005447;
UPDATE `db_script_string` SET `content_loc7`='¡Con este don, arrasaré la tierra para recrearla!' WHERE `entry`=2000005448;
UPDATE `db_script_string` SET `content_loc7`='¡Mira! ¡Mira cómo se abre el suelo ante nosotros! ¡Mira cómo fluye la energía! Alimentará a nuestro ejército...' WHERE `entry`=2000005449;
UPDATE `db_script_string` SET `content_loc7`='¡Nunca nos faltará poder! He asegurado nuestro futuro. ¡Someteos a mi gloria!' WHERE `entry`=2000005450;
UPDATE `db_script_string` SET `content_loc7`='Ya... está... hecho...' WHERE `entry`=2000005451;
UPDATE `db_script_string` SET `content_loc7`='¡Parte la tierra, Gul\'dan! ¡Corta el vínculo!' WHERE `entry`=2000005452;
UPDATE `db_script_string` SET `content_loc7`='¡No lo hagas, Gul\'dan! ¡Los elementos no nos perdonarán nunca!' WHERE `entry`=2000005453;
UPDATE `db_script_string` SET `content_loc7`='¡Nos han abandonado! ¡No olvides!' WHERE `entry`=2000005455;
UPDATE `db_script_string` SET `content_loc7`='¡Tomaremos por la fuerza lo que la tierra nos niegue!' WHERE `entry`=2000005456;
UPDATE `db_script_string` SET `content_loc7`='¡Para! ¡Detente de una vez!' WHERE `entry`=2000005457;
UPDATE `db_script_string` SET `content_loc7`='Nos has condenado a todos... Estamos perdidos.' WHERE `entry`=2000005458;
UPDATE `db_script_string` SET `content_loc7`='¡Si quieres la llave tendrás que cogerme!' WHERE `entry`=2000005460;
UPDATE `db_script_string` SET `content_loc7`='¡Cógeme si puedes! ¡Corro tan rápido como el viento!' WHERE `entry`=2000005461;
UPDATE `db_script_string` SET `content_loc7`='¿Por qué ibas a querer hacerme daño? ¡Vamos! ¡Podemos ser amigos!' WHERE `entry`=2000005462;
UPDATE `db_script_string` SET `content_loc7`='¿MORIR? ¡Estás haciendo llorar a Pusillín!' WHERE `entry`=2000005463;
UPDATE `db_script_string` SET `content_loc7`='¡Saluda a mis amiguitos!' WHERE `entry`=2000005464;
UPDATE `db_script_string` SET `content_loc7`='¡A sus órdenes, señor!' WHERE `entry`=2000005465;
UPDATE `db_script_string` SET `content_loc7`='Danos la fuerza de los gigantes de Sombraluna, Corok. El maestro es un gobernante justo. Se asegurará de cuidar de aquellos que ayudaron a dominar a los disidentes de este mundo.' WHERE `entry`=2000005466;
UPDATE `db_script_string` SET `content_loc7`='Entonces os convertiréis en enemigos de mi señor, Illidan. Los enemigos de Illidan son destruidos. ¿Son capaces tus gigantes de resistir un ataque de las tropas de la Punta Eclipse?' WHERE `entry`=2000005468;
UPDATE `db_script_string` SET `content_loc7`='No... no lo pensaba...' WHERE `entry`=2000005469;
UPDATE `db_script_string` SET `content_loc7`='Volveremos a encontrarnos... Algún día.' WHERE `entry`=2000005471;
UPDATE `db_script_string` SET `content_loc7`='¡La encarnación de la muerte ha sido liberada una vez más sobre el mundo! Teron Sanguino ha vuelto...' WHERE `entry`=2000005472;
UPDATE `db_script_string` SET `content_loc7`='No estaré mucho tiempo en esta celda. Tendré la cabeza de Illidan de una forma u otra.' WHERE `entry`=2000005475;
UPDATE `db_script_string` SET `content_loc7`='¡Considéralo un calentamiento! ¡Tu entrenamiento real será mucho más duro!' WHERE `entry`=2000005476;
UPDATE `db_script_string` SET `content_loc7`='Con una recompensa como esa, ¿cómo podrían decir que no?' WHERE `entry`=2000005477;
UPDATE `db_script_string` SET `content_loc7`='¡Escóndete! ¡Escóndete antes de que te vean! ¡Arriba!' WHERE `entry`=2000005478;
UPDATE `db_script_string` SET `content_loc7`='¡Alto!' WHERE `entry`=2000005479;
UPDATE `db_script_string` SET `content_loc7`='¿Qué sucede, mi señor?' WHERE `entry`=2000005480;
UPDATE `db_script_string` SET `content_loc7`='¡Zarath, debes volver al Templo Oscuro de inmediato! Pa... parece que he extraviado las órdenes de Lord Illidan. ¡Rápido!' WHERE `entry`=2000005481;
UPDATE `db_script_string` SET `content_loc7`='Mi señor, sin duda no esperas que te deje desatendido. Lord Illidan me cortaría la cabeza si te ocurriera algo.' WHERE `entry`=2000005482;
UPDATE `db_script_string` SET `content_loc7`='Zarath, soy perfectamente capaz de subir este camino hasta Punta Eclipse. ¡Si no entregamos la misiva, Lord Illidan nos cortará la cabeza a los dos! ¡Puedes marcharte!' WHERE `entry`=2000005483;
UPDATE `db_script_string` SET `content_loc7`='Como desee, mi señor.' WHERE `entry`=2000005484;
UPDATE `db_script_string` SET `content_loc7`='Ah, dulce cardo de sangre... Probablemente olvidado por uno de esos mugrientos adictos de Punta Eclipse.' WHERE `entry`=2000005485;
UPDATE `db_script_string` SET `content_loc7`='Su pérdida es, sin duda, mi ganancia...' WHERE `entry`=2000005486;
UPDATE `db_script_string` SET `content_loc7`='El momento de atacar se acerca. Acabad con Icarius.' WHERE `entry`=2000005487;
UPDATE `db_script_string` SET `content_loc7`='%s se mete el fardo de cardos de sangre en la toga.' WHERE `entry`=2000005488;
UPDATE `db_script_string` SET `content_loc7`='Atrás, $N. Los espíritus pueden atacar.' WHERE `entry`=2000005489;
UPDATE `db_script_string` SET `content_loc7`='¿Por qué nos has capturado?' WHERE `entry`=2000005490;
UPDATE `db_script_string` SET `content_loc7`='¿Qué esperas obtener de esto?' WHERE `entry`=2000005491;
UPDATE `db_script_string` SET `content_loc7`='%s se inclina.' WHERE `entry`=2000005492;
UPDATE `db_script_string` SET `content_loc7`='Queremos ayudarte, espíritu. Buscamos tu sabiduría.' WHERE `entry`=2000005493;
UPDATE `db_script_string` SET `content_loc7`='¿Sabiduría? Tu pueblo fue el que provocó la terrible ira de Cyrukh.' WHERE `entry`=2000005494;
UPDATE `db_script_string` SET `content_loc7`='¿Por qué deberíamos ayudarte? El mundo está arrasado. Destruido.' WHERE `entry`=2000005495;
UPDATE `db_script_string` SET `content_loc7`='%s asiente.' WHERE `entry`=2000005496;
UPDATE `db_script_string` SET `content_loc7`='Tienes razón, espíritu. Mi pueblo os hizo esto. Déjanos enmendar nuestros errores.' WHERE `entry`=2000005497;
UPDATE `db_script_string` SET `content_loc7`='Solo dinos esto: ¿qué o quién es Cyrukh?' WHERE `entry`=2000005498;
UPDATE `db_script_string` SET `content_loc7`='¡Insensato! ¡Cyrukh, el señor de Fuego! Cuando se rompió el vínculo, Cyrukh despertó. Son las lágrimas de Cyrukh las que llenan este mundo de roca fundida.' WHERE `entry`=2000005499;
UPDATE `db_script_string` SET `content_loc7`='El... ¿El Señor del Fuego? ¿Despertó a un señor elemental? Claro... ¡Maldito Gul\'dan!' WHERE `entry`=2000005500;
UPDATE `db_script_string` SET `content_loc7`='Ahora ha perdido la razón. Se volvió loco durante la rotura. No hay descanso para los elementos.' WHERE `entry`=2000005501;
UPDATE `db_script_string` SET `content_loc7`='Tiene que haber una forma.' WHERE `entry`=2000005502;
UPDATE `db_script_string` SET `content_loc7`='Sí, una forma. Destruir a Cyrukh. Destruir a un señor elemental.' WHERE `entry`=2000005503;
UPDATE `db_script_string` SET `content_loc7`='No dudes de mis camaradas ni de mí, espíritu. Dinos cómo invocar al señor elemental. Mis aliados y yo lo destruiremos.' WHERE `entry`=2000005504;
UPDATE `db_script_string` SET `content_loc7`='Debéis pronunciar las palabras...' WHERE `entry`=2000005505;
UPDATE `db_script_string` SET `content_loc7`='Sí... Sí..., las palabras. Las palabras de la maldición. Las palabras pronunciadas por el oscuro, tu maestro.' WHERE `entry`=2000005506;
UPDATE `db_script_string` SET `content_loc7`='Sí... Gul\'dan...' WHERE `entry`=2000005507;
UPDATE `db_script_string` SET `content_loc7`='Encontraremos esas palabras. Invocaremos a tu maestro y lo destruiremos.' WHERE `entry`=2000005508;
UPDATE `db_script_string` SET `content_loc7`='Hablas. Solo hablas... Ahora libéranos, mortal.' WHERE `entry`=2000005509;
UPDATE `db_script_string` SET `content_loc7`='Ahora, a recuperar las palabras perdidas.' WHERE `entry`=2000005510;
UPDATE `db_script_string` SET `content_loc7`='¿... crear un fondo para la compra de gomina? Me gusta mi pelo tal y como está, gracias.' WHERE `entry`=2000005511;
UPDATE `db_script_string` SET `content_loc7`='¿... como desertor, disfrutarás de actividades como nadar atado y cuidar perros? ¡Qué ridículo!' WHERE `entry`=2000005512;
UPDATE `db_script_string` SET `content_loc7`='Nunca me uniría a nada así. Mejor guardar esto. Puede ser de utilidad en el baño...' WHERE `entry`=2000005513;
UPDATE `db_script_string` SET `content_loc7`='¿... los desertores buscan un Theramore donde los ciudadanos puedan pasar el día haciendo malabarismos? ¿Qué es esta tontería?' WHERE `entry`=2000005514;
UPDATE `db_script_string` SET `content_loc7`='Esto es lo más divertido que he leído en mucho tiempo. ¡Me has alegrado el día!' WHERE `entry`=2000005515;
UPDATE `db_script_string` SET `content_loc7`='Esto... esto es una broma, ¿verdad?' WHERE `entry`=2000005516;
UPDATE `db_script_string` SET `content_loc7`='Hey, gracias.' WHERE `entry`=2000005517;
UPDATE `db_script_string` SET `content_loc7`='%s lee el panfleto.' WHERE `entry`=2000005518;
UPDATE `db_script_string` SET `content_loc7`='¿... recibe el 50 por ciento de descuento en ropa interior de desertores? ¿Qué es esta basura?' WHERE `entry`=2000005519;
UPDATE `db_script_string` SET `content_loc7`='¡Menuda panda de lunáticos! ¿De verdad os creéis esto?' WHERE `entry`=2000005520;
UPDATE `db_script_string` SET `content_loc7`='¡Gracias, $R! Tus servicios a los Mag\'har no se olvidarán.' WHERE `entry`=2000005521;
UPDATE `db_script_string` SET `content_loc7`='¡Muchas gracias, héroe!' WHERE `entry`=2000005522;
UPDATE `db_script_string` SET `content_loc7`='¡Muerte a los ogros!' WHERE `entry`=2000005524;
UPDATE `db_script_string` SET `content_loc7`='Intrusos: su victoria será efímera. Soy el Comandante Varos Cloudstrider. Mis dracos controlan los cielos y protegen este conducto. ¡Me encargaré personalmente de que el Óculo no caiga en sus manos!' WHERE `entry`=2000005526;
UPDATE `db_script_string` SET `content_loc7`='El tramposo Señor de la magia Urom protege el tercer anillo. Aparecerá solo e indefenso, ¡pero que no te engañen las apariencias! Urom es un poderoso conjurador que dirige un grupo de criaturas fantasmales. Búscale arriba.' WHERE `entry`=2000005527;
UPDATE `db_script_string` SET `content_loc7`='El mayor de tus desafíos aún está por llegar. El Guardián-Ley Eregos es un dragón Azul de inmenso poder. Lo encontrarás sobrevolando el anillo superior.' WHERE `entry`=2000005528;
UPDATE `db_script_string` SET `content_loc7`='Solo aquellos que se alimentan de almas perdidas conocen el hambre.' WHERE `entry`=2000005529;
UPDATE `db_script_string` SET `content_loc7`='Contempla el vacío. Esa es la perpetuidad en la que moran.' WHERE `entry`=2000005530;
UPDATE `db_script_string` SET `content_loc7`='Se beben tu temor: es la sangre de la vida.' WHERE `entry`=2000005531;
UPDATE `db_script_string` SET `content_loc7`='Son el susurro del escalofriante viento.' WHERE `entry`=2000005532;
UPDATE `db_script_string` SET `content_loc7`='No mueren, no viven. Están fuera del ciclo.' WHERE `entry`=2000005533;
UPDATE `db_script_string` SET `content_loc7`='Donde uno cae, muchos podrán ocupar su puesto.' WHERE `entry`=2000005534;
UPDATE `db_script_string` SET `content_loc7`='¡Gracias! No podía aguantar mucho más... Ha ocurrido algo terrible en este lugar.' WHERE `entry`=2000005535;
UPDATE `db_script_string` SET `content_loc7`='Creíamos que el Sagrario estaba bien fortificado, pero no estábamos preparados para este tipo de asalto.' WHERE `entry`=2000005536;
UPDATE `db_script_string` SET `content_loc7`='El Dragonante Negro se materializó de la nada y nos embistió antes de que pudiéramos reaccionar.' WHERE `entry`=2000005537;
UPDATE `db_script_string` SET `content_loc7`='Era nuestro fin. Con mis hermanos pereciendo a mi alrededor, logré la retirada hasta aquí, donde barriqué la entrada.' WHERE `entry`=2000005538;
UPDATE `db_script_string` SET `content_loc7`='Nos masacraron con una eficacia fría, pero parecían más interesados en los huevos guardados aquí, en el Sagrario.' WHERE `entry`=2000005539;
UPDATE `db_script_string` SET `content_loc7`='El comandante de las fuerzas terrestres es un salvaje cruel llamado Zarithrian, pero me temo que hay fuerzas superiores de por medio.' WHERE `entry`=2000005540;
UPDATE `db_script_string` SET `content_loc7`='En su asalto inicial, pude ver fugazmente a su verdadero líder, un temible dragón crepuscular.' WHERE `entry`=2000005541;
UPDATE `db_script_string` SET `content_loc7`='Desconozco sus verdaderos planes, héroes, pero sí sé esto: ¡no hay que permitirles que los lleven a cabo!' WHERE `entry`=2000005542;
UPDATE `db_script_string` SET `content_loc7`='Gracias. Nunca debí abandonar el Bosque de Argénteos.' WHERE `entry`=2000005543;
UPDATE `db_script_string` SET `content_loc7`='¡Te lo agradezco!' WHERE `entry`=2000005544;
UPDATE `db_script_string` SET `content_loc7`='¡Vale, allá voy!' WHERE `entry`=2000005548;
UPDATE `db_script_string` SET `content_loc7`='¿Después de todo lo que hemos pasado juntos? ¡Si al fin y al cabo no me gustabas!' WHERE `entry`=2000005550;
UPDATE `db_script_string` SET `content_loc7`='¡Yo me largo!' WHERE `entry`=2000005551;
UPDATE `db_script_string` SET `content_loc7`='¡Venga, en marcha!' WHERE `entry`=2000005553;
UPDATE `db_script_string` SET `content_loc7`='Cuchicheos... pétalos de fuego... Cuchicheos...' WHERE `entry`=2000005554;
UPDATE `db_script_string` SET `content_loc7`='Mmm... Mmm...' WHERE `entry`=2000005555;
UPDATE `db_script_string` SET `content_loc7`='... quizás... con la mezcla correcta...' WHERE `entry`=2000005556;
UPDATE `db_script_string` SET `content_loc7`='... vides con espinas... murmura... ¡Auuu!' WHERE `entry`=2000005557;
UPDATE `db_script_string` SET `content_loc7`='¡Vámonos, son demasiados! ¡Escapad mientras podéis!' WHERE `entry`=2000005558;
UPDATE `db_script_string` SET `content_loc7`='¡Ayuda! ¡Que alguien nos ayude!' WHERE `entry`=2000005559;
UPDATE `db_script_string` SET `content_loc7`='Nuestros amigos lucharon bien, Brann, pero nosotros no hemos terminado todavía.' WHERE `entry`=2000005560;
UPDATE `db_script_string` SET `content_loc7`='Puede, pero es solo cuestión de tiempo que volvamos a irrumpir en Ulduar. ¿Ha habido suerte al buscar un teletransportador?' WHERE `entry`=2000005561;
UPDATE `db_script_string` SET `content_loc7`='En absoluto. Sospecho que tiene algo que ver con el gigantesco ensamblaje mecánico que nuestros exploradores divisaron frente a la puerta.' WHERE `entry`=2000005562;
UPDATE `db_script_string` SET `content_loc7`='Bueno. Así que, al fin y al cabo, ¿tendremos que enfrentarnos a esa cosa?' WHERE `entry`=2000005563;
UPDATE `db_script_string` SET `content_loc7`='¿Qué pasa con ese protodraco blindado y con el gigante de fuego que avistamos cerca? ¿Crees que tus magos pueden encargarse de ellos?' WHERE `entry`=2000005564;
UPDATE `db_script_string` SET `content_loc7`='Al Kirin Tor no le resulta posible dedicar ningún recurso adicional a enfrentarse a nada de semejante tamaño. Aunque puede que no sea necesario.' WHERE `entry`=2000005565;
UPDATE `db_script_string` SET `content_loc7`='Podemos pasar de largo sin que nos vean. Mientras podamos abatir el ensamblaje frente a la puerta, debiéramos ser capaces de entrar.' WHERE `entry`=2000005566;
UPDATE `db_script_string` SET `content_loc7`='¿¡Escabullirnos!? ¿Qué crees que somos, marmotas?' WHERE `entry`=2000005567;
UPDATE `db_script_string` SET `content_loc7`='Estamos dando caza a un dios antiguo, Brann.' WHERE `entry`=2000005568;
UPDATE `db_script_string` SET `content_loc7`='Bien. Si nuestros aliados son los que van a ensuciarse las manos, dejaremos que sean ellos quienes decidan cómo proceder.' WHERE `entry`=2000005569;
UPDATE `db_script_string` SET `content_loc7`='¡¿Qué... Qué has hecho, $N?! ¡Brann! ¡Braaaaaannnn!' WHERE `entry`=2000005570;
UPDATE `db_script_string` SET `content_loc7`='¡Brann! ¡$N acaba de activar el sistema de defensa orbital! ¡Si no nos largamos pronto de aquí, estamos fritos!' WHERE `entry`=2000005571;
UPDATE `db_script_string` SET `content_loc7`='¡Magos del Kirin Tor, cuando Brann dé la orden, abran el escudo! ¡Defiendan esta plataforma y nuestros aliados con sus vidas! ¡Por Dalaran! ¡Por Dalaran!' WHERE `entry`=2000005575;
UPDATE `db_script_string` SET `content_loc7`='Nuestros aliados están preparados. ¡Desactiven el escudo y abran paso!' WHERE `entry`=2000005576;
UPDATE `db_script_string` SET `content_loc7`='¡Han vencido al Consejo Férreo y abierto el Archivum! ¡Muy bien, muchachos!' WHERE `entry`=2000005577;
UPDATE `db_script_string` SET `content_loc7`='¡Lo logramos! Llegamos antes que Algalon. Tal vez podamos alterar el sistema para interferir en su análisis…' WHERE `entry`=2000005578;
UPDATE `db_script_string` SET `content_loc7`='Volveré al Archivum y trataré de interferir su señal. Tal vez nos de algo de tiempo mientras ustedes se ocupan de él.' WHERE `entry`=2000005579;
UPDATE `db_script_string` SET `content_loc7`='¡Oye! ¡Deja de merodear por mi sótano! ¡Lárgate!' WHERE `entry`=2000005580;
UPDATE `db_script_string` SET `content_loc7`='¿Qué estabas haciendo en mi sótano? ¡Hay problemas con la comida y lo último que necesito son extraños enredando en mis bienes! ¡Fuera!' WHERE `entry`=2000005581;
UPDATE `db_script_string` SET `content_loc7`='Toda esta situación resulta un poco paranoica, ¿no te parece?' WHERE `entry`=2000005582;
UPDATE `db_script_string` SET `content_loc7`='Las órdenes son las órdenes. Si el Príncipe dice salta...' WHERE `entry`=2000005583;
UPDATE `db_script_string` SET `content_loc7`='No puedes negar que es una orden rara. ¿Comida sospechosa? ¡Basándote en esa definición, deberías arrestar a Belfast!' WHERE `entry`=2000005584;
UPDATE `db_script_string` SET `content_loc7`='¡TE HE OÍDO! ¡Se acabó la cerveza para ti! ¡Ni una gota más!' WHERE `entry`=2000005585;
UPDATE `db_script_string` SET `content_loc7`='Ya basta, Michael. El negocio ya está sufriendo bastante con este susto. Podemos usar hasta la última moneda de cobre.' WHERE `entry`=2000005586;
UPDATE `db_script_string` SET `content_loc7`='Los soldados están realizando un gran trabajo. Mal, si lo que te interesa es que tus clientes vivan otro día más, la seguridad del pueblo es lo más importante.' WHERE `entry`=2000005587;
UPDATE `db_script_string` SET `content_loc7`='%s asiente a regañadientes.' WHERE `entry`=2000005588;
UPDATE `db_script_string` SET `content_loc7`='Eso no lo puedo discutir.' WHERE `entry`=2000005589;
UPDATE `db_script_string` SET `content_loc7`='No te preocupes demasiado. Para cuando terminé la guardia no habíamos encontrado ni un poquito de ese grano.' WHERE `entry`=2000005590;
UPDATE `db_script_string` SET `content_loc7`='Gracias a la Luz.' WHERE `entry`=2000005591;
UPDATE `db_script_string` SET `content_loc7`='%s asiente.' WHERE `entry`=2000005592;
UPDATE `db_script_string` SET `content_loc7`='¡Hombre! ¡Hola de nuevo!' WHERE `entry`=2000005593;
UPDATE `db_script_string` SET `content_loc7`='Vale, basta de trabajo por el momento. Es hora de tomar unos refrescos y charlar un rato en la posada.' WHERE `entry`=2000005594;
UPDATE `db_script_string` SET `content_loc7`='Espera, ¿qué es ese olor?' WHERE `entry`=2000005595;
