UPDATE creature_ai_texts SET content_loc5='我會讓你痛苦的死去。' WHERE entry=-571; /* content: Your death will be a painful one. */
UPDATE creature_ai_texts SET content_loc5='你...被標記了。' WHERE entry=-572; /* content: You... are marked. */
