UPDATE db_script_string SET content_loc5='成為團體中首位擁有電荷污漬破壞器的人吧!' WHERE entry=2000005206;
UPDATE db_script_string SET content_loc5='酒漬，草漬，連寵物污漬都不是電荷污漬破壞器的對手!' WHERE entry=2000005208;
UPDATE db_script_string SET content_loc5='別再遲疑了!現在就下訂購買我的電荷污漬破壞器，不需要額外費用喔!' WHERE entry=2000005209;
UPDATE db_script_string SET content_loc5='它永遠不會停止。永遠...' WHERE entry=2000005210;
UPDATE db_script_string SET content_loc5='嗨!你喜歡沙司嗎?沙司很美味，不是嗎?' WHERE entry=2000005214;
UPDATE db_script_string SET content_loc5='什麼?哦，灰燼使者?沒有聽說過...什麼?別煩我!' WHERE entry=2000005215;
UPDATE db_script_string SET content_loc5='嘿，誰來幫我抓住這個東西!' WHERE entry=2000005218;
UPDATE db_script_string SET content_loc5='那應該會找到泰拉需要的幫助!' WHERE entry=2000005219;
UPDATE db_script_string SET content_loc5='別忘記買點飲料和紀念品!' WHERE entry=2000005223;
UPDATE db_script_string SET content_loc5='我該服下那透明小藥丸的。' WHERE entry=2000005228;
UPDATE db_script_string SET content_loc5='做這種事情才得到這麼點報酬，虧大了!' WHERE entry=2000005229;
UPDATE db_script_string SET content_loc5='你等得越久，我所能告訴關於你未來的資訊就越少。' WHERE entry=2000005232;
UPDATE db_script_string SET content_loc5='我可負擔不起這麼高的稅金!' WHERE entry=2000005234;
UPDATE db_script_string SET content_loc5='嗯嗯...我懷疑...' WHERE entry=2000005242;
UPDATE db_script_string SET content_loc5='那麼，東西在哪?' WHERE entry=2000005243;
UPDATE db_script_string SET content_loc5='笨法師...老是用完最後一個材料又不補充。' WHERE entry=2000005244;
UPDATE db_script_string SET content_loc5='深謀遠慮並不代表要給敵人不必要的優勢。' WHERE entry=2000005246;
UPDATE db_script_string SET content_loc5='我所有的背包都打包好了，我要走了。' WHERE entry=2000005248;
UPDATE db_script_string SET content_loc5='為何不?家庭會給你力量。' WHERE entry=2000005253;
UPDATE db_script_string SET content_loc5='這不是個適合撫養你孩子的地方。這裡很冷，而且又沒有人會撫養他們。' WHERE entry=2000005254;
UPDATE db_script_string SET content_loc5='你不能撫養他們嗎?' WHERE entry=2000005255;
UPDATE db_script_string SET content_loc5='喔不，不，不...我是個火箭人。' WHERE entry=2000005256;
UPDATE db_script_string SET content_loc5='我會來審判。' WHERE entry=2000005261;
UPDATE db_script_string SET content_loc5='那裡，有很多他們的人嗎?' WHERE entry=2000005263;
UPDATE db_script_string SET content_loc5='你找到他們了嗎?' WHERE entry=2000005264;
UPDATE db_script_string SET content_loc5='前面有巨龍教團的人嗎?' WHERE entry=2000005265;
UPDATE db_script_string SET content_loc5='%s發出低沉，平靜的嗥叫，順著它的肩膀回頭望。' WHERE entry=2000005266;
UPDATE db_script_string SET content_loc5='能為那些不幸的巨龍教團鄙惡者做些什麼事?' WHERE entry=2000005267;
UPDATE db_script_string SET content_loc5='巨龍教團如何獲得那麼多的跟隨者?他們從哪來的?!' WHERE entry=2000005268;
UPDATE db_script_string SET content_loc5='巨龍教團一定正使用另一條道路。我們仍然可在魯安曠野找到他們。' WHERE entry=2000005269;
UPDATE db_script_string SET content_loc5='我們要在這裡等待。他們派其它人先行檢查隧道內部。' WHERE entry=2000005270;
UPDATE db_script_string SET content_loc5='我們固然尊重你方意見，阿達歐派我們來這裡調查凱爾薩斯的活動，而不是要發起一次大規模攻擊。' WHERE entry=2000005271;
UPDATE db_script_string SET content_loc5='...看看那讓我們落得什麼下場!' WHERE entry=2000005278;
UPDATE db_script_string SET content_loc5='大傢伙終於開始行動了!' WHERE entry=2000005280;
UPDATE db_script_string SET content_loc5='現在可以安全通過那個鬼東西了嗎?' WHERE entry=2000005283;
UPDATE db_script_string SET content_loc5='那可不一定。' WHERE entry=2000005284;
UPDATE db_script_string SET content_loc5='你可以不要再問了嗎?' WHERE entry=2000005285;
UPDATE db_script_string SET content_loc5='好吧，看起來安全了。進去吧，工程師!' WHERE entry=2000005286;
UPDATE db_script_string SET content_loc5='什麼都沒發生!' WHERE entry=2000005287;
UPDATE db_script_string SET content_loc5='這會名留青史...' WHERE entry=2000005288;
UPDATE db_script_string SET content_loc5='%s用手摸摸他的身體確認一切都完好無缺。' WHERE entry=2000005289;
UPDATE db_script_string SET content_loc5='我成功了!我成功了!' WHERE entry=2000005290;
UPDATE db_script_string SET content_loc5='%s不贊同地搖搖他的頭。' WHERE entry=2000005291;
UPDATE db_script_string SET content_loc5='反正我也不喜歡他 。說的很多，「做」的卻不夠。' WHERE entry=2000005292;
UPDATE db_script_string SET content_loc5='幹得好你這無能的智障!我們又一個工程師被蒸發了!' WHERE entry=2000005293;
UPDATE db_script_string SET content_loc5='來看看派瑞·卡特勒的諧劇風格，還有幾分鐘就開始!' WHERE entry=2000005302;
UPDATE db_script_string SET content_loc5='如果你們準備好大笑一場，就前往世界的盡頭小酒館!' WHERE entry=2000005303;
UPDATE db_script_string SET content_loc5='現在，牛頭大佬出現在撒塔斯城!每小時整點，在世界的盡頭小酒館，現場演出!' WHERE entry=2000005304;
UPDATE db_script_string SET content_loc5='你說布洛葛可以待在這裡的!你答應過布洛葛要奪回石槌氏族的領地!' WHERE entry=2000005306;
UPDATE db_script_string SET content_loc5='嗯...我的魚竿在哪兒呢?哦，找到了。' WHERE entry=2000005307;
UPDATE db_script_string SET content_loc5='來看看魚兒有沒有在咬餌。' WHERE entry=2000005308;
UPDATE db_script_string SET content_loc5='嗯...我覺得我需要更好的誘餌。我得去我父親的貨車中找找。' WHERE entry=2000005309;
UPDATE db_script_string SET content_loc5='野豬人肉片!這個應該可以。魚喜歡野豬人的味道。' WHERE entry=2000005310;
UPDATE db_script_string SET content_loc5='抓到一條了!不知道有沒有守衛快要餓扁了。快來吧，魚兒!' WHERE entry=2000005311;
UPDATE db_script_string SET content_loc5='你餓了嗎?我抓了一條魚...' WHERE entry=2000005312;
UPDATE db_script_string SET content_loc5='幹得好!你為部落帶來了榮耀，我們感謝你!' WHERE entry=2000005313;
UPDATE db_script_string SET content_loc5='你還想要吃點什麼嗎?' WHERE entry=2000005314;
UPDATE db_script_string SET content_loc5='孩子，給我更多的麵包!' WHERE entry=2000005315;
UPDATE db_script_string SET content_loc5='遵命，長官。' WHERE entry=2000005316;
UPDATE db_script_string SET content_loc5='這是你要的麵包，先生。' WHERE entry=2000005317;
UPDATE db_script_string SET content_loc5='啊...太好了。快走吧!' WHERE entry=2000005318;
UPDATE db_script_string SET content_loc5='如你所願，長官。' WHERE entry=2000005319;
UPDATE db_script_string SET content_loc5='爸爸，我能出去收集更多的石頭來做項鍊嗎?' WHERE entry=2000005320;
UPDATE db_script_string SET content_loc5='好的!我會小心的，爸爸!' WHERE entry=2000005322;
UPDATE db_script_string SET content_loc5='哦...這塊石頭真是漂亮。看看我能不能找到更多這樣的石頭...' WHERE entry=2000005323;
UPDATE db_script_string SET content_loc5='嗯...那塊石頭很不錯，但是太小了一點。' WHERE entry=2000005324;
UPDATE db_script_string SET content_loc5='這石頭很適合拿來製作長矛，我敢打賭，我的朋友格魯克會喜歡的!非常鋒利...希望他會喜歡。' WHERE entry=2000005326;
UPDATE db_script_string SET content_loc5='嗯...這些石頭應該足夠做一條項鍊啦。無論如何，我現在得回家啦。' WHERE entry=2000005327;
UPDATE db_script_string SET content_loc5='我要送你一個喬遷之喜的禮物，加爾。' WHERE entry=2000005328;
UPDATE db_script_string SET content_loc5='不要緊，打開吧。我想你會喜歡它。' WHERE entry=2000005329;
UPDATE db_script_string SET content_loc5='加爾?' WHERE entry=2000005330;
UPDATE db_script_string SET content_loc5='除非這是我可以吃的東西，不然我不要。' WHERE entry=2000005331;
UPDATE db_script_string SET content_loc5='加爾，自從我來了以後你一點都不像從前的你。到底發生了什麼事?' WHERE entry=2000005332;
UPDATE db_script_string SET content_loc5='沒事。想不想幫我攪拌一下那些青蛙腿燉肉呢?' WHERE entry=2000005333;
UPDATE db_script_string SET content_loc5='我到這裡來是為了看你，加爾!結果我看你竟然住在一個空蕩蕩的小屋裡，四周是鳥不生蛋的地方，並且嘴裡講的竟然是關於青蛙的事情?你真是沒救了!' WHERE entry=2000005334;
UPDATE db_script_string SET content_loc5='而我下決心要找出更多相關的東西...如果你還需要我的幫助，你知道我會在哪裡。' WHERE entry=2000005335;
UPDATE db_script_string SET content_loc5='安娜雅...?我的雙眼沒有欺騙我嗎?真的是你嗎?' WHERE entry=2000005336;
UPDATE db_script_string SET content_loc5='歲月對我和你都很殘酷，我的愛人...但最後我們還是重逢了。' WHERE entry=2000005337;
UPDATE db_script_string SET content_loc5='命運是如此殘酷，它讓我們在分別了數千年之後才能重逢...' WHERE entry=2000005338;
UPDATE db_script_string SET content_loc5='我的愛人，你恨我嗎?我在萬般無奈之下摧毀了你的軀殼，因為只有這樣才能讓你從痛苦中永久地解脫。' WHERE entry=2000005339;
UPDATE db_script_string SET content_loc5='親愛的，不要為此而感到內疚。你使我從無盡的苦楚和折磨中解脫了出來，我只會為此而更加愛你。' WHERE entry=2000005340;
UPDATE db_script_string SET content_loc5='但是，即使是這經過數千年的分離才換來的重逢也是如此短暫...我和這個世界的聯結正在逐漸減弱，我正在慢慢地離開它...離開你...' WHERE entry=2000005341;
UPDATE db_script_string SET content_loc5='不!安娜雅...安娜雅!請不要離開我!不...' WHERE entry=2000005342;
UPDATE db_script_string SET content_loc5='再見了，塞瑞利恩，我的愛人。我們終能再見...' WHERE entry=2000005343;
UPDATE db_script_string SET content_loc5='怎麼辦，我的愛人?沒有你，我怎麼辦...沒有你...我如何有勇氣在這個世界上生存...' WHERE entry=2000005345;
UPDATE db_script_string SET content_loc5='壁泉瀑布在東邊和北邊的山脈之間。' WHERE entry=2000005347;
UPDATE db_script_string SET content_loc5='莫格，這是什麼意思?' WHERE entry=2000005348;
UPDATE db_script_string SET content_loc5='安靜!' WHERE entry=2000005349;
UPDATE db_script_string SET content_loc5='『這』就是你的英雄?' WHERE entry=2000005351;
UPDATE db_script_string SET content_loc5='你被騙了，傻子。' WHERE entry=2000005352;
UPDATE db_script_string SET content_loc5='這...整個...行動...已經失敗了!' WHERE entry=2000005353;
UPDATE db_script_string SET content_loc5='我要在一個小時內看見這個小蟲的屍體一片一片地躺在我的居所中。失敗的話你將會遭受比死亡還慘的命運。' WHERE entry=2000005354;
UPDATE db_script_string SET content_loc5='%s開始在這兩具化石上調整雷魯之石。' WHERE entry=2000005356;
UPDATE db_script_string SET content_loc5='誰召喚了阿曼?' WHERE entry=2000005357;
UPDATE db_script_string SET content_loc5='啊，你終於帶著那些古物回來了。' WHERE entry=2000005358;
UPDATE db_script_string SET content_loc5='注意，即使是創物之神也可能犯錯。' WHERE entry=2000005359;
UPDATE db_script_string SET content_loc5='對你的過去了解得越多，你的未來就越危險。' WHERE entry=2000005360;
UPDATE db_script_string SET content_loc5='嗨，吉札!哈拉薩米區又有十七份訂單了。重綠礦石的市場已全部崩潰了，正如你所預期。' WHERE entry=2000005362;
UPDATE db_script_string SET content_loc5='海拉茲出口物價正在上升，長官。我們已收買了適宜的課稅官員，走私品這幾天就會開始到貨了。' WHERE entry=2000005363;
UPDATE db_script_string SET content_loc5='沒什麼特別的事。帶些真正的消息回來好嗎?' WHERE entry=2000005364;
UPDATE db_script_string SET content_loc5='正如我所預期。非常好，退下吧。' WHERE entry=2000005365;
UPDATE db_script_string SET content_loc5='尊命，長官。' WHERE entry=2000005366;
UPDATE db_script_string SET content_loc5='如果你在這裡，那就表明你已經準備好去研究召喚惡魔的方法了。我們將從最低級的生物召喚開始，然後再慢慢進階。讓我們開始吧。' WHERE entry=2000005367;
UPDATE db_script_string SET content_loc5='最容易召喚的是小鬼。你的能力也許已經遠遠超過召喚小鬼的水準，但是我們為了保證召喚教學的完整性，所以還是從召喚它開始吧。' WHERE entry=2000005368;
UPDATE db_script_string SET content_loc5='這些邪惡的小傢伙就是小鬼。它很小而且很瘦弱，顯然是不能做為肉盾使用的，而它的威力也不大。這種召喚生物最好是在多人組隊的時候作為支援力量使用。' WHERE entry=2000005369;
UPDATE db_script_string SET content_loc5='現在你已經瞭解了小鬼的特性，那麼讓我們繼續研究下一種你可以召喚的生物:虛無行者。' WHERE entry=2000005370;
UPDATE db_script_string SET content_loc5='這種惡魔被稱為虛無行者。它的力量和耐力都非常驚人，可以出色地勝任防禦任務。首先控制它來攻擊你的敵人，這樣就能保證你在有肉盾保護的情況下安全地使用法術和技能。' WHERE entry=2000005371;
UPDATE db_script_string SET content_loc5='如果你還沒有看見過虛空行者，那你一定要去看看。這種生物看起來就極具氣勢，而且在戰場上也能發揮很強的作用。接下來，讓我們看看所有男性學員都在熱切期盼的召喚生物:魅魔。' WHERE entry=2000005372;
UPDATE db_script_string SET content_loc5='好了。除了和前面那兩種召喚生物的外形有著明顯的差異之外，魅魔的攻擊能力也非常可觀。然而，她的防禦力很差，所有很難作為肉盾來使用。' WHERE entry=2000005373;
UPDATE db_script_string SET content_loc5='努力學習，總有一天你們也可以召喚魅魔的，但現在我們要繼續課程了，下一講是惡魔獵犬。' WHERE entry=2000005374;
UPDATE db_script_string SET content_loc5='現在你們看到的就是惡魔獵犬。這種生物擁有法力燃燒等多種技能，這些技能在對付施法型的敵人時是十分有用的。' WHERE entry=2000005375;
UPDATE db_script_string SET content_loc5='當遇見施法者的時候，這種惡魔是你最好的朋友。現在，讓我們進行一下講的課程。這種召喚生物主要被用來幫助你更快地旅行。它就是惡魔戰馬。' WHERE entry=2000005376;
UPDATE db_script_string SET content_loc5='所有的召喚生物都介紹完畢了。召喚課程已經結束。新的課程將會很快開始，如果你要想要得到進一步的提高，那就請不要走遠，先在周圍轉轉。' WHERE entry=2000005378;
UPDATE db_script_string SET content_loc5='呼!我的幸運帽在這。我到處在找它。' WHERE entry=2000005379;
UPDATE db_script_string SET content_loc5='我不知道我在想什麼，沒帶我的劍就出去了。如果我有看到它在這裡就會帶著它了...' WHERE entry=2000005380;
UPDATE db_script_string SET content_loc5='我不可能不穿我的披風就出門。我希望它在這裡...' WHERE entry=2000005381;
UPDATE db_script_string SET content_loc5='在這裡!我發誓我上次看的時候它不在這裡...' WHERE entry=2000005382;
UPDATE db_script_string SET content_loc5='沒有一個好聽的床邊故事我沒辦法睡。現在我可以好好休息了。' WHERE entry=2000005383;
UPDATE db_script_string SET content_loc5='那是什麼聲音?聽起來像什麼動物的吼叫聲。' WHERE entry=2000005384;
UPDATE db_script_string SET content_loc5='銳爪?!放鬆點，我的朋友...你的主人沃科爾在哪裡?' WHERE entry=2000005386;
UPDATE db_script_string SET content_loc5='放鬆，放鬆，我的朋友。等一下...' WHERE entry=2000005388;
UPDATE db_script_string SET content_loc5='這樣...應該可以了。很好，現在告訴我發生了什麼事，銳爪。' WHERE entry=2000005390;
UPDATE db_script_string SET content_loc5='我明白，朋友。我會找人去幫助你的主人。現在你要回到他那邊去，至少離他近一點。' WHERE entry=2000005392;
UPDATE db_script_string SET content_loc5='如果你有時間，銳爪和他的主人沃科爾需要你的幫助。要是你感興趣的話，就繼續和我談談吧...' WHERE entry=2000005394;
UPDATE db_script_string SET content_loc5='我受夠了你不斷的發牢騷，隊長。你這樣還算什麼隊長?我是說，拿出一點膽量來!' WHERE entry=2000005395;
UPDATE db_script_string SET content_loc5='%s聳了聳肩。' WHERE entry=2000005397;
UPDATE db_script_string SET content_loc5='喔好吧，回到計畫階段。' WHERE entry=2000005398;
UPDATE db_script_string SET content_loc5='在我開始我的測試後，請原諒我...' WHERE entry=2000005400;
UPDATE db_script_string SET content_loc5='真有趣...' WHERE entry=2000005401;
UPDATE db_script_string SET content_loc5='不過，這是什麼?' WHERE entry=2000005402;
UPDATE db_script_string SET content_loc5='哦，天呀!' WHERE entry=2000005403;
UPDATE db_script_string SET content_loc5='要是我的血沒在很久以前就凝結了，那它現在就會因為憤怒而沸騰...' WHERE entry=2000005404;
UPDATE db_script_string SET content_loc5='石匠們...呃...迪菲亞兄弟會的成員們，請注意:今晚拋下生鏽的錨。' WHERE entry=2000005407;
UPDATE db_script_string SET content_loc5='牧師在寧靜的祈禱中跪下。' WHERE entry=2000005409;
UPDATE db_script_string SET content_loc5='聖光，給我力量。' WHERE entry=2000005410;
UPDATE db_script_string SET content_loc5='聖光，引領墮落的人們從不真實到真實，從黑暗到光明，和從死亡到不朽。' WHERE entry=2000005411;
UPDATE db_script_string SET content_loc5='不～～～～!!!' WHERE entry=2000005415;
UPDATE db_script_string SET content_loc5='朋友...我讓你失望了...' WHERE entry=2000005416;
UPDATE db_script_string SET content_loc5='不我的朋友...我們讓你失望了...我們讓你失望了。' WHERE entry=2000005417;
UPDATE db_script_string SET content_loc5='一切事情都準備好了，戰爭使者。' WHERE entry=2000005418;
UPDATE db_script_string SET content_loc5='毀滅領主卡札克會很開心。你應該加快你的攻擊速度。加速破壞獸人及矮人的要塞。' WHERE entry=2000005419;
UPDATE db_script_string SET content_loc5='不用擔心。我已經增加了死亡熔爐的產量。你將會得到所有你需要的煉獄火以執行你的命令。別失敗了，裘瓦安。' WHERE entry=2000005421;
