UPDATE script_texts SET content_loc5='你擊敗不了戈魯爾之手!' WHERE entry=-1565001; /* content: You will not defeat the hand of Gruul! */
UPDATE script_texts SET content_loc5='呸!不能證明任何事!' WHERE entry=-1565003; /* content: Pah! Does not prove anything! */
UPDATE script_texts SET content_loc5='很好，現在跟我打吧!' WHERE entry=-1565005; /* content: Good, now you fight me! */
UPDATE script_texts SET content_loc5='%s變大了!' WHERE entry=-1565019; /* content: %s grows in size! */
UPDATE script_texts SET content_loc5='風之聖魂將是你的夢魘!' WHERE entry=-1568000; /* content: Spirits of da wind be your doom! */
UPDATE script_texts SET content_loc5='燒死你們!' WHERE entry=-1568001; /* content: I burn ya now! */
UPDATE script_texts SET content_loc5='雌鷹哪裡去啦?快去孵蛋!' WHERE entry=-1568002; /* content: Where ma hatcha? Get to work on dem eggs! */
UPDATE script_texts SET content_loc5='現在，讓我來告訴你們什麼叫數量優勢...' WHERE entry=-1568003; /* content: I show you strength... in numbers. */
UPDATE script_texts SET content_loc5='你們沒有時間啦!' WHERE entry=-1568004; /* content: You done run outta time! */
UPDATE script_texts SET content_loc5='一切都結束了，小子!' WHERE entry=-1568005; /* content: It all be over now, mon! */
UPDATE script_texts SET content_loc5='(食人妖語)你完蛋了!' WHERE entry=-1568006; /* content: Tazaga-choo! */
UPDATE script_texts SET content_loc5='上前去，衛兵!殺戮時間開始了!' WHERE entry=-1568010; /* content: Get da move on, guards! It be killin\' time! */
UPDATE script_texts SET content_loc5='衛兵，快上!你們更怕誰...他們...還是我?' WHERE entry=-1568011; /* content: Guards, go already! Who you more afraid of, dem... or me? */
UPDATE script_texts SET content_loc5='就是現在!衝過去，把他們的腦袋給我帶來!' WHERE entry=-1568012; /* content: Ride now! Ride out dere and bring me back some heads! */
UPDATE script_texts SET content_loc5='我要失去耐心了!上吧:讓他們感覺生不如死!' WHERE entry=-1568013; /* content: I be losin\' me patience! Go on: make dem wish dey was never born! */
UPDATE script_texts SET content_loc5='有什麼能比侍奉偉大的熊靈以換取永恆的生命還好呢?靠近一點，把你們的靈魂交給我!' WHERE entry=-1568014; /* content: What could be better than servin\' da bear spirit for eternity? Come closer now. Bring your souls to me! */
UPDATE script_texts SET content_loc5='你很快就會死了!' WHERE entry=-1568016; /* content: You be dead soon enough! */
UPDATE script_texts SET content_loc5='我帶來痛苦!' WHERE entry=-1568017; /* content: I bring da pain! */
UPDATE script_texts SET content_loc5='你們既然將野獸召喚出來，就將付出更多的代價!' WHERE entry=-1568018; /* content: You call on da beast, you gonna get more dan you bargain for! */
UPDATE script_texts SET content_loc5='阿曼尼將再度統治!' WHERE entry=-1568022; /* content: Da Amani gonna rule again! */
UPDATE script_texts SET content_loc5='我...會在另一個世界等著...' WHERE entry=-1568023; /* content: I... be waitin\' on da udda side.... */
UPDATE script_texts SET content_loc5='我是掠食者!而你們，就是獵物!' WHERE entry=-1568026; /* content: I be da predator! You da prey... */
UPDATE script_texts SET content_loc5='盡情享用吧，兄弟們!' WHERE entry=-1568027; /* content: Feed, me bruddahs! */
UPDATE script_texts SET content_loc5='你真沒用!' WHERE entry=-1568030; /* content: Ya got nothin\'! */
UPDATE script_texts SET content_loc5='別吵了!' WHERE entry=-1568031; /* content: Stop your cryin\'! */
UPDATE script_texts SET content_loc5='你們...永遠無法消滅...我的靈魂!' WHERE entry=-1568032; /* content: You can\'t... kill... me spirit! */
UPDATE script_texts SET content_loc5='出現一股電氣風暴!' WHERE entry=-1568033; /* content: An Electrical Storm Appears! */
UPDATE script_texts SET content_loc5='在利爪與尖牙面前下跪吧，祈禱吧，顫慄吧!' WHERE entry=-1568034; /* content: Get on ya knees and bow.... to da fang and claw! */
UPDATE script_texts SET content_loc5='狂野的靈魂與我同在...' WHERE entry=-1568035; /* content: I fight wit\' untamed spirit.... */
UPDATE script_texts SET content_loc5='靈魂，回到我這裡來!' WHERE entry=-1568036; /* content: Spirit, come back to me! */
UPDATE script_texts SET content_loc5='我要把你撕碎!' WHERE entry=-1568037; /* content: Me gonna carve ya now! */
UPDATE script_texts SET content_loc5='你將死無全屍!' WHERE entry=-1568038; /* content: You gonna leave in pieces! */
UPDATE script_texts SET content_loc5='(食人妖語)你們將會在恐懼中死去。' WHERE entry=-1568042; /* content: Chaga... choka\'jinn. */
UPDATE script_texts SET content_loc5='我已經等待很久了，陌生人。你的死亡使我更強大!' WHERE entry=-1568044; /* content: I be waitin, strangers. Your deaths gonna make me stronger! */
UPDATE script_texts SET content_loc5='你們不會從這個夢魘裡醒來的!' WHERE entry=-1568047; /* content: Dis a nightmare ya don\' wake up from! */
UPDATE script_texts SET content_loc5='(食人妖語)現在你了解這股力量了吧!' WHERE entry=-1568048; /* content: Azzaga choogo zinn! */
UPDATE script_texts SET content_loc5='你的意志現在屬於我!' WHERE entry=-1568049; /* content: Your will belong ta me now! */
UPDATE script_texts SET content_loc5='你的靈魂將會受到傷害!' WHERE entry=-1568051; /* content: Your soul gonna bleed! */
UPDATE script_texts SET content_loc5='這不會影響最後的結局。' WHERE entry=-1568052; /* content: It not gonna make no difference. */
UPDATE script_texts SET content_loc5='你將會死得比他更慘。' WHERE entry=-1568053; /* content: You gonna die worse dan him. */
UPDATE script_texts SET content_loc5='我不在乎。' WHERE entry=-1568054; /* content: Dat no bodda me. */
UPDATE script_texts SET content_loc5='噢等等。我有新花招...像是我的熊兄弟...' WHERE entry=-1568058; /* content: Got me some new tricks... like me brudda bear.... */
UPDATE script_texts SET content_loc5='誰都逃不過雄鷹之爪!' WHERE entry=-1568059; /* content: Dere be no hidin\' from da eagle! */
UPDATE script_texts SET content_loc5='來，介紹一下我的新兄弟:利牙和爪子!' WHERE entry=-1568060; /* content: Let me introduce you to me new bruddas: fang and claw! */
UPDATE script_texts SET content_loc5='要看龍鷹，不必抬頭，眼前就有了!' WHERE entry=-1568061; /* content: Ya don\' have to look to da sky to see da dragonhawk! */
UPDATE script_texts SET content_loc5='用火燒也很快!' WHERE entry=-1568062; /* content: Fire kill you just as quick! */
UPDATE script_texts SET content_loc5='靈魂們今天要好好享用祭品!儀式開始，獻祭那些囚徒...將空間留給我們的新訪客!' WHERE entry=-1568068; /* content: Da spirits gonna feast today! Begin da ceremonies, sacrifice da prisoners... make room for our new guests! */
UPDATE script_texts SET content_loc5='自由選擇吧，入侵者們!我手下任何一位祭司都願意陪陪你們。' WHERE entry=-1568069; /* content: Take your pick, trespassers! Any of ma priests be happy to accommodate ya. */
UPDATE script_texts SET content_loc5='別害怕。你們之前有千百人過來嘗試過。你們並不孤獨。' WHERE entry=-1568070; /* content: Don\'t be shy. Thousands have come before you. Ya not be alone in your service. */
UPDATE script_texts SET content_loc5='你們就要失敗了，陌生人。很多人也曾嘗試過，但他們只是使我們更強大!' WHERE entry=-1568071; /* content: Ya gonna fail, strangers. Many try before you, but dey only make us stronger! */
UPDATE script_texts SET content_loc5='你們的努力是徒然的，入侵者。儀式已經接近完成了。' WHERE entry=-1568072; /* content: Your efforts was in vain, trespassers. The rituals nearly be complete. */
UPDATE script_texts SET content_loc5='很快，籠子就會變空，獻祭就將完成，而你們則會代替他們的位置。' WHERE entry=-1568073; /* content: Soon da cages gonna be empty, da sacrifices be complete, and you gonna take dere places. */
UPDATE script_texts SET content_loc5='時間緊迫，陌生人。很快的你們將會加入那些你們無力拯救的靈魂之中。' WHERE entry=-1568074; /* content: Time be running low, strangers. Soon you gonna join da souls of dem ya failed to save. */
UPDATE script_texts SET content_loc5='動作快點，祭司們!儀式絕不能被中斷!' WHERE entry=-1568075; /* content: Make haste, ma priests! Da rituals must not be interrupted! */
UPDATE script_texts SET content_loc5='你們的努力值得讚賞...但現在你們就將加入死者的行列了。' WHERE entry=-1568076; /* content: Ya make a good try... but now you gonna join da ones who already fall. */
UPDATE script_texts SET content_loc5='注意。所有的祭品都將增強我們與靈魂世界的聯繫。很快，我們就將無堅不摧!' WHERE entry=-1568078; /* content: Watch now. Every offering gonna strengthen our ties to da spirit world. Soon, we gonna be unstoppable! */
UPDATE script_texts SET content_loc5='根據我的計算，只要我們夠多人一起敲響銅鑼，這樣那些門扉的封印就會破裂，我們就能進去了。' WHERE entry=-1568080; /* content: According to my calculations, if enough of us bang the gong at once the seal on these doors will break and we can enter. */
UPDATE script_texts SET content_loc5='%s吸收了熊之靈的精華!' WHERE entry=-1568082; /* content: %s absorbs the essence of the bear spirit! */
