UPDATE script_texts SET content_loc5='你還不夠資格服侍我。' WHERE entry=-1574019; /* content: You may serve me yet. */
UPDATE script_texts SET content_loc5='史卡沃，你這個無能的懶蟲!滾回去做好你的本份!' WHERE entry=-1574020; /* content: Skarvald, you incompetent slug! Return and make yourself useful! */
UPDATE script_texts SET content_loc5='%s咆哮著!' WHERE entry=-1574022; /* content: %s roars! */
UPDATE script_texts SET content_loc5='陛下!我已完成您的要求，如今懇求您的祝福!' WHERE entry=-1575000; /* content: My liege! I have done as you asked, and now beseech you for your blessing! */
UPDATE script_texts SET content_loc5='這感覺...真是超乎想像。我將永遠聽命於您，吾王。' WHERE entry=-1575002; /* content: The sensation is... beyond my imagining. I am yours to command, my king. */
UPDATE script_texts SET content_loc5='你的第一個試煉正在等待著你。消滅那些不速之客。' WHERE entry=-1575003; /* content: Your first test awaits you. Destroy our uninvited guests. */
UPDATE script_texts SET content_loc5='我很樂意以你的名義來屠殺他們!來吧，天譴軍的敵人!我會讓你們瞧瞧巫妖王的力量 !' WHERE entry=-1575004; /* content: I will be happy to slaughter them in your name! Come, enemies of the Scourge! I will show you the might of the Lich King! */
UPDATE script_texts SET content_loc5='我要征服你的靈魂!' WHERE entry=-1575005; /* content: I will vanquish your soul! */
UPDATE script_texts SET content_loc5='你竟蠢到膽敢挑戰巫妖王的力量!' WHERE entry=-1575006; /* content: You were a fool to challenge the power of the Lich King! */
UPDATE script_texts SET content_loc5='你無法逃避你的末日。' WHERE entry=-1575011; /* content: Your end is inevitable. */
UPDATE script_texts SET content_loc5='(維酷語)嚥下最後一口氣吧!' WHERE entry=-1575012; /* content: Yor-guul mak! */
UPDATE script_texts SET content_loc5='還有什麼遺言嗎?' WHERE entry=-1575013; /* content: Any last words? */
UPDATE script_texts SET content_loc5='不!我歷盡千辛萬苦，不是為了...' WHERE entry=-1575014; /* content: Nooo! I did not come this far... to... */
UPDATE script_texts SET content_loc5='這是什麼地方?我要摧毀你!' WHERE entry=-1575015; /* content: What this place? I will destroy you! */
UPDATE script_texts SET content_loc5='輕鬆的任務!' WHERE entry=-1575017; /* content: An easy task! */
UPDATE script_texts SET content_loc5='現在沒這麼無禮了，對吧?' WHERE entry=-1575026; /* content: Not so brash now, are you? */
UPDATE script_texts SET content_loc5='你們這些沒教養的垃圾!你們的屍體剛好拿來當龍的點心!' WHERE entry=-1575029; /* content: You motherless knaves! Your corpses will make fine morsels for my new drake! */
UPDATE script_texts SET content_loc5='黑色風暴之王畢雍!賜我榮耀!降臨吧!' WHERE entry=-1575032; /* content: Bjorn of the Black Storm! Honor me now with your presence! */
UPDATE script_texts SET content_loc5='又...又有什麼...在等著我呢?' WHERE entry=-1575040; /* content: What... awaits me... now? */
UPDATE script_texts SET content_loc5='%s深深地吸了一口氣。' WHERE entry=-1575041; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc5='這裡有我千萬個分身。' WHERE entry=-1576002; /* content: There\'s plenty of me to go around. */
UPDATE script_texts SET content_loc5='現在，最後一步!' WHERE entry=-1576003; /* content: Now to finish the job! */
UPDATE script_texts SET content_loc5='這該死的...運氣。' WHERE entry=-1576005; /* content: Damn the... luck. */
