UPDATE trans_words SET content_loc4='You must meet with Arcanist Tybalin on the top floor of the Silver Enclave. He will entrust you with a book that is beyond value. Be discreet in your movements and guard this book with your life. Carry it back to Caladis Brightspear at Quel\'Delar\'s rest with all haste.' WHERE word_id = 126464;
