UPDATE `trans_words`SET `content_loc4`='I smell their cauldrons from here, but that\'s not plague they\'re cooking.$B$BPledges to the cult are forced to drink a concoction that steals their will and enslaves them forever to the Lich King.$B$BSome say the potion contains the twisted soul of a being who was tormented before being mercilessly slaughtered, but I\'ve never been able to reproduce it...$B$BFill this flask with a sample from each of the three cauldrons in the northwest region of the gate and bring it to Chief Engineer Copperclaw.' WHERE `word_id`=163644; -- locales_quest - details : entry=13355
UPDATE `trans_words`SET `content_loc4`='Koltira aboard Orgrim\'s Hammer wants you to use the Partitioned Flask at each of the 3 Cultists\' Cauldrons at Aldur\'thar, then bring it to Chief Engineer Copperclaw.' WHERE `word_id`=163645; -- locales_quest - objectives : entry=13355
UPDATE `trans_words`SET `content_loc4`='<Copperclaw pours the contents of the flask into his particle analyzer.>$B$BAhh... I know just the thing!' WHERE `word_id`=163646; -- locales_quest - offerrewardtext : entry=13355
UPDATE `trans_words`SET `content_loc4`='Wahoo!$B$BThat\'ll teach \'em!' WHERE `word_id`=163534; -- locales_quest - offerrewardtext : entry=13356
UPDATE `trans_words`SET `content_loc4`='Wahoo!$B$BThat\'ll teach \'em!' WHERE `word_id`=163534; -- locales_quest - offerrewardtext : entry=13357
