UPDATE `trans_words`SET `content_loc4`='Searching the databanks of the prototype console reveals schematics for a scrapbot construction kit. With your engineering expertise, you could learn this schematic and produce scrapbot construction kits of your own.' WHERE `word_id`=164399; -- locales_quest - details : entry=13843
UPDATE `trans_words`SET `content_loc4`='You take note of the information displayed in the schematic and the materials required to construct the scrapbot kit.' WHERE `word_id`=275150; -- locales_quest - offerrewardtext : entry=13843
