UPDATE `trans_words`SET `content_loc4`='Acquire some Bread of the Dead and give it to the Cheerful Orc Spirit in Orgrimmar\'s graveyard.$B$BUse an Orange Marigold to see the otherwise invisible spirit.' WHERE `word_id`=164859; -- locales_quest - objectives : entry=14175
UPDATE `trans_words`SET `content_loc4`='Ah, more delicious Bread of the Dead, thank you!$B$BPlease, take this in return... a little something fun for the holiday.' WHERE `word_id`=164849; -- locales_quest - offerrewardtext : entry=14175
UPDATE `trans_words`SET `content_loc4`='Acquire some Bread of the Dead and give it to the Cheerful Tauren Spirit in Thunder Bluff\'s graveyard.$B$BUse an Orange Marigold to see the otherwise invisible spirit.' WHERE `word_id`=164860; -- locales_quest - objectives : entry=14176
