UPDATE `trans_words`SET `content_loc4`='Excellent work, $N. The integrity of our borders must be preserved at all costs!' WHERE `word_id`=155989; -- locales_quest - offerrewardtext : entry=24219
UPDATE `trans_words`SET `content_loc4`='Win an Arathi Basin battleground match and return to an Alliance Brigadier General at any Alliance capital city, Wintergrasp, Dalaran, or Shattrath.' WHERE `word_id`=155981; -- locales_quest - objectives : entry=24220
UPDATE `trans_words`SET `content_loc4`='Excellent news, $C! If our tactical deployments continue to be this successful, the Horde will be in full retreat before long.' WHERE `word_id`=164950; -- locales_quest - offerrewardtext : entry=24220
UPDATE `trans_words`SET `content_loc4`='Win an Arathi Basin battleground match and return to a Horde Warbringer at any Horde capital city, Wintergrasp, Dalaran, or Shattrath.' WHERE `word_id`=155990; -- locales_quest - objectives : entry=24221
UPDATE `trans_words`SET `content_loc4`='You\'ve brought great honor to the Horde, $N. Your victory today will ensure a thousand victories tomorrow!' WHERE `word_id`=155991; -- locales_quest - offerrewardtext : entry=24221
