UPDATE `locales_npc_text` SET `Text0_1_loc2`='', `Text1_0_loc2`='To learn to cure leather and work with hides go to Orgrimmar to the north. One of the Orgrimmar Grunts will be able to tell you the way to the Leatherworking Trainer.', WHERE `entry`=4028;
UPDATE `locales_npc_text` SET `Text0_1_loc2`='', `Text1_0_loc2`='Plenty of good mining to be had in Durotar, or so I hear. So good in fact that we have a Mining Trainer right here in Razor Hill. You\'ll find Krunn next to the heated forge in the northwest corner of town.', WHERE `entry`=4029;
UPDATE `locales_npc_text` SET `Text0_1_loc2`='', `Text1_0_loc2`='There\'s one in Orgrimmar. Go north and talk to one of their grunts.', WHERE `entry`=4030;
UPDATE `locales_npc_text` SET `Text0_1_loc2`='', `Text1_0_loc2`='You can learn to sew up in Orgrimmar. That\'s to the north. Maybe one of the Orgrimmar Grunts can help you.', WHERE `entry`=4031;
UPDATE `locales_npc_text` SET `Text0_1_loc2`='', `Text1_0_loc2`='Yeah, I need to drop off some things myself. Find it by heading north up the road to Orgrimmar and then ask one of the grunts there.', WHERE `entry`=4032;
UPDATE `locales_npc_text` SET `Text0_1_loc2`='', `Text1_0_loc2`='Can\'t you see I\'m busy here?  Go north up the road to Orgrimmar and ask one of the grunts there. You can\'t miss it.', WHERE `entry`=4033;
UPDATE `locales_npc_text` SET `Text0_1_loc2`='', `Text1_0_loc2`='It\'s the big building in Razor Hill that\'s not the barracks. Just talk to Innkeeper Grosk inside. Got that?', WHERE `entry`=4034;
