UPDATE `locales_npc_text` SET `Text0_1_loc2`='', `Text1_0_loc2`='If ye ask me, I think Grif spends a little too much time with the animals. Still, nobody else knows the hunter\'s path quite like he does. Look for him outside the smithy in Kharanos.', WHERE `entry`=4293;
