UPDATE `locales_npc_text` SET `Text0_1_loc2`='', `Text1_0_loc2`='That Granis Swiftaxe could drink a fish under the table and yet his axe still swings true. However, if yer looking for training, ye best catch him while ye can understand him.', WHERE `entry`=4299;
