UPDATE `locales_npc_text` SET `Text0_1_loc2`='', `Text1_0_loc2`='Only a few spots to get any fishing done around here. Paxton knows them all and probably a few he won\'t tell ya about. Why don\'t ye go over to Iceflow Lake and see what he will tell ya.', WHERE `entry`=4307;
