UPDATE locales_quest SET ObjectiveText3_loc2='Blacksmith Assaulted',ObjectiveText4_loc2='Farm Assaulted' WHERE entry=8166;
UPDATE locales_quest SET ObjectiveText3_loc2='Blacksmith Assaulted',ObjectiveText4_loc2='Farm Assaulted' WHERE entry=8167;
UPDATE locales_quest SET ObjectiveText3_loc2='Blacksmith Assaulted',ObjectiveText4_loc2='Farm Assaulted' WHERE entry=8168;
UPDATE locales_quest SET ObjectiveText3_loc2='Blacksmith Assaulted',ObjectiveText4_loc2='Stable Assaulted' WHERE entry=8169;
