UPDATE locales_quest SET offerrewardtext_loc2='The heavens, in their countless stars, hold answers to earthly mysteries, $N.  Might, then, the wise, and the lucky, gaze up and find truth?' WHERE entry=8688;
UPDATE locales_quest SET offerrewardtext_loc2='Ah yes... this drape is immaculate.$B$BTake this cloak, $N. May it protect you from the foul magic of the Qiraji.' WHERE entry=8689;
