UPDATE locales_quest SET offerrewardtext_loc2='Ah, yes. Mux will be quite happy with this.$B$BThose bracers were part of a larger armor set. If you\'re interested in providing me with further assistance, I\'d be willing to consider parting with the rest of it.' WHERE entry=8913;
